pragma solidity ^0.8.4;


contract FrogStake {
    using SafeMath for uint256;

    event OwnershipTransferred(address indexed owner, address indexed newOwner);
    event StakeLimitUpdated(uint256 stakeLimit);
    event TokenAddressUpdated(address oldAddress, address tokenAddress);

    address public owner;
    ERC20 public token;
    uint256 public minTxAmount = 1;
    uint256 public dailyReward = 2500;
    bool public isStakingEnabled = true;
    uint256 public totalRewardsDistributed = 0;
    uint256 public amountStaked = 0;
    uint256 stakeDuration = 15778476; //6months
    address burnAddress = 0x000000000000000000000000000000000000dEaD;
    uint256 burnFee = 5;
    uint256 poolFee = 15;

    struct userDetails {
        uint256 amount;
        uint256 lastReward;
        uint256 totalClaimed;
        uint256 unStake;
    }

    mapping(address => userDetails) private user;

    modifier onlyOwner() {
        require(owner == msg.sender, "Ownable: Caller is not owner");
        _;
    }

    constructor(address _token) {
        token = ERC20(_token);
        owner = msg.sender;
    }


    /**
     * After changing token address, please add tokens to this contract
     */
    function changeTokenAddress(address newTokenAddress)
        public
        onlyOwner
        returns (bool)
    {
        require(newTokenAddress != address(0), "Token address cannot be 0");
        require(
            newTokenAddress != address(token),
            "Token address cannot be the same as the current one"
        );
        emit TokenAddressUpdated(address(token), newTokenAddress);
        token = ERC20(newTokenAddress);
        return true;
    }

    function transferOwnership(address newOwner)
        public
        onlyOwner
        returns (bool)
    {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
        return true;
    }

    function disableStaking() public onlyOwner returns (bool) {
        isStakingEnabled = false;
        return true;
    }

    function enableStaking() public onlyOwner returns (bool) {
        isStakingEnabled = true;
        return true;
    }

    function stake(uint256 _amount) public returns (bool) {
        require(isStakingEnabled, "Staking is disabled");
        uint256 tokenDecimals = token.decimals();
        uint256 amount = _amount * 10**tokenDecimals;
        uint256 _minTxAmount = minTxAmount * 10**tokenDecimals;
        require(amount >= _minTxAmount, "amount is less than minTxAmount");
        // Transfer before staking to prevent error while staking and transfer at the same time
        token.transferFrom(msg.sender, address(this), amount);

        user[msg.sender].amount += amount;
        user[msg.sender].lastReward = block.timestamp;
        user[msg.sender].unStake = block.timestamp + stakeDuration;
        amountStaked += amount;
        return true;
    }

    function getRewards(address account)
        public
        view
        returns (uint256)
    {
        if (user[account].amount > 0) {
            uint256 stakeAmount = user[account].amount;
            uint256 lastReward = user[account].lastReward;
            //convert milliseconds to minutes
            uint256 timeDiff = (block.timestamp - lastReward) / 60;

            if(timeDiff > 0){
                uint256 tokenDecimals = token.decimals();
                uint256 percentage = stakeAmount * 10**tokenDecimals / amountStaked;
                //contractReward per minute
                uint256 contractReward = (dailyReward * 10**tokenDecimals) / 1440;
                //users percentage per minute
                uint256 rewardPerMinute =  (percentage * contractReward) / 10**tokenDecimals;
                //reward since the last claim
                uint256 rewardAmount = rewardPerMinute * timeDiff;
                return rewardAmount;


            }else return 0;
            
           
        } else return 0;
    }

    function claim() public returns (bool) {
        require(user[msg.sender].amount > 0, "user not exist");
        uint256 rewardToClaim = getRewards(msg.sender);
        user[msg.sender].lastReward = block.timestamp;
        user[msg.sender].totalClaimed += rewardToClaim;
        totalRewardsDistributed += rewardToClaim;
        token.transfer(msg.sender, rewardToClaim);
        return true;

    }

    function unstake(uint256 _amount) public returns (bool) {
        uint256 tokenDecimals = token.decimals();
        uint256 unstakeAmount = _amount * 10**tokenDecimals;

        require(user[msg.sender].amount > 0, "user not exist");
        require(unstakeAmount <= user[msg.sender].amount, "out of bounds");

        uint256 rewardAmount = getRewards(msg.sender);
        uint256 amount = rewardAmount + unstakeAmount;
        
        user[msg.sender].amount -= unstakeAmount;
        user[msg.sender].totalClaimed += rewardAmount;
        user[msg.sender].lastReward = block.timestamp;
        totalRewardsDistributed += rewardAmount;
        amountStaked -= unstakeAmount;
        if( block.timestamp < user[msg.sender].unStake){
            //fee deduction
            uint256 users = amount * (100 - burnFee - poolFee) / 100;
            uint256 burn = amount * burnFee / 100;
            if(burn > 0){
                token.transfer(burnAddress, burn);

            }

            //send % to user
            token.transfer(msg.sender, users);

        }else{

            token.transfer(msg.sender, amount);

        }

        if(user[msg.sender].amount == 0){
            user[msg.sender].unStake = 0;
        }
        return true;
    }

    function getUserDetails(address account)
        public
        view
        returns (userDetails memory, uint256)
    {
        uint256 reward = getRewards(account);
        return (
            userDetails(
                user[account].amount,
                user[account].totalClaimed,
                user[account].lastReward,
                user[account].unStake
            ),
            reward
        );
    }

    function setPoolFee(uint256 _poolFee) public onlyOwner returns (bool) {
        poolFee = _poolFee;
        return true;
    }

    function setBurnFee(uint256 _burnFee) public onlyOwner returns (bool) {
       burnFee = _burnFee;
        return true;
    }

    function setStakeLimit(uint256 stakeLimit) public onlyOwner returns (bool) {
        minTxAmount = stakeLimit;
        emit StakeLimitUpdated(minTxAmount);
        return true;
    }
    function setDailyReward(uint256 _dailyReward) public onlyOwner returns (bool) {
        dailyReward = _dailyReward;
        return true;
    }
    
    function transferTokens() public onlyOwner{
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }

}
