pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

abstract contract ReentrancyGuard {
    bool internal locked;

    modifier noReentrant() {
        require(!locked, "No re-entrancy");
        locked = true;
        _;
        locked = false;
    }
}

contract DSNFTClaim is ReentrancyGuard{

    bool claimPeriodOpen = false;
    bool reentrancy = false;
    uint256 public claimPerNFT = 0;
    uint256 public payPeriod   = 0;
    address nftAddress = 0x9bA6dc1e03939e3BE69d22028E1940c19CA63761;
    //mainnet
    //0x9bA6dc1e03939e3BE69d22028E1940c19CA63761
    address usdcTokenAddress   = 0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d;
    //mainnet
    //0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d;
    address portfolioWallet    = 0x8e3057ACbE33Eb464fd5936b90c96dCC056b127d;
    address admin;

    IERC721 nftInstance;
    IERC20 usdcInstance;

    struct userClaimData {
        uint256 payPeriod;
        uint256 claimAmount;
    }

    mapping(address => userClaimData) public claimData;

    constructor() {
        admin = msg.sender;
        nftInstance = IERC721(nftAddress);
        usdcInstance = IERC20(usdcTokenAddress);
    }

    modifier adminOnly {
        require(msg.sender == admin, "Admin only");
        _;
    }
    function nftBalance(address addr) public view returns(uint256){
        uint256 nft_Balance = nftInstance.balanceOf(addr);
        return nft_Balance;
    }

    function getClaimAmount(uint256 balance) public view returns(uint256) {
        uint256 amount = balance * claimPerNFT;
        return amount;
    }

    function claimAvailable(address account)public view returns(bool){
        if (claimData[account].payPeriod < payPeriod){
            return true;
        }
        return false;
    }

    function claim() public noReentrant {
        require(claimPeriodOpen == true, "No NFTs");
        //only allow one clain per period
        require(claimData[msg.sender].payPeriod < payPeriod, "Already claimed");
        uint256 nft_Balance = nftInstance.balanceOf(msg.sender);
        require(nft_Balance > 0, "No NFTs");

        claimData[msg.sender].payPeriod = payPeriod;

        uint256 claimAmount = getClaimAmount(nft_Balance);
        claimData[msg.sender].claimAmount += claimAmount;
        require(usdcInstance.transfer(msg.sender, claimAmount), "Claim failed");
    }

    //admin deposits funds to contract then calls 
    //SetClaimPerNFT() from bscscan. 
    //this sets the amount distributed per nft
    function SetClaimPerNFT() public adminOnly {
        uint256 balance = usdcInstance.balanceOf(address(this));
        claimPerNFT = balance / 200;
        claimPeriodOpen = true;
        payPeriod++;
    }

    function SetClaimState(bool state) public adminOnly{
        claimPeriodOpen = state;
    }

    function removeExcessUsdc() public adminOnly {
        uint256 balance = usdcInstance.balanceOf(address(this));
        usdcInstance.transfer(portfolioWallet, balance);
    }

    function changeAdmin(address newAdmin) public adminOnly {
        admin = newAdmin;
    }

}"
