    "contracts/PresaleSwapper.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Presale is Pausable, Ownable, ReentrancyGuard {

    using EnumerableSet for EnumerableSet.AddressSet;

    uint256 constant PRECISION = 10 ** 5;
    uint256 _accountLimit;
    uint256 _pricePerToken;
    uint256 _startTimestamp;
    uint8 _decimals;
    address public presaleVault;
    IERC20Metadata _token;
    uint256 public fundsRaised;
    uint256[] public amountRaisedTillDay;
    uint256[] public uniqueHoldersTillDay;
    uint256[] public dailyTimestamp;

    mapping(address => uint) _received;
    EnumerableSet.AddressSet _accounts;
    uint256 public lastDailyUpdate;
    string public contractName;

    /// @notice construct the presale contract
    constructor(string memory _contractName, address _ownerAddress, address _presaleToken, uint _presalePrice, uint _accountBuyLimit, address _presaleVault)  {
        contractName = _contractName;
        _pricePerToken = _presalePrice;
        _token = IERC20Metadata(_presaleToken);
        _startTimestamp = block.timestamp;
        _decimals = _token.decimals();
        _accountLimit = _accountBuyLimit;
        presaleVault = _presaleVault;
        pause();
        transferOwnership(_ownerAddress);
    }

    /// @notice pause the presale
    function pause() public onlyOwner {
        _pause();
    }

    /// @notice unpause the presale
    function unpause() public onlyOwner {
        _unpause();
        if (_startTimestamp == 0) {
            _startTimestamp = block.timestamp;
        }
    }

    /// @notice get the presale data
    /// @return the price, hard limit, duration and hold limit
    function data() external view returns (uint256, uint256) {
        return (_pricePerToken, _accountLimit);
    }

    /// @notice set all the presale settings
    /// @param walletLimit_ the hold limit per holder of the presale
    /// @param price_ the price of the token in the native currency
    function setAll(uint walletLimit_, uint price_) external onlyOwner {
        _accountLimit = walletLimit_;
        _pricePerToken = price_;
    }

    function setPresaleToken(address _tokenAddress) external onlyOwner {
        _token = IERC20Metadata(_tokenAddress);
    }

    /// @notice set the hold limit
    /// @param walletLimit_ the hold limit per holder of the presale
    function setWalletLimit(uint walletLimit_) external onlyOwner {
        _accountLimit = walletLimit_;
    }

    /// @notice set the price of the token
    /// @param price_ the price per token
    function setPrice(uint price_) external onlyOwner {
        _pricePerToken = price_;
    }

    /// @notice withdraw the native currency stored in the contract
    function withdrawBNB(address to_, uint amount_) external onlyOwner {
        (bool success,) = payable(to_).call{value: amount_}("");
        if (!success) revert("payment failed");
    }

    function setVault(address _vault) external onlyOwner {
        presaleVault = _vault;
    }

    /// @notice withdraw tokens stored in the contract
    function withdrawTokens(address token_, address to_, uint amount_) external onlyOwner {
        IERC20Metadata(token_).transfer(to_, amount_);
    }

    /// @notice lookup the amount received from a given account
    function receivedFrom(address account_) external view returns (uint) {
        return _received[msg.sender];
    }

    /// @notice lookup the total bnb received
    function totalReceived() external view returns (uint) {
        return _received[address(this)];
    }

    function _isEligible(address account_, uint _value) internal view returns (bool) {
        return _received[account_] + _value <= _accountLimit;
    }

    receive() external payable nonReentrant whenNotPaused {
        _receive(msg.sender, msg.value);
    }

    fallback() external {

    }

    function _receive(address account_, uint256 value_) internal {

        uint256 tokensRequested = value_ * PRECISION / _pricePerToken / PRECISION * 10 ** _decimals;
        uint256 availableTokens = _token.balanceOf(address(this));
        bool success;

        tokensRequested = tokensRequested > availableTokens ? availableTokens : tokensRequested;
        tokensRequested = tokensRequested > _accountLimit - _received[account_] ? _accountLimit - _received[account_] : tokensRequested;

        if(tokensRequested == 0){revert("Insufficient token out value");}
        _received[account_] += tokensRequested;

        _token.transfer(account_, tokensRequested);

        uint256 bnbPaid = tokensRequested * PRECISION * _pricePerToken / PRECISION / 10 ** _decimals;
        if(value_ != bnbPaid){
            (success,) = payable(account_).call{value: value_ - bnbPaid}("");
            if(!success){
                revert("Transfer Failed");
            }
        }
        (success,) = payable(presaleVault).call{value: bnbPaid}("");
        if(!success){
            revert("Transfer Failed");
        }
        fundsRaised += bnbPaid;
        if(block.timestamp >= lastDailyUpdate + 1 days) {
            dailyTimestamp.push(block.timestamp);
            amountRaisedTillDay.push(bnbPaid);
            if(_accounts.add(account_)){
                uniqueHoldersTillDay.push(1);
            }
            lastDailyUpdate = block.timestamp;
        } else {
            amountRaisedTillDay[amountRaisedTillDay.length - 1] += bnbPaid;
            if(_accounts.add(account_)){
                uniqueHoldersTillDay[uniqueHoldersTillDay.length - 1] += 1;
            }
        }
    }

    function getStatsForWeek() external view returns(uint256[] memory timePeriods, uint256[] memory totalFundsRaised, uint256[] memory totalUniqueHolders) {
        uint256 historyLength = amountRaisedTillDay.length;
        uint256 arraySize = historyLength >= 7 ? 7 : historyLength;
        totalFundsRaised = new uint256[](arraySize);
        totalUniqueHolders = new uint256[](arraySize);
        timePeriods = new uint256[](arraySize);

        for(uint256 i = 0; i < arraySize; ++i){
            totalFundsRaised[i] = amountRaisedTillDay[i + historyLength - arraySize];
            totalUniqueHolders[i] = uniqueHoldersTillDay[i + historyLength - arraySize];
            timePeriods[i] = dailyTimestamp[i + historyLength - arraySize];
        }
    }

    function getTotalWalletCount() external view returns (uint256) {
        return _accounts.length();
    }
