contract PresaleXsvi is Ownable, ReentrancyGuard, Pausable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    struct Round {
        uint256 start;
        uint256 end;
        uint256 usdRate;
        uint256 usdRaised;
        uint256 totalTokens;
        uint256 targetUsd;
    }

    IERC20 public token = IERC20(0x4FB9799470b1f2355e2Cd119a1d409eA9ba25728);
    IERC20 public usdt = IERC20(0x55d398326f99059fF775485246999027B3197955);
    IERC20 public eth = IERC20(0x2170Ed0880ac9A755fd29B2688956BD959F933F8);
    Round[] public rounds;
    uint256 public currentRound;

    event TokensPurchased(address indexed buyer, uint256 amount);
    event TokensRecovered(address indexed owner, address indexed token, uint256 amount);
    event SetCurrentRound(uint256 round);

    AggregatorV3Interface public priceFeedBNB = AggregatorV3Interface(0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE);
    AggregatorV3Interface public priceFeedUSDT = AggregatorV3Interface(0xB97Ad0E74fa7d920791E90258A6E2085088b4320);
    AggregatorV3Interface public priceFeedETH = AggregatorV3Interface(0x9ef1B8c0E4F7dc8bF5719Ea496883DC6401d5b2e);

    constructor() {
        currentRound = 0;
        rounds.push(Round(1701367200, 1709229600, 0.01 * 10**18, 0, 100000000 * 10**18, 1000000 * 10**18));
        rounds.push(Round(1709247600, 1717178400, 0.03 * 10**18, 0, 87000000 * 10**18, 2610000 * 10**18));
        rounds.push(Round(1717196400, 1725127200, 0.07 * 10**18, 0, 173000000 * 10**18, 12110000 * 10**18));
    }

    function buyTokensWithBNB() external payable nonReentrant whenNotPaused {
        Round storage current = getCurrentRound();
        require(block.timestamp >= current.start, "Presale has not started yet");
        require(block.timestamp <= current.end, "Presale has ended");
        require(msg.value > 0, "Amount must be greater than zero");

        uint256 bnbAmount = msg.value;
        uint256 amount = getTokenAmountBNB(bnbAmount);
        uint256 usdValue = amount.mul(getPriceInUSD()).div(1e36);

        require(amount > 0, "Amount must be greater than zero");
        require(amount <= token.balanceOf(address(this)), "Not enough tokens available");

        (bool ethTransferSuccess, ) = payable(owner()).call{value: bnbAmount}("");
        require(ethTransferSuccess, "BNB transfer failed");

        SafeERC20.safeTransfer(token, msg.sender, amount);

        current.usdRaised = current.usdRaised.add(usdValue);
        emit TokensPurchased(msg.sender, amount);
    }

    function buyTokensWithUSDT(uint256 usdtAmount)
        external
        nonReentrant
        whenNotPaused
    {
        Round storage current = getCurrentRound();
        require(block.timestamp >= current.start, "Presale has not started yet");
        require(block.timestamp <= current.end, "Presale has ended");
        require(usdtAmount > 0, "Amount must be greater than zero");

        uint256 amount = getTokenAmountUSDT(usdtAmount);
        uint256 usdValue = amount.mul(getPriceInUSD()).div(1e36);

        require(amount > 0, "Amount must be greater than zero");
        require(amount <= token.balanceOf(address(this)), "Not enough tokens available");


        SafeERC20.safeTransferFrom(usdt, msg.sender, owner(), usdtAmount);

        SafeERC20.safeTransfer(token, msg.sender, amount);

        current.usdRaised = current.usdRaised.add(usdValue);
        emit TokensPurchased(msg.sender, amount);
    }

    function buyTokensWithETH(uint256 ethAmount)
        external
        nonReentrant
        whenNotPaused
    {
        Round storage current = getCurrentRound();
        require(block.timestamp >= current.start, "Presale has not started yet");
        require(block.timestamp <= current.end, "Presale has ended");
        require(ethAmount > 0, "Amount must be greater than zero");

        uint256 amount = getTokenAmountETH(ethAmount);
        uint256 usdValue = amount.mul(getPriceInUSD()).div(1e36);

        require(amount > 0, "Amount must be greater than zero");
        require(amount <= token.balanceOf(address(this)), "Not enough tokens available");

        SafeERC20.safeTransferFrom(eth, msg.sender, owner(), ethAmount);

        SafeERC20.safeTransfer(token, msg.sender, amount);

        current.usdRaised = current.usdRaised.add(usdValue);
        emit TokensPurchased(msg.sender, amount);
    }

    function getTokenAmountBNB(uint256 amountBNB)
        public
        view
        returns (uint256)
    {
        uint256 lastBNBPriceByUSD = getLatestPriceBNBPerUSD();
        return amountBNB.mul(lastBNBPriceByUSD).div(getPriceInUSD());
    }

    function getTokenAmountETH(uint256 amountETH)
        public
        view
        returns (uint256)
    {
        uint256 lastETHPriceByUSD = getLatestPriceETHPerUSD();
        return amountETH.mul(lastETHPriceByUSD).div(getPriceInUSD());
    }

    function getTokenAmountUSDT(uint256 amountUSDT)
        public
        view
        returns (uint256)
    {
        uint256 lastUSDTPriceByUSD = getLatestPriceUSDTPerUSD();
        return amountUSDT.mul(lastUSDTPriceByUSD).div(getPriceInUSD());
    }

    function getLatestPriceBNBPerUSD() public view returns (uint256) {
        (, int256 price, , , ) = priceFeedBNB.latestRoundData();
        price = (price * (10**10));
        return uint256(price);
    }

    function getLatestPriceETHPerUSD() public view returns (uint256) {
        (, int256 price, , , ) = priceFeedETH.latestRoundData();
        price = (price * (10**10));
        return uint256(price);
    }

    function getLatestPriceUSDTPerUSD() public view returns (uint256) {
        (, int256 price, , , ) = priceFeedUSDT.latestRoundData();
        price = (price * (10**10));
        return uint256(price);
    }

    function getPriceInUSD() public view returns (uint256) {
        return rounds[currentRound].usdRate;
    }

    function recoverBNBAmount() external onlyOwner {
        uint256 bnbBalance = address(this).balance;
        require(bnbBalance > 0, "No BNB balance to recover");
        (bool success, ) = payable(owner()).call{value: bnbBalance}("");
        require(success, "BNB recover failed");
    }

    function recoverWrongTokens(address _tokenAddress) external onlyOwner {
        IERC20 wrongToken = IERC20(_tokenAddress);
        uint256 balance = wrongToken.balanceOf(address(this));
        require(balance > 0, "No tokens to recover");
        require(wrongToken.transfer(owner(), balance), "Token recovery failed");
        emit TokensRecovered(msg.sender, _tokenAddress, balance);
    }

    function updateContractAddresses(
        address _newPriceFeedBNB,
        address _newPriceFeedUSDT,
        address _newPriceFeedETH
    ) external onlyOwner {
        require(_newPriceFeedBNB != address(0), "BNB price feed address cannot be zero");
        require(_newPriceFeedUSDT != address(0), "USDT price feed address cannot be zero");
        require(_newPriceFeedETH != address(0), "ETH price feed address cannot be zero");
        priceFeedBNB = AggregatorV3Interface(_newPriceFeedBNB);
        priceFeedUSDT = AggregatorV3Interface(_newPriceFeedUSDT);
        priceFeedETH = AggregatorV3Interface(_newPriceFeedETH);
    }

    function updateTime(
        uint256 roundIndex,
        uint256 start,
        uint256 end
    ) external onlyOwner {
        require(roundIndex < rounds.length, "Invalid round index");
        require(start < end, "Start time must be before end time");
        Round storage round = rounds[roundIndex];
        round.start = start;
        round.end = end;
    }

    function updateUsdRate(
        uint256 roundIndex,
        uint256 usdRate
    ) external onlyOwner {
        require(roundIndex < rounds.length, "Invalid round index");
        require(usdRate > 0, "USD rate must be greater than zero");
        Round storage round = rounds[roundIndex];
        round.usdRate = usdRate;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function setCurrentRound(uint256 newRound) external onlyOwner {
        require(currentRound < rounds.length, "No more rounds available");
        currentRound = newRound;
        emit SetCurrentRound(newRound);
    }

    function getCurrentRound() internal view returns (Round storage) {
        require(currentRound < rounds.length, "No more rounds available");
        return rounds[currentRound];
    }
}
