pragma solidity 0.7.6;

interface IRewardVesting {
  function stake(uint256 amount, address _user) external;
}

// File: contracts/MerkleDistributorManagerMining.sol

pragma solidity =0.7.6;





contract MerkleDistributorManagerMining is IMerkleDistributorManager {
    address public vest;
    IRewardVesting vesting;

    constructor(address _vest) {
        vest = _vest;
        vesting = IRewardVesting(vest);
    }

    function claim(
        uint64 distributionId,
        uint256 index,
        address account,
        uint256 amount,
        bytes32[] calldata merkleProof
    ) virtual external {
        require(!isClaimed(distributionId, index), 'MerkleDistributor: Drop already claimed.');
        Distribution storage dist = distributionMap[distributionId];
        require(amount <= dist.remainingAmount, "MerkleDistributor: Insufficient token.");

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(index, account, amount));
        require(MerkleProof.verify(merkleProof, dist.merkleRoot, node), 'MerkleDistributor: Invalid proof.');

        // Mark it claimed and send the token.
        _setClaimed(distributionId, index);
        dist.remainingAmount = dist.remainingAmount - amount;

        IERC20(dist.token).approve(vest, 0);
        IERC20(dist.token).approve(vest, uint256(-1));
        vesting.stake(amount, account);

        emit Claimed(distributionId, account, amount);
    }
}
