      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./KururuKuruToken.sol";

contract KururuOrderBookV1 is Ownable, ReentrancyGuard, ERC721Holder, ERC1155Holder {
    using SafeERC20 for IERC20;

    event Created(address indexed sender, uint256 indexed id);
    event Fulfilled(address indexed sender, uint256 indexed id);

    enum TokenType {
		ETHER,
        ERC20,
        ERC721,
        ERC1155
    }

    // seller swap a with buyer b
    struct OrderInfo {
        bool fulfill;
        address seller; // order creator
        address buyer;
        address a;
        address b;
        TokenType aType;
        TokenType bType;
        uint256 aId;
        uint256 bId;
        uint256 aAmount;
        uint256 bAmount;
    }

    // order id => info
    mapping (uint256 => OrderInfo) public orders;
    uint256 public total;

    KururuKuruToken public immutable kuru;
    address public receiver;
    uint256 public feeA;

    constructor(KururuKuruToken kuru_, uint256 a) {
        kuru = kuru_;
        receiver = msg.sender;
        feeA = a;
    }

    function setFee(uint256 a) external onlyOwner {
        feeA = a;
    }

    function setReceiver(address receiver_) external onlyOwner {
        require(receiver_ != address(0), "nope");
        receiver = receiver_;
    }

    function _transferFee(uint256 fee) internal {
        if (fee == 0) {
            return;
        }
        kuru.transferFrom(msg.sender, receiver, fee);
    }

    function create(
        address a, TokenType aType, uint256 aId, uint256 aAmount,
        address b, TokenType bType, uint256 bId, uint256 bAmount
    ) payable external returns (uint256) {
		if (aType != TokenType.ETHER) {
        	require(a != address(0), "invalid a");
		}
		if (bType != TokenType.ETHER) {
        	require(b != address(0), "invalid b");
		}
        require(aAmount > 0, "invalid aAmount");
        require(bAmount > 0, "invalid bAmount");

        _transferFee(feeA);

        uint256 id = total;
        total++;
        orders[id] = OrderInfo(
            false,
            msg.sender,
            address(0),
            a,
            b,
            aType,
            bType,
            aId,
            bId,
            aAmount,
            bAmount
        );
        _place(a, aType, aId, aAmount);

        emit Created(msg.sender, id);

        return id;
    }

    function fulfill(uint256 id) payable external {
        OrderInfo storage order = orders[id];
		require(order.seller != address(0), "no order");
        require(!order.fulfill, "order fulfilled");

        _place(order.b, order.bType, order.bId, order.bAmount);
        order.fulfill = true;
        order.buyer = msg.sender;

        _transfer(order.buyer, order.a, order.aType, order.aId, order.aAmount);
        _transfer(order.seller, order.b, order.bType, order.bId, order.bAmount);

        emit Fulfilled(msg.sender, id);
    }

    function cancel(uint256 id) external {
        OrderInfo storage order = orders[id];
		require(order.seller == msg.sender, "not seller");
        require(!order.fulfill, "order fulfilled");

        order.fulfill = true;

        _transfer(order.seller, order.a, order.aType, order.aId, order.aAmount);

        emit Fulfilled(address(0), id);
    }

    function _place(address tokenAddress, TokenType tokenType, uint256 tokenId, uint256 tokenAmount) internal {
		if (tokenType == TokenType.ETHER) {
			require(msg.value == tokenAmount, "ether not enough");
			return;
		}
        if (tokenType == TokenType.ERC20) {
            uint256 x = IERC20(tokenAddress).balanceOf(address(this));
            IERC20(tokenAddress).safeTransferFrom(msg.sender, address(this), tokenAmount);
            uint256 y = IERC20(tokenAddress).balanceOf(address(this));
            require(y == x + tokenAmount, "nope");
            return;
        }
        if (tokenType == TokenType.ERC721) {
            require(tokenAmount == 1, "invalid amount");
            IERC721(tokenAddress).safeTransferFrom(msg.sender, address(this), tokenId);
            return;
        }
        if (tokenType == TokenType.ERC1155) {
            IERC1155(tokenAddress).safeTransferFrom(msg.sender, address(this), tokenId, tokenAmount, "");
            return;
        }
        revert("invalid token type");
    }

    function _transfer(address to, address tokenAddress, TokenType tokenType, uint256 tokenId, uint256 tokenAmount) internal nonReentrant {
		if (tokenType == TokenType.ETHER) {
			require(address(this).balance >= tokenAmount, "no ether");
			(bool success,) = to.call{value: tokenAmount}("");
			require(success, "transfer error");
			return;
		}
        if (tokenType == TokenType.ERC20) {
            IERC20(tokenAddress).safeTransfer(to, tokenAmount);
            return;
        }
        if (tokenType == TokenType.ERC721) {
            require(tokenAmount == 1, "invalid a amount");
            IERC721(tokenAddress).safeTransferFrom(address(this), to, tokenId);
            return;
        }
        if (tokenType == TokenType.ERC1155) {
            IERC1155(tokenAddress).safeTransferFrom(address(this), to, tokenId, tokenAmount, "");
            return;
        }
        revert("invalid token type");
    }

	receive() external payable {
		revert();
	}
}
"
