    "contracts/AllocationProjectSwappableV2.sol": {
      "content": "pragma solidity ^0.8.0;

import "./erc/ERC1155.sol";
import "./utils/Ownable.sol";
import "./erc/IERC20.sol";
import "./erc/SafeERC20.sol";
import "./uniswap/IUniswapV2Router01.sol";


contract AllocationProjectSwappableV2 is ERC1155, Ownable {
    using SafeERC20 for IERC20;

    /// @notice Project name
    string public name;

    /// @notice MMPRO token address
    address public immutable mmpro;

    /// @notice BUSD token address
    address public immutable busd;

    /// @notice swapV2 router address
    address public immutable swapRouter;

    struct TokenInfo {
        uint256 allocationAmount;
        uint256 allocationLimit;
        uint256 maxAllocation;
        uint256 price;
        bool allowMint;
        bool pauseMint;
    }

    TokenInfo[] public tokensInfo;

    mapping (uint256 => mapping(address => uint256)) public userAllocation;

    /// @notice emits when token is minted(bought)
    event MintAndAllocate(
        uint256 indexed tokenId,
        address indexed minter,
        uint256 mmproReceived,
        uint256 busdAllocated
    );

    constructor(
        string memory _name,
        address _mmpro,
        address _busd,
        address _swapRouter,
        address __owner
    ) ERC1155(_name) {
        name = _name;
        mmpro = _mmpro;
        busd = _busd;
        swapRouter = _swapRouter;

        // approve busd just once to save gas later
        IERC20(_busd).approve(_swapRouter, type(uint256).max);
        _transferOwnership(__owner);
    }

    /// @notice mints tokenId to msg.sender by taking busd as a payment
    /// @param tokenId - token id to mint. Should not be minted before
    /// @param amountOutMin - minimum mmpro amount to be locked after mint
    /// @param deadline - busd-mmpro swap deadline
    function mintAndAllocate(
        uint256 tokenId,
        uint256 amountOutMin,
        uint256 deadline,
        uint256 allocationAmount,
        bytes[] calldata data
    ) external {

        TokenInfo memory tokenInfo = tokensInfo[tokenId];
        require(tokenInfo.allowMint, "Mint not allowed");
        require(!tokenInfo.pauseMint, "Mint paused");
        require(tokenInfo.maxAllocation >= allocationAmount, "AllocationAmount too high");
        require(IERC20(busd).balanceOf(_msgSender()) > (tokenInfo.price + allocationAmount), "Insufficient balance");


        IERC20(busd).safeTransferFrom(
            _msgSender(),
            address(this),
            tokenInfo.price + allocationAmount
        );

        uint256 mmproReceived = _swapExactBusdToMmpro(
            tokenInfo.price,
            amountOutMin,
            deadline
        );

        _mint(_msgSender(), tokenId, 1, "");
        tokenInfo.allocationAmount = tokenInfo.allocationAmount + 1;

        if (tokenInfo.allocationAmount > tokenInfo.allocationLimit) {
            tokenInfo.allowMint = false;
        }
        tokensInfo[tokenId] = tokenInfo;

        userAllocation[tokenId][_msgSender()] += allocationAmount;

        emit MintAndAllocate(tokenId, _msgSender(), mmproReceived, allocationAmount);
    }

    /// @notice sets new mint price
    function setAllocationPrice(uint256 tokenId, uint256 newPrice) external onlyOwner {
        tokensInfo[tokenId].price = newPrice;
    }

    /// @notice sets new allocation limit
    function setAllocationLimit(uint256 tokenId, uint128 limit) external onlyOwner {
        tokensInfo[tokenId].allocationLimit = limit;
    }

    /// @notice disallow mint
    function disallowMint(uint256 tokenId) external onlyOwner {
        tokensInfo[tokenId].allowMint = false;
    }

    /// @notice pause mint
    function pauseMint(uint256 tokenId) external onlyOwner {
        tokensInfo[tokenId].pauseMint = true;
    }

    /// @notice resume mint
    function resumeMint(uint256 tokenId) external onlyOwner {
        tokensInfo[tokenId].pauseMint = false;
    }

    function createToken(uint256 allocationLimit, uint256 maxAllocation, uint256 price) external onlyOwner {
        TokenInfo memory tokenInfo = TokenInfo(0, allocationLimit, maxAllocation, price, true, false);
        tokensInfo.push(tokenInfo);
    }

    /// @notice withdraws tokens from marketplace
    function withdrawExtraTokens(
        address token,
        uint256 amount,
        address withdrawTo
    ) external onlyOwner {
        IERC20(token).safeTransfer(withdrawTo, amount);
    }

    /// @dev swaps exact amount of busd to mmpro
    /// @return amount of mmrpo returned from swap
    function _swapExactBusdToMmpro(
        uint256 amountIn,
        uint256 amountOutMin,
        uint256 deadline
    ) private returns (uint256) {
        address[] memory path = new address[](2);

        path[0] = busd;
        path[1] = mmpro;

        return
        IUniswapV2Router01(swapRouter).swapExactTokensForTokens(
            amountIn,
            amountOutMin,
            path,
            address(this),
            deadline
        )[1];
    }
}
"
