      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";


interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address who) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
    function decimals() external view returns (uint8);
    function allowance(
        address owner,
        address spender
    ) external view returns (uint256);
    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);
}


contract PtcStaking is Ownable,ReentrancyGuard{

    using SafeMath for uint256;

    IERC20 public token;
    IERC20 public rewardToken;

    //Staking Info
    bool public isStakingEnabled = false;
    uint256 public tokenDecimals = 8;
    uint256 public maxStakingAmount = 51000000 * (10 ** tokenDecimals);
    uint256 public minStakingAmount = 260000000 * (10 ** tokenDecimals);
    uint256 public currentStakingId = 0;

    //Log Time for Actions
    uint256 public lastStakeEnabledAt ;
    uint256 public lastClaimEnabledAt ;
    uint256 public lastStakeDisabledAt;
    uint256 public lastClaimDisabledAt;
    uint256 public newWindowInitiatedAt;
    

    uint256 public totalRewardsDistributed = 0;

    //Fees
    uint256 public tokenFee =  40000  * (10 ** tokenDecimals);
    uint256 public stakingFee = 0.00080  ether;
    address treasuryWallet = 0x6d8f8CAC87CcE303fE7391549C7A357f0D98D09a;
    address deadAddress = 0x000000000000000000000000000000000000dEaD;
    

    //Rewards Distribution
    bytes32 public merkleRoot;
    bool public rewardsClaimable = false;
    

    struct Staker {
        uint256 stakedAmount;
        uint256 stakeTime;
        uint256 balanceAsOfStaking;
        bool isStaked;
    }

    
    //Events
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event Claimed(address indexed user, uint256 amount);

    mapping(uint256 => mapping(address => Staker)) public stakers;
    mapping(uint256 => mapping(address => bool)) public claimedWallets;
    mapping(uint256 => uint256) public totalStaked;






    constructor(address tokenAddress, address rewardTokenAddress) {
        token = IERC20(tokenAddress);
        rewardToken = IERC20(rewardTokenAddress);
        tokenDecimals = token.decimals();
    }

    function stake(uint256 amount) public payable nonReentrant{
        uint256 actualAmount = amount.sub(tokenFee);
        uint256 balanceToken = token.balanceOf(msg.sender);
        require(isStakingEnabled, "Staking is not enabled");
        require(msg.value >= stakingFee, "Insufficient staking fee");
        require(stakers[currentStakingId][msg.sender].isStaked == false, "Already staked");
        require(amount >= minStakingAmount, "Amount is less than minimum staking amount");
        require(amount <= maxStakingAmount, "Amount is more than maximum staking amount");
        require(balanceToken >= amount, "Insufficient token balance");
        require(token.allowance(msg.sender, address(this)) >= tokenFee, "Insufficient token allowance");
        require(token.transferFrom(msg.sender,deadAddress, tokenFee), "Token transfer failed");
        stakers[currentStakingId][msg.sender].stakedAmount = actualAmount;
        stakers[currentStakingId][msg.sender].stakeTime = block.timestamp;
        stakers[currentStakingId][msg.sender].isStaked = true;
        stakers[currentStakingId][msg.sender].balanceAsOfStaking = balanceToken.sub(tokenFee);
        totalStaked[currentStakingId] = totalStaked[currentStakingId].add(actualAmount);
        emit Staked(msg.sender, actualAmount);   
    }  

    function initiateNewStakingWindow() external onlyOwner{
        currentStakingId = currentStakingId.add(1);
        rewardsClaimable = false;
        newWindowInitiatedAt = block.timestamp;
    }

    function isValidRewardProof(address _address,uint256 _amount,bytes32[] calldata merkleProof) public view returns(bool){
        bytes32 leaf = keccak256(abi.encodePacked(_address,_amount));
        return MerkleProof.verify(merkleProof, merkleRoot, leaf);
    }

    function claim(bytes32[] calldata merkleProof,uint256 _amount) external nonReentrant{
        require(isStakingEnabled == false, "Cannot claim while staking is enabled");
        require(rewardsClaimable == true, "Rewards not claimable");
        require(claimedWallets[currentStakingId][msg.sender] == false, "Already claimed");
        require(stakers[currentStakingId][msg.sender].isStaked == true, "Not staked");
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender,_amount));
        require(
            MerkleProof.verify(merkleProof, merkleRoot, leaf),
            "You are Not whitelisted"
        );
        rewardToken.transfer(msg.sender, _amount);
        claimedWallets[currentStakingId][msg.sender] = true;
        emit Claimed(msg.sender, _amount);
    }


    function toogleStaking() external onlyOwner {
        
        isStakingEnabled = !isStakingEnabled;

        if(isStakingEnabled){
            lastStakeEnabledAt = block.timestamp;
        }
        else{
            lastStakeDisabledAt = block.timestamp;
        }
        
    }

    function togleRewardsClaimable() external onlyOwner {
        rewardsClaimable = !rewardsClaimable;
        if(rewardsClaimable){
            lastClaimEnabledAt = block.timestamp;
        }
        else{
            lastStakeDisabledAt = block.timestamp;
        }
    }

    function setMaxStakingAmount(uint256 amount) external onlyOwner {
        maxStakingAmount = amount;
    }

    function setMinStakingAmount(uint256 amount) external onlyOwner {
        minStakingAmount = amount;
    }

    function setTokenDecimals(uint256 decimals) external onlyOwner {
        tokenDecimals = decimals;
    }

    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        merkleRoot = _merkleRoot;
    }

    function updateFee(
        uint256 _tokenFee,
        uint256 _stakingFee
    ) external onlyOwner {
        tokenFee = _tokenFee;
        stakingFee = _stakingFee;
    }

    function withdrawToken(
        address tokenAddress,
        uint256 amount
    ) external onlyOwner {
        IERC20 _token = IERC20(tokenAddress);
        require(_token.transfer(msg.sender, amount), "Transfer failed");
    }

    function setTreasuryWallet(address _treasuryWallet) external onlyOwner {
        treasuryWallet = _treasuryWallet;
    }

    function withdrawBNB() external onlyOwner {
        payable(treasuryWallet).transfer(address(this).balance);
    }

}"
