contract BTCommune is ERC20, Ownable {
    using SafeMath for uint256;

    IRouter public router;
    address public pair;
    
    bool private swapping;

    address public deadWallet = 0x000000000000000000000000000000000000dEaD;
    address public marketingWallet = 0x7A01005088325e071492EE7F0C211C8Dc6c990A0;
    address public vaultWallet = 0x5B727b92B65E1D28d69468C7F3f8A60e987F9728;

    uint256 public swapTokensAtAmount = 100_000 * (10**18);

    //Anti-dump    
    uint256 public maxWalletToken = 2222222 * (10**18);  // 2%
    
    // fees
    uint256 public VaultFee = 2;
    uint256 public LiquidityFee = 1;
    uint256 public MarketingFee = 2;
    uint256 public totalFees = VaultFee + LiquidityFee + MarketingFee;

    uint256 public sellVaultFee = 2;
    uint256 public sellLiquidityFee = 1;
    uint256 public sellMarketingFee = 2;
    uint256 public sellTotalFees = sellVaultFee + sellLiquidityFee + sellMarketingFee;

    // exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event MarketingWalletUpdated(address indexed newMarketingWallet, address indexed oldMarketingWallet);    

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    constructor() ERC20("BTCo", "BTCo", 18) {

       // router = IRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        
        //pair = IFactory(router.factory()).createPair(address(this), router.WETH());

       // _setAutomatedMarketMakerPair(pair, true);

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(marketingWallet), true);
        
        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(),  111111111 * (10**18));
    }

    receive() external payable {

    }
    
    // @dev Owner functions start -------------------------------------
    
    function updateSwapTokensAtAmount(uint256 newAmount) external onlyOwner{
        swapTokensAtAmount = newAmount * 10**18;
    }

    // excludes wallets from max txn and fees.
    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    // allows multiple exclusions at once
    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) external onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    // updates the marketing wallet
    function updateMarketingWallet(address newMarketingWallet) external onlyOwner {
        require(newMarketingWallet != marketingWallet, "PERA: The operations wallet is already this address");
        excludeFromFees(newMarketingWallet, true);
        emit MarketingWalletUpdated(newMarketingWallet, marketingWallet);
        marketingWallet = newMarketingWallet;
    }
    
    function updateFees(uint256 _vault, uint256 _liquidity, uint256 _marketing) external onlyOwner {
    require(_vault.add(_liquidity).add(_marketing) <= 10, "Cannot be total fees higher than 10%");
        VaultFee = _vault;
        LiquidityFee = _liquidity;
        MarketingFee = _marketing;        
        totalFees = VaultFee + LiquidityFee + MarketingFee;
    }

    function updateSellFees(uint256 _vault, uint256 _liquidity, uint256 _marketing) external onlyOwner {
    require(_vault.add(_liquidity).add(_marketing) <= 10, "Cannot be total fees higher than 10%");
        sellVaultFee = _vault;
        sellLiquidityFee = _liquidity;
        sellMarketingFee = _marketing;        
        sellTotalFees = sellVaultFee + sellLiquidityFee + sellMarketingFee;
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }
    
    // @dev Token functions
    
    function _setAutomatedMarketMakerPair(address _pair, bool value) private {
        require(automatedMarketMakerPairs[_pair] != value, "PERA: Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[_pair] = value;
    }
    
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        
        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
        

        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;
        bool excludedAccount = _isExcludedFromFees[from] || _isExcludedFromFees[to];

        if (automatedMarketMakerPairs[from] && !excludedAccount) {

            uint256 contractBalanceRecipient = balanceOf(to);
            require(contractBalanceRecipient + amount <= maxWalletToken,"PERA: maximum token per wallet amount exceed");
        }


        if(canSwap && !swapping && !automatedMarketMakerPairs[from] && !excludedAccount) {
            swapping = true;
            swapAndLiquify(contractTokenBalance);
            swapping = false;
        }


        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(excludedAccount) {
            takeFee = false;
        }

        if(takeFee) {
            
            uint256 fees;
            if(automatedMarketMakerPairs[to]){
               fees = amount * sellTotalFees / 100;
            } 
            else if(automatedMarketMakerPairs[from]){
             fees = amount * totalFees / 100;
            }
            else {
                fees = 0;
            }
            if(fees > 0){  
              amount = amount.sub(fees);
              super._transfer(from, address(this), fees);
            }
        }

        super._transfer(from, to, amount);

    }

   function swapAndLiquify(uint256 tokens) private{
       // Split the contract balance into halves
        uint256 denominator = sellTotalFees.mul(2);
        uint256 tokensToAddLiquidityWith = tokens.mul(sellLiquidityFee).div(denominator);
        uint256 toSwap = tokens.sub(tokensToAddLiquidityWith);

        uint256 initialBalance = address(this).balance;

        swapTokensForEth(toSwap);

        uint256 deltaBalance = address(this).balance.sub(initialBalance);
        uint256 unitBalance= deltaBalance.div(denominator).sub(sellLiquidityFee);
        uint256 bnbToAddLiquidityWith = unitBalance.mul(sellLiquidityFee);

        if(bnbToAddLiquidityWith > 0){
            // Add liquidity to pancake
            addLiquidity(tokensToAddLiquidityWith, bnbToAddLiquidityWith);
        }
      
        uint256 marketingAmt = unitBalance.mul(2).mul(sellMarketingFee);
        if(marketingAmt > 0){
            payable(marketingWallet).transfer(address(this).balance);
        }

         uint256 vaultAmt = unitBalance.mul(2).mul(sellVaultFee);
        if(vaultAmt > 0){
            payable(vaultWallet).transfer(address(this).balance);
        }

    }

    function swapTokensForEth(uint256 tokenAmount) private {

        
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), tokenAmount);

        // make the swap
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
        
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(router), tokenAmount);

        // add the liquidity
        router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            deadWallet,
            block.timestamp
        );
        
    }
}
