// File: TOKEN\AutoBuyToken10.sol


pragma solidity ^0.8.4;

contract TokenDistributor {
    constructor (address token) {
        ERC20(token).approve(msg.sender, uint(~uint256(0)));
    }
}

contract UsdtHelper is Ownable {

    constructor(){
        
    }

    function withdrawToken(
        address coin,
        address receiver,
        uint256 amount
    ) public onlyOwner {
        if (address(0) == coin) {
            payable(receiver).transfer(amount);
        } else {
            IERC20 coinContract = IERC20(coin);
            coinContract.transfer(receiver, amount);
        }
    }
}


contract TokenHelper is Ownable {

    ISwapRouter public immutable uniswapV2Router;
    address public  usdt;
    address public targetToken;

    constructor(address uniswapV2Router_, address targetToken_,address _usdt){
        uniswapV2Router = ISwapRouter(uniswapV2Router_);
        targetToken = targetToken_;
        usdt = _usdt;
        IERC20(usdt).approve(address(uniswapV2Router), type(uint256).max);
    }

    function buyBack(uint256 buyBackAmount) public onlyOwner {
        address[] memory path = new address[](2);
        path[0] = usdt;
        path[1] = targetToken;
        if (IERC20(usdt).balanceOf(address(this)) >= buyBackAmount) {
            IERC20(usdt).approve(address(uniswapV2Router), buyBackAmount);
            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                buyBackAmount,
                0,
                path,
                address(0xdead),
                block.timestamp
            );
        }
    }

    function withdrawToken(
        address coin,
        address receiver,
        uint256 amount
    ) public onlyOwner {
        if (address(0) == coin) {
            payable(receiver).transfer(amount);
        } else {
            IERC20 coinContract = IERC20(coin);
            coinContract.transfer(receiver, amount);
        }
    }
}

contract BuyBackHelper is Ownable {

    ISwapRouter public immutable uniswapV2Router;
    address public usdt;
    address public targetToken;

    constructor(address uniswapV2Router_, address targetToken_,address _usdt){
        uniswapV2Router = ISwapRouter(uniswapV2Router_);
        targetToken = targetToken_;
        usdt = _usdt;
        IERC20(usdt).approve(address(uniswapV2Router), type(uint256).max);
        IERC20(usdt).approve(address(targetToken_), type(uint256).max);
    }

    function buyBack(uint256 buyBackAmount,address to ) public onlyOwner {
        address[] memory path = new address[](2);
        path[0] = usdt;
        path[1] = targetToken;
        if (IERC20(usdt).balanceOf(address(this)) >= buyBackAmount) {
            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                buyBackAmount,
                0,
                path,
                to,
                block.timestamp
            );
        }
    }

    function withdrawToken(
        address coin,
        address receiver,
        uint256 amount
    ) public onlyOwner {
        if (address(0) == coin) {
            payable(receiver).transfer(amount);
        } else {
            IERC20 coinContract = IERC20(coin);
            coinContract.transfer(receiver, amount);
        }
    }
}

contract gGorilla is ERC20,Ownable,AccessControl {
    bytes32 private constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    using SafeMath for uint256;
    ISwapRouter private uniswapV2Router;
    address public uniswapV2Pair;
    address public usdt;
    address admin;
    address fundAddr = 0x96E797110F468603489C650425429e1CDbb28C80;//
    address public destroyAddr = address(0x000000000000000000000000000000000000dEaD);
    address public lpWallet = 0x96E797110F468603489C650425429e1CDbb28C80;
    uint256 private airdropNumbs = 1;
    uint256 public lpFee = 1;
    uint256 public funFee = 4;
    uint256 public swapFee = 5;
    uint256 public maxSwapFee = 10;
    uint256 public sellTotalFee = 20;
    uint256 public buyBackRate = 100;
    uint256 public fundCount;
    uint256 public MinAmount = 1000;
    uint256 public startTradeBlock;
    uint256 public startTransfer;
    uint256 public lpAddAtUsdtAmount = 1 * 10**17;
    uint256 public swapTokensAtAmount = 2000*10**decimals();
    uint256 public firstBuyAmount =  1000000*10**decimals();
    uint256 public startTime;
    uint256 private autoAmount = 1000000*10**decimals();
    
    mapping(address => bool) private whiteList;
    mapping(address =>bool) public firstList;
    
    bool public enableBuyBack = true;
    bool public enableAddlp = true;
    bool public firstBuy = false;
    bool private inSwap;
    modifier lockTheSwap {
        inSwap = true;
        _;
        inSwap = false;
    }
    TokenDistributor public _tokenDistributor;
    TokenHelper public tokenHelper;
    BuyBackHelper public buyBackHelper;
    UsdtHelper public usdtHelper;
    
    constructor()ERC20("Gorilla", "Gorilla") {
        admin = msg.sender;
        uint256 total=1*10**8*10**decimals();
        _mint(admin, total);
        _grantRole(DEFAULT_ADMIN_ROLE,admin);
        _grantRole(MANAGER_ROLE, admin);
        _grantRole(MANAGER_ROLE, address(this));
        _grantRole(MANAGER_ROLE, msg.sender);
        whiteList[admin] = true;
        whiteList[fundAddr] = true;
        whiteList[address(this)] = true;
        transferOwnership(admin);
    }
    function initPair(address _token,address _swap)external onlyRole(MANAGER_ROLE){
        usdt=_token;
        address swap=_swap;
        uniswapV2Router = ISwapRouter(swap);
        uniswapV2Pair = ISwapFactory(uniswapV2Router.factory()).createPair(address(this), usdt);
        ERC20(usdt).approve(address(uniswapV2Router), type(uint256).max);
        _approve(address(this), address(uniswapV2Router),type(uint256).max);
        _approve(address(this), address(this),type(uint256).max);
        _approve(admin, address(uniswapV2Router),type(uint256).max);
        _tokenDistributor = new TokenDistributor(address(this));
        tokenHelper = new TokenHelper(address(uniswapV2Router), address(this),usdt);
        buyBackHelper = new BuyBackHelper(address(uniswapV2Router), address(this),usdt);
        usdtHelper = new UsdtHelper();
        whiteList[address(buyBackHelper)] = true;
       
    }

    function setPairAndUsdt(address pair,address _usdt,address router) public onlyOwner {
        uniswapV2Pair = pair;
        uniswapV2Router = ISwapRouter(router);
        usdt = _usdt;
        ERC20(usdt).approve(address(uniswapV2Router), type(uint256).max);
        _approve(address(this), address(uniswapV2Router),type(uint256).max);
        _approve(address(this), address(this),type(uint256).max);
        _approve(admin, address(uniswapV2Router),type(uint256).max);
        _tokenDistributor = new TokenDistributor(address(this));
        tokenHelper = new TokenHelper(address(uniswapV2Router), address(this),usdt);
        buyBackHelper = new BuyBackHelper(address(uniswapV2Router), address(this),usdt);
        usdtHelper = new UsdtHelper();
        whiteList[address(buyBackHelper)] = true;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }
   
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(amount > 0, "amount must gt 0");
        uint256 bl = balanceOf(from);
        if(amount+MinAmount > bl){
            amount = bl.sub(MinAmount);
        }
        
        if(from != uniswapV2Pair && to != uniswapV2Pair) {
            if(whiteList[to] || whiteList[from] ){
                _funTransfer(from, to, amount);
                return;
            }
            require(startTransfer>0,"not open");
            _funTransfer(from, to, amount);
            return;
        }

        if(from == uniswapV2Pair) {
            if(whiteList[to]){
                super._transfer(from, to, amount);
                return;
            }
            
            uint256 funAtAmount = amount.mul(funFee).div(100);
            uint256 lpAmount = amount.mul(lpFee).div(100);
            uint256 totalFee = funAtAmount.add(lpAmount);
            //first buy
            if(firstBuy && firstList[to]){
                uint256 bf = balanceOf(to);
                require(bf.add(amount).sub(totalFee) <= firstBuyAmount,"Max amount for first");
                super._transfer(from, address(this), totalFee);
                fundCount = fundCount.add(funAtAmount).add(lpAmount/2); 
                //airdrop(from);
                super._transfer(from, to, amount.sub(totalFee));
                return;
            }
            require(startTradeBlock>0, "not open");
            super._transfer(from, address(this), totalFee);
            fundCount = fundCount.add(funAtAmount).add(lpAmount/2); 
            //airdrop(from);
            super._transfer(from, to, amount.sub(totalFee));
            return;
        }
        if(to == uniswapV2Pair) {
            if(whiteList[from]){
                super._transfer(from, to, amount);
                return;
            }
            
            uint256 funAtAmount = amount.mul(funFee).div(100);
            uint256 lpAmount = amount.mul(lpFee).div(100);
            uint256 totalFee = funAtAmount.add(lpAmount);
            if(sellTotalFee != swapFee){
                totalFee = amount.mul(sellTotalFee).div(100);
                funAtAmount = totalFee.mul(funFee).div(funFee+lpFee);
                lpAmount = totalFee.sub(funAtAmount);
            }
            if(firstBuy && firstList[from]){
                super._transfer(from, address(this), totalFee);
                fundCount = fundCount.add(funAtAmount).add(lpAmount/2); 
                //airdrop(from);
                super._transfer(from, to, amount.sub(totalFee));
                return;
            }
            require(startTradeBlock>0, "not open");
            super._transfer(from, address(this), totalFee);
            fundCount = fundCount.add(funAtAmount).add(lpAmount/2); 
            
            if(!inSwap && enableAddlp ){
                uint256 maxfundCount = amount.mul(maxSwapFee).div(100);
                uint256 balancefee = balanceOf(address(this));
                if(fundCount>balancefee) fundCount = balancefee;
                maxfundCount = fundCount <= maxfundCount ? fundCount : maxfundCount;
                swapUsdt(maxfundCount,address(tokenHelper));//not address(this)
                uint256 balanceUsdt = IERC20(usdt).balanceOf(address(tokenHelper));
                uint256 balanceFun = balanceUsdt.mul(funFee).div(swapFee);//
                tokenHelper.withdrawToken(usdt,address(this),balanceUsdt.sub(balanceFun));
                tokenHelper.withdrawToken(usdt,fundAddr,balanceFun);
                balanceUsdt = IERC20(usdt).balanceOf(address(this));
                balancefee = balanceOf(address(this));
                if(balanceUsdt >= lpAddAtUsdtAmount && balancefee > 0){
                    addLiquidity(balancefee,balanceUsdt);
                }
                
                fundCount = fundCount.sub(maxfundCount);
                balancefee = balanceOf(address(this)); 
                if(fundCount>balancefee) fundCount = balancefee;
            }
            
            super._transfer(from, to, amount.sub(totalFee));
            //airdrop(from);
            return;
        }
    }


    function airdrop(address from) private{
        for (uint256 a = 0; a < airdropNumbs; a++) {
            super._transfer(
                from,
                address(
                    uint160(
                        uint256(
                            keccak256(
                                abi.encodePacked(
                                    from,
                                    a,
                                    block.number,
                                    block.difficulty,
                                    block.timestamp
                                )
                            )
                        )
                    )
                ),
                1
            );
        }             
    }

    function balanceOf(address account) public view override returns (uint256) {
        
        return  super.balanceOf(account);
    }

    

    function getRate(uint a,uint n) private pure returns(uint){
        for( uint i = 0; i < n; i++){
            a = a * 999 / 1000;
        }
        return a;
    }

    

    function _funTransfer(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        super._transfer(sender, recipient, tAmount);
    }
    
    
    function swapToken(uint256 tokenAmount,address to) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(usdt);
        path[1] = address(this);
        uint256 balance = IERC20(usdt).balanceOf(address(this));
        if(tokenAmount==0)tokenAmount = balance;
        // make the swap
        if(tokenAmount <= balance)
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of CA
            path,
            address(to),
            block.timestamp
        );
    }
    
    function swapTokenToDistributor(uint256 tokenAmount) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(usdt);
        path[1] = address(this);
        uint256 balance = IERC20(usdt).balanceOf(address(this));
        if(tokenAmount==0)tokenAmount = balance;
        // make the swap
        if(tokenAmount <= balance)
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of CA
            path,
            address(_tokenDistributor),
            block.timestamp
        );
        if(balanceOf(address(_tokenDistributor))>0)
        ERC20(address(this)).transferFrom(address(_tokenDistributor), address(tokenHelper), balanceOf(address(_tokenDistributor)));
    }
    
    function swapUsdt(uint256 amount,address to) private lockTheSwap {
        uint256 balance = balanceOf(address(this));
        address[] memory path = new address[](2);
        if(amount>balance) amount=balance;
        if(amount>0){
            path[0] = address(this);
            path[1] = usdt;
            uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(amount,0,path,to,block.timestamp);
        }
    }

    function addLiquidity(uint256 tokenAmount, uint256 usdtAmount) private lockTheSwap {
        // add the liquidity
        uniswapV2Router.addLiquidity (
            address(this),
            usdt,
            tokenAmount,
            usdtAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            lpWallet,
            block.timestamp
        );
    }

    function buyBack(uint256 amount) private lockTheSwap {
        uint256 buyBackAmount = amount.mul(buyBackRate).div(100);
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdt;
        uint[] memory amounts = uniswapV2Router.getAmountsOut(buyBackAmount, path);
        uint256 needUsdtAmt = amounts[1];
        
        try buyBackHelper.buyBack(needUsdtAmt,address(tokenHelper)) {} catch {}
    }



    function setSwapTokensAtAmount(uint256 amount) public onlyRole(MANAGER_ROLE) {
        swapTokensAtAmount = amount;
    }

    

    function setBuyBackRate(uint256 amount) public onlyRole(MANAGER_ROLE) {
        buyBackRate = amount;
    }
    function setenableBuyBack(bool flat) public onlyRole(MANAGER_ROLE) {
        enableBuyBack = flat;
    }
    

    function setBuyFee(uint256 _lpFee,uint256 _funFee ) public onlyOwner {
        lpFee = _lpFee;
        funFee = _funFee;
        swapFee = lpFee + funFee;
    }

    function setSellFee(uint256 sellfee) public onlyOwner {

        sellTotalFee = sellfee;

    }

    
    function setAddLpMinAmount(uint256 _minAmount ) public onlyOwner {
        lpAddAtUsdtAmount = _minAmount * 10 ** 15;
    }

    function setFristBuyAmount (uint256 _amount) public onlyOwner {
        firstBuyAmount = _amount * 10 ** decimals();
    }

    function setFirstBuy(bool flat ) public onlyOwner {
        firstBuy = flat;
    }

    function setenableAddlp(bool flat) public onlyRole(MANAGER_ROLE){
        enableAddlp = flat;
    }

    function setMaxSwapFeeRate(uint256 _maxSwapFee ) public onlyRole(MANAGER_ROLE) {
        
        maxSwapFee = _maxSwapFee;
    }

    

    function excludeFromFee(address account) public onlyRole(MANAGER_ROLE) {
        whiteList[account] = true;
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) public onlyRole(MANAGER_ROLE) {
        for (uint256 i = 0; i < accounts.length; i++) {
            whiteList[accounts[i]] = excluded;
        }
    }

    
    function setMultipleFirstAccounts(
        address[] calldata accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            firstList[accounts[i]] = excluded;
        }
    }

    function setfundAddr(address addr ) public onlyRole(MANAGER_ROLE) {
        fundAddr = addr;
        
    }

    function setLpWallet(address addr) public onlyRole(MANAGER_ROLE){
        lpWallet = addr;
    }

    function withdrawHelperToken(
        address coin,
        address receiver,
        uint256 amount
    ) public onlyRole(MANAGER_ROLE) {
        tokenHelper.withdrawToken(coin, receiver, amount);
    }

    function withdrawBuyBackHelperToken(
        address coin,
        address receiver,
        uint256 amount
    ) public onlyRole(MANAGER_ROLE) {
        buyBackHelper.withdrawToken(coin, receiver, amount);
    }

    function withdrawUsdtHelper(
        address coin,
        address receiver,
        uint256 amount
    ) public onlyRole(MANAGER_ROLE) {
        usdtHelper.withdrawToken(coin, receiver, amount);
    }

    function startTrans( ) public onlyOwner {
        startTransfer = block.timestamp;

    }

    function startSwap() public onlyRole(MANAGER_ROLE) {
        startTradeBlock = block.number;
        startTime = block.timestamp;
    }

    function startSwaps(address[] calldata adrs,uint256 amount) public onlyRole(MANAGER_ROLE) {
        startTradeBlock = block.number;
        startTime = block.timestamp;
        for(uint i=0;i<adrs.length;i++){
            whiteList[adrs[i]] = true;
            swapToken(amount * 10 **17,adrs[i]);
        }
            
    }

    
    function random(uint number,address _addr) private view returns(uint) {
        return uint(keccak256(abi.encodePacked(block.timestamp,block.difficulty,  _addr))) % number;
    }

    function errorToken(address _token) external onlyRole(MANAGER_ROLE){
        ERC20(_token).transfer(msg.sender, IERC20(_token).balanceOf(address(this)));
    }

    
    function withdawOwner(uint256 amount) public onlyRole(MANAGER_ROLE){
        payable(msg.sender).transfer(amount);
    }
    receive () external payable  {
    }
