pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/presets/ERC20PresetMinterPauser.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-IERC20Permit.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

interface ICrossChainSwap {
    function swapFrom(
        uint256 netId,
        address token,
        address from,
        uint256 amount
    ) external;
}

contract ERC20Token is ERC20PresetMinterPauser, IERC20Permit, EIP712 {
    using Counters for Counters.Counter;
    mapping(address => Counters.Counter) private _nonces;

    // solhint-disable-next-line var-name-mixedcase
    bytes32 private immutable _PERMIT_TYPEHASH =
        keccak256(
            "Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)"
        );

    address public crossChainSwap;

    constructor(string memory name, string memory symbol)
        ERC20PresetMinterPauser(name, symbol)
        EIP712(name, "1")
    {}

    function swapToEthereum(uint256 amount) public {
        uint256 netId = 0; // Ethereum Mainnet
        address msgSender = _msgSender();
        _approve(msgSender, crossChainSwap, amount);
        ICrossChainSwap(crossChainSwap).swapFrom(
            netId,
            address(this),
            msgSender,
            amount
        );
    }

    function swapToNetwork(uint256 netId, uint256 amount) public {
        address msgSender = _msgSender();
        _approve(msgSender, crossChainSwap, amount);
        ICrossChainSwap(crossChainSwap).swapFrom(
            netId,
            address(this),
            msgSender,
            amount
        );
    }

    function setSwap(address crossChainSwapAddress) public {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "Must have admin role"
        );
        crossChainSwap = crossChainSwapAddress;
    }

    /**
     * @dev See {IERC20Permit-permit}.
     */
    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public virtual override {
        // solhint-disable-next-line not-rely-on-time
        require(block.timestamp <= deadline, "ERC20Permit: expired deadline");

        bytes32 structHash =
            keccak256(
                abi.encode(
                    _PERMIT_TYPEHASH,
                    owner,
                    spender,
                    value,
                    _nonces[owner].current(),
                    deadline
                )
            );

        bytes32 hash = _hashTypedDataV4(structHash);

        address signer = ECDSA.recover(hash, v, r, s);
        require(signer == owner, "ERC20Permit: invalid signature");

        _nonces[owner].increment();
        _approve(owner, spender, value);
    }

    /**
     * @dev See {IERC20Permit-nonces}.
     */
    function nonces(address owner) public view override returns (uint256) {
        return _nonces[owner].current();
    }

    /**
     * @dev See {IERC20Permit-DOMAIN_SEPARATOR}.
     */
    // solhint-disable-next-line func-name-mixedcase
    function DOMAIN_SEPARATOR() external view override returns (bytes32) {
        return _domainSeparatorV4();
    }
}
