      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "IERC20.sol";
import "Initializable.sol";
import "OwnableUpgradeable.sol";
import "SafeERC20.sol";

import "AggregatorV3Interface.sol";

/// @title CakeRush
/// @author Breakfast Team
/// @notice Cake Rush is the initial contract we need in order to accumulate CAKE. It's initialisable in order to then be staked into the MasterChief protocol
/// @dev the owner of this contract holds a lot of power, and should be owned by a multisig
contract CakeRush is Initializable, OwnableUpgradeable {
    using SafeERC20 for IERC20;
    AggregatorV3Interface public priceFeed;

    address public cake;
    address public xCake;
    address public mainStaking;

    mapping(address => uint256) public deposited;
    mapping(address => uint256) public userToPersonalMultiplier;

    uint256[] public personalThresholds;
    uint256[] public personalMultipliers;

    uint256[] public communityThresholds;
    uint256[] public communityMultipliers;

    uint256[] public emissionThresholds;
    uint256[] public emissionMultipliers;

    uint256 public totalDeposited;
    uint256 public totalPersonalWeightedDeposit;
    uint256 public totalDepositedInLockedTokens;

    uint256 public priceToken;
    uint256 public emissionPerYear;

    uint256 public constant DENOMINATOR = 10000;
    uint256 public MAXIMUM_PERCENTAGE_LOCKED;

    function __CakeRush_init_(address _cake) external initializer {
        __Ownable_init();
        cake = _cake;
        priceFeed = AggregatorV3Interface(0xB6064eD41d4f67e353768aA239cA86f4F73665a1);
    }

    // Getters
    function getCommunityMultiplierLength() external view returns (uint256) {
        return communityMultipliers.length;
    }

    function getPersonalMultiplierLength() external view returns (uint256) {
        return personalMultipliers.length;
    }

    function getCakePrice() public view returns (uint256) {
        (, int256 price, , , ) = priceFeed.latestRoundData();
        return uint256(price);
    }

    /// @notice The more conversion, the more emission will be given out to xCake/Cake participants. This enables computation of this based on emission thresholds.
    /// @return boost in DENOMINATOR precision (10**4) divide by 10**4 to get the percentage boost
    function getEmissionBoost() public view returns (uint256 boost) {
        boost = 1000;
        uint256 length = emissionMultipliers.length;
        for (uint256 i; i < length; i++) {
            if (totalDeposited >= emissionThresholds[i]) {
                boost += emissionMultipliers[i];
            }
        }
    }

    /// @notice Computes overall value distrbuted to the protocol by emissions. Need to divide by emission allocation.
    /// @return emission with 12 decimal precision, divide by 10**12 to get the dollar value of our token emission per year
    function getRevenueDistributed() public view returns (uint256) {
        return emissionPerYear * priceToken * DENOMINATOR;
    }

    /// @notice This computes the overall denominator when computing APR.
    function getTotalWeightedDeposits() public view returns (uint256) {
        return (totalPersonalWeightedDeposit +
            totalDeposited *
            (DENOMINATOR + getCommunityBoost()));
    }

    /// @notice For users who have not participated in the rush, we can compute the APR as they will have no community nor personal boost
    /// @return APR in percentage, with 8 decimal precision (divide by 10**8 to get the percentage)
    function computeAPRForNonParticipant() external view returns (uint256) {
        return
            ((getRevenueDistributed() * DENOMINATOR * (10**18)) /
                (getTotalWeightedDeposits() * getCakePrice())) * getEmissionBoost();
    }

    /// @notice For all users who have participated in the rush, we can compute the APR as they will have only community boost
    /// @return APR in percentage, with 8 decimal precision (divide by 10**8 to get the percentage)
    function computeAPRForParticipant() external view returns (uint256) {
        uint256 communityBoost = getCommunityBoost();
        return
            ((getRevenueDistributed() * (DENOMINATOR + communityBoost) * (10**18)) /
                (getTotalWeightedDeposits() * getCakePrice())) * getEmissionBoost();
    }

    /// @notice For personal APR, we can compute the APR as they will have community boost and personal boost
    /// @return APR in percentage, with 8 decimal precision (divide by 10**8 to get the percentage)
    function computePersonalAPR(address user) external view returns (uint256) {
        uint256 boost = getBoost(user);
        return
            ((getRevenueDistributed() * (DENOMINATOR + boost) * (10**18)) /
                (getTotalWeightedDeposits() * getCakePrice())) * getEmissionBoost();
    }

    /// @notice To entice user converting more, we can compute the APR at a certain tier.
    /// @return APR in percentage, with 8 decimal precision (divide by 10**8 to get the percentage)
    function computePersonalAPRAtCertainTier(address user, uint256 tier)
        external
        view
        returns (uint256)
    {
        (uint256 expectedBoost, uint256 leftToReach) = getPersonalBoostAtTier(user, tier);
        uint256 currentTotalWeightedDeposits = getTotalWeightedDeposits();
        uint256 updateTotalWeightedDeposits = currentTotalWeightedDeposits -
            userToPersonalMultiplier[user] *
            deposited[user];
        updateTotalWeightedDeposits =
            updateTotalWeightedDeposits +
            expectedBoost *
            (deposited[user] + leftToReach);
        uint256 boost = getCommunityBoost() + expectedBoost;
        return
            ((getRevenueDistributed() * (DENOMINATOR + boost) * (10**18)) /
                (updateTotalWeightedDeposits * getCakePrice())) * getEmissionBoost();
    }

    /// @notice To entice the community to convert more, we can compute the APR at a next tier
    /// @return APR in percentage, with 8 decimal precision (divide by 10**8 to get the percentage)
    function computeCommunityAPRAtNextTier() external view returns (uint256) {
        uint256 length = communityMultipliers.length;
        uint256 nextTier;
        for (uint256 i; i < length; i++) {
            if (totalDeposited >= communityThresholds[i]) {
                nextTier = i + 1;
            }
        }
        uint256 currentTotalWeightedDeposits = getTotalWeightedDeposits();
        uint256 updateTotalWeightedDeposits = currentTotalWeightedDeposits +
            DENOMINATOR *
            (communityThresholds[nextTier] - totalDeposited);
        uint256 boost = getCommunityBoost() + communityMultipliers[nextTier];
        return
            ((getRevenueDistributed() * (DENOMINATOR + boost) * (10**18)) /
                (updateTotalWeightedDeposits * getCakePrice())) *
            (getEmissionBoost() + emissionMultipliers[nextTier]);
    }

    /// @return boost in DENOMINATOR precision (10**4) divide by 10**4 to get the percentage boost
    function getBoost(address _user) public view returns (uint256) {
        return getPersonalBoost(_user) + getCommunityBoost();
    }

    /// @notice Compute boost at a certain tier and left to reach for a user.
    /// @return expectedBoost in DENOMINATOR precision (10**4) divide by 10**4 to get the percentage boost
    /// @return leftToReach in tokens
    function getPersonalBoostAtTier(address _user, uint256 tier)
        public
        view
        returns (uint256 expectedBoost, uint256 leftToReach)
    {
        uint256 amount = deposited[_user];
        leftToReach = personalThresholds[tier] > amount ? personalThresholds[tier] - amount : 0;
        uint256 length = personalMultipliers.length;
        for (uint256 i; i < length; i++) {
            if (i <= tier) {
                expectedBoost += personalMultipliers[i];
            }
        }
    }

    /// @notice Compute boost for a specific user.
    /// @return personalBoost in DENOMINATOR precision (10**4) divide by 10**4 to get the percentage boost
    function getPersonalBoost(address _user) public view returns (uint256 personalBoost) {
        uint256 amount = deposited[_user];
        uint256 length = personalMultipliers.length;
        for (uint256 i; i < length; i++) {
            if (amount >= personalThresholds[i]) {
                personalBoost += personalMultipliers[i];
            }
        }
    }

    /// @notice Display current tiers for each users (only for front-end)
    /// @dev Only user for front-end. tier is default at 0.
    /// @return personalTier starting at 1
    /// @return communityTier starting at 1
    function getCurrentTiers(address _user)
        public
        view
        returns (uint256 personalTier, uint256 communityTier)
    {
        uint256 amount = deposited[_user];
        uint256 length = personalMultipliers.length;
        for (uint256 i; i < length; i++) {
            if (amount >= personalThresholds[i]) {
                personalTier = i + 1;
            }
        }
        length = communityMultipliers.length;
        for (uint256 i; i < length; i++) {
            if (totalDeposited >= communityThresholds[i]) {
                communityTier = i + 1;
            }
        }
    }

    /// @notice Compute boost for a all cake rush participants.
    /// @return communityBoost in DENOMINATOR precision (10**4) divide by 10**4 to get the percentage boost
    function getCommunityBoost() public view returns (uint256 communityBoost) {
        uint256 length = communityMultipliers.length;
        for (uint256 i; i < length; i++) {
            if (totalDeposited >= communityThresholds[i]) {
                communityBoost += communityMultipliers[i];
            }
        }
    }

    function balanceOf(address user) external view returns (uint256) {
        return deposited[user];
    }

    // Modifier
    modifier onlyMainstaking() {
        require(mainStaking != address(0));
        require(msg.sender == mainStaking, "Only Mainstaking");
        _;
    }

    // Setters

    function setMainStaking(address _mainStaking) external onlyOwner {
        mainStaking = _mainStaking;
    }

    function setMaximumLockedConversion(uint256 amount) external onlyOwner {
        require(amount <= DENOMINATOR);
        MAXIMUM_PERCENTAGE_LOCKED = amount;
    }

    function setPersonalMultiplierData(uint256[] calldata thresholds, uint256[] calldata multiplier)
        external
        onlyOwner
    {
        uint256 length = thresholds.length;
        require(multiplier.length == length);
        personalThresholds = thresholds;
        personalMultipliers = multiplier;
    }

    function setPriceToken(uint256 price) external onlyOwner {
        priceToken = price;
    }

    function setEmissionPerYear(uint256 emission) external onlyOwner {
        emissionPerYear = emission;
    }

    function setEmissionData(uint256[] calldata thresholds, uint256[] calldata multiplier)
        external
        onlyOwner
    {
        uint256 length = thresholds.length;
        require(multiplier.length == length);
        emissionThresholds = thresholds;
        emissionMultipliers = multiplier;
    }

    function setCommunityMultiplierData(
        uint256[] calldata thresholds,
        uint256[] calldata multiplier
    ) external onlyOwner {
        uint256 length = thresholds.length;
        require(multiplier.length == length);
        communityThresholds = thresholds;
        communityMultipliers = multiplier;
    }

    // Function
    function depositFor(uint256 amount, address _for) external {
        totalPersonalWeightedDeposit =
            totalPersonalWeightedDeposit -
            userToPersonalMultiplier[_for] *
            deposited[_for];
        IERC20(cake).safeTransferFrom(msg.sender, address(this), amount);
        deposited[_for] += amount;
        totalDeposited += amount;
        userToPersonalMultiplier[_for] = getPersonalBoost(_for);
        totalPersonalWeightedDeposit += userToPersonalMultiplier[_for] * deposited[_for];
    }

    function depositLockedTokensFor(uint256 amount, address _for) external onlyMainstaking {
        totalPersonalWeightedDeposit =
            totalPersonalWeightedDeposit -
            userToPersonalMultiplier[_for] *
            deposited[_for];
        deposited[_for] += amount;
        totalDeposited += amount;
        userToPersonalMultiplier[_for] = getPersonalBoost(_for);
        totalPersonalWeightedDeposit += userToPersonalMultiplier[_for] * deposited[_for];
        totalDepositedInLockedTokens += amount;
        require(
            (totalDepositedInLockedTokens * DENOMINATOR) / totalDeposited <=
                MAXIMUM_PERCENTAGE_LOCKED,
            "Too much locked"
        );
    }

    /// @notice increase allowance to a contract to the maximum amount for a specific token if it is needed
    /// @param token the token to increase the allowance of
    /// @param _to the contract to increase the allowance
    /// @param _amount the amount of allowance that the contract needs
    function _approveTokenIfNeeded(
        address token,
        address _to,
        uint256 _amount
    ) private {
        if (IERC20(token).allowance(address(this), _to) < _amount) {
            IERC20(token).approve(_to, type(uint256).max);
        }
    }
}
"
