    "contracts/LPb.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface wbnb {
    function deposit() external payable;

    function balanceOf(address account) external view returns (uint256);

    function withdraw(uint wad) external;
}

contract LPb is Ownable {
    address public lpToken;  // LP代币的地址
    uint256 public threshold = 5 ether; // 分配的BNB数量阈值
    mapping(address => uint256) public lpBalances; // 用户LP代币数量
    mapping(address => bool) public isParticipant; // 是否是参与者
    address[] public participants; // 所有参与者的地址
    address public authorizedContract; // 授权的合约地址
    wbnb public wbnbToken = wbnb(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);



    modifier onlyAuthorizedContract() {
        require(msg.sender == authorizedContract, "Caller is not the authorized contract");
        _;
    }


    function storeLPBalance(address user) external onlyAuthorizedContract {
        if (user == address(0)) {
            return;
        }
        uint256 lpBalance = IERC20(lpToken).balanceOf(user);

        if (lpBalance > 0) {
            if (!isParticipant[user]) {
                participants.push(user);
                isParticipant[user] = true;
            }
            lpBalances[user] = lpBalance;
        } else {
            if (isParticipant[user]) {
                removeParticipant(user);
            }
            lpBalances[user] = 0;
        }
       // wbnbToken.withdraw(wbnbToken.balanceOf(address(this)));
    }


    function removeParticipant(address user) internal {
        require(isParticipant[user], "User is not a participant");

        for (uint256 i = 0; i < participants.length; i++) {
            if (participants[i] == user) {
                participants[i] = participants[participants.length - 1];
                participants.pop();
                isParticipant[user] = false;
                break;
            }
        }
    }

// 当合约收到BNB时触发
    receive() external payable {
        if (address(this).balance >= threshold) {
            distributeRewards();
        }
    }

// 分配BNB
    function distributeRewards() internal virtual {
        require(address(this).balance >= threshold, "Not enough BNB to distribute");

        uint256 totalLP = 0;
        for (uint256 i = 0; i < participants.length; i++) {
            totalLP += lpBalances[participants[i]];
        }

        require(totalLP > 0, "No LP tokens stored");

        for (uint256 i = 0; i < participants.length; i++) {
            address participant = participants[i];
            uint256 reward = address(this).balance * lpBalances[participant] / totalLP;
            payable(participant).transfer(reward);
        }
    }

    function setThreshold(uint256 _threshold) external onlyOwner {
        threshold = _threshold;
    }

    function claimBalance() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }


    function claimToken(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(msg.sender, amount);
    }

    function setAuthorizedContract(address _authorizedContract) external onlyOwner {
        authorizedContract = _authorizedContract;
    }

    function setLPToken(address _lpToken) external onlyOwner {
        lpToken = _lpToken;
    }

    function getParticipants() external view returns (address[] memory) {
        return participants;
    }

    function getParticipantsLength() external view returns (uint256) {
        return participants.length;
    }

    function getLPBalance(address user) external view returns (uint256) {
        return lpBalances[user];
    }


}
