// File: CampaignMirror.sol



pragma solidity ^0.8.0;



contract CampaignMirror  {

    IManager internal _manager;
    bool private _initialized;

    address public factory;
    address public deedNftAddress;
    bool public readyForApproval;
    bool public approved;
    bool public finalized;

    uint public totalRaised;
    uint public totalEntitlement;
    uint public purchaseCount;

    struct ClaimerStat {
        uint totalBought;
        uint totalEntitlement;
        bool mintedNFT;     // user has claimed/minted his NFT ?
    }
    mapping(address => ClaimerStat) public claimers;
    

    constructor(IManager manager) {
        _manager = manager;
        factory = msg.sender;
    }
    
    modifier onlyFactory() {
        require(msg.sender == factory, "Error.NotFactory");
        _;
    }

    modifier onlyConfigurator() {
        require(_manager.getRoles().isConfigurator(msg.sender), "Error.CannotConfigure");
        _;
    }
    
    modifier onlyDeployer() {
        require(_manager.getRoles().isDeployer(msg.sender), "Error.NoRights");
        _;
    }

    modifier onlyApprover() {
        require(_manager.getRoles().isApprover(msg.sender), "Error.NoRights");
        _;
    }

    modifier notFinalized() {
        require(!finalized, "Error.AlreadyFinalized");
        _;
    }

    event ReadyForApprover(address indexed configuratorAddress, bool oldState, bool newState);
    event SetApproval(address indexed approverAddress, bool oldState, bool newState);
    event Finalized(address indexed contractAddress);
    event ClaimNFT(address indexed user, uint id);
    

    //--------------------//
    // EXTERNAl FUNCTIONS //
    //--------------------//
    
    function initialize(address deed) onlyFactory external
    {
        require(!_initialized, "Error.AlreadyInitialized");
        require(deed != address(0), "Error.InvalidAddress");
        _initialized = true;
        deedNftAddress = deed;
    }

    function uploadData(address[] calldata destAddresses, uint[] calldata boughts, uint[] calldata entitlements) 
        external onlyConfigurator notFinalized
    {
        uint len = destAddresses.length;
        require(boughts.length == len, "Error.InvalidRange");
        require(entitlements.length == len, "Error.InvalidRange");

        uint bought;
        uint entitle;

        for (uint n=0; n<len; n++) {
            
            bought = boughts[n];
            entitle = entitlements[n];

            purchaseCount++;

            // Save claimer map
            ClaimerStat storage stat = claimers[destAddresses[n]];
            stat.totalBought += bought; 
            stat.totalEntitlement += entitle;

            // Update total raise, entitlements
            totalRaised += bought; 
            totalEntitlement += entitle;
        }
    }

    function setReadyForApproval(bool state) external onlyConfigurator notFinalized {
        emit ReadyForApprover(msg.sender, readyForApproval, state);
        readyForApproval = state;
    }

    function setApproval(bool state) external onlyApprover notFinalized {
        require(readyForApproval, "Error.NotReady");
        emit SetApproval(msg.sender, approved, state);
        approved = state;
    }
    
    function finalize() external onlyDeployer  notFinalized {
        require(approved, "Error.NotApprovedYet");
        finalized = true;
        emit Finalized(msg.sender);

        ISuperDeedNFT(deedNftAddress).setTotalRaise(totalRaised, totalEntitlement);
    }

    function claimNFT() external {

        require(finalized, "Error.NotFinalizedYet");

        ClaimerStat storage stat = claimers[msg.sender];
        require(stat.totalBought > 0, "Error.DidNotPurchase");
        require(!stat.mintedNFT, "Error.AlreadyMinted");

        // Mint
        stat.mintedNFT = true;
        uint id = ISuperDeedNFT(deedNftAddress).mint(msg.sender, stat.totalBought);
        emit ClaimNFT(msg.sender, id);
    }

    // Getters
    function getPurchaseInfo(address destAddress) public view returns (uint, uint) {
        return (claimers[destAddress].totalBought, claimers[destAddress].totalEntitlement);
    }

    function getGlobalData() external view returns (uint, uint, uint) {
        return (totalRaised, totalEntitlement, purchaseCount);
    }
}



// File: Factory.sol



pragma solidity ^0.8.0;



contract Factory {
    
    uint private constant FACTORY_VERSION = 1;
  
    IManager private _manager;

    constructor(IManager manager) {
        _manager = manager;
    }

    function createCampaignMirror(address bscCampAddress, address distributor, address daoFeeAddress, string calldata symbol) external {
        if (_manager.getRoles().isDeployer(msg.sender)) {

            bytes32 salt = keccak256(abi.encodePacked(bscCampAddress, msg.sender));
            address campaignAddress = address(new CampaignMirror{salt: salt}(_manager));
            

             // Deploy NFT Deed certificate
            string memory deedName = string(abi.encodePacked(symbol, "-Deed")); // Append symbol from XYZ -> XYZ-Deed
            salt = keccak256(abi.encodePacked(deedName, bscCampAddress, msg.sender));
            address nftDeedAddress = address(new SuperDeedNFT{salt:salt}(campaignAddress, distributor, daoFeeAddress, symbol, deedName)); 
       
            CampaignMirror(campaignAddress).initialize(nftDeedAddress);

            _manager.addCampaign(campaignAddress, distributor, nftDeedAddress);
        }
    }

    function version() external pure returns (uint) {
        return FACTORY_VERSION;
    }
}
