library Roles {
    struct Role {
        mapping (address => bool) bearer;
        address[] addressList;
    }

    /**
     * @dev give an account access to this role
     */
    function add(Role storage role, address account) internal {
        require(account != address(0));
        require(!has(role, account));

        role.addressList.push(account);
        role.bearer[account] = true;
    }

    /**
     * @dev remove an account's access to this role
     */
    function remove(Role storage role, address account) internal {
        require(account != address(0));
        require(has(role, account));

        for(uint256 index = 0; index < role.addressList.length; index++) {
            if(role.addressList[index] == account) {
                role.addressList[index] = role.addressList[role.addressList.length - 1];
                role.addressList.pop();
                break;
            }
        }
        role.bearer[account] = false;
    }

    function getAddressList(Role storage role) internal view returns(address[] memory) {
        return role.addressList;
    }

    /**
     * @dev check if an account has this role
     * @return bool
     */
    function has(Role storage role, address account)
    internal
    view
    returns (bool)
    {
        require(account != address(0));
        return role.bearer[account];
    }
}

contract MinterRole {
    using Roles for Roles.Role;

    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);

    Roles.Role private _minters;

    modifier onlyMinter() {
        require(isMinter(msg.sender));
        _;
    }

    function isMinter(address account) public view returns (bool) {
        return _minters.has(account);
    }

    function addMinter(address account) public onlyMinter {
        _addMinter(account);
    }

    function renounceMinter() public {
        _removeMinter(msg.sender);
    }

    function minters() public view returns (address[] memory) {
        return _minters.getAddressList();
    }

    function _addMinter(address account) internal {
        _minters.add(account);
        emit MinterAdded(account);
    }

    function _removeMinter(address account) internal {
        _minters.remove(account);
        emit MinterRemoved(account);
    }
}

contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     */
    function _transferOwnership(address newOwner) internal {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contract ShareToken is Context, IBEP20, MinterRole, Ownable {
    event ClaimShares(address indexed user, uint256 claimId, uint256 claimAmount, uint256 atTime);

    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    uint8 private _decimals;
    string private _symbol;
    string private _name;
    address public signer = 0xaF94Cfc93cf22a5d92c828A659299777540b9505;

    bool public lockSystem;
    address[] public guys;
    mapping(address => uint256) public usersBlocked;
    mapping(address => uint256) public currentSignedTime;
    mapping(address => mapping(uint256 => bool)) public isPaid;

    constructor() {
        _name = "Share Token";
        _symbol = "Share";
        _decimals = 18;
        _addMinter(msg.sender);
    }

    modifier onlySigner() {
        require(signer == _msgSender(), "Signer: caller is not the signer");
        _;
    }

    modifier auth() {
        require(lockSystem == false && usersBlocked[msg.sender] == 0, "not-authorized");
        _;
    }

    function getGuys() public view returns (address[] memory) {
        return guys;
    }

    function lock(address guy) external onlyOwner {
        guys.push(guy);
        usersBlocked[guy] = 1;
    }

    function unlock(uint256 index) external onlyOwner {
        address guy = guys[index];
        usersBlocked[guy] = 0;
        guys[index] = guys[guys.length - 1];
        guys.pop();
    }

    function getOwner() external view returns (address) {
        return owner();
    }

    function decimals() external view returns (uint8) {
        return _decimals;
    }

    function symbol() external view returns (string memory) {
        return _symbol;
    }

    function name() external view returns (string memory) {
        return _name;
    }

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) external auth returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) external view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) external returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external auth returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "BEP20: transfer amount exceeds allowance"));
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");

        _balances[sender] = _balances[sender].sub(amount, "BEP20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 value) internal {
        require(account != address(0));
        require(value <= _balances[account]);

        _totalSupply = _totalSupply.sub(value);
        _balances[account] = _balances[account].sub(value);
        emit Transfer(account, address(0), value);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "BEP20: approve from the zero address");
        require(spender != address(0), "BEP20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function toggleLockSystem() public onlyOwner {
        lockSystem = !lockSystem;
    }

    function mint(address to, uint256 value) public onlyMinter returns (bool) {
        _mint(to, value);
        return true;
    }

    function burn(uint256 value) public returns (bool) {
        _burn(msg.sender, value);
        return true;
    }

    function getMessageHash(uint256 claimId, uint256 timestamp, address user, uint256 shares) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(claimId, timestamp, user, shares));
    }

    function getEthSignedMessageHash(bytes32 _messageHash) public pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash));
    }

    function allowClaimShare(
        uint256 claimId,
        uint256 timestamp,
        address user,
        uint256 shares,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public view returns (bool) {
        return ecrecover(getEthSignedMessageHash(getMessageHash(claimId, timestamp, user, shares)), v, r, s) == signer;
    }

    function claimShares(uint256 claimId, uint256 timestamp, uint256 shares, uint8 v, bytes32 r, bytes32 s) public {
        require(!isPaid[msg.sender][claimId], "Shares: Already paid!");
        require(currentSignedTime[msg.sender] < timestamp, "Shares: Invalid timestamp");
        require(allowClaimShare(claimId, timestamp, msg.sender, shares, v, r, s), "Shares: Invalid signal");
        isPaid[msg.sender][claimId] = true;
        currentSignedTime[msg.sender] = timestamp;
        _mint(msg.sender, shares);
        emit ClaimShares(msg.sender, claimId, shares, timestamp);
    }

    function configSigner(address _signer) public onlySigner {
        signer = _signer;
    }
}
