interface IERC721Enumerable is IERC721 {

    /**
     * @dev Returns the total amount of tokens stored by the contract.
     */
    function totalSupply() external view returns (uint256);

    /**
     * @dev Returns a token ID owned by `owner` at a given `index` of its token list.
     * Use along with {balanceOf} to enumerate all of ``owner``'s tokens.
     */
    function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256 tokenId);

    /**
     * @dev Returns a token ID at a given `index` of all the tokens stored by the contract.
     * Use along with {totalSupply} to enumerate all tokens.
     */
    function tokenByIndex(uint256 index) external view returns (uint256);
}

contract SINVERSE is Ownable {
  using Strings for uint256;
  using SafeMath for uint256;
  bool public paused = false;
  string public baseURI;
  IBEP20 public ERC20_TOKEN = IBEP20(0x6397de0F9aEDc0F7A8Fa8B438DDE883B9c201010);
  address public settlementFeeAddress = 0x845105DC112805fA5C18c0935f895833AdD8900d;
  uint256 public settlementFeePercentage = 0; // 2% decimal number 100
  enum SellType {Invalid, FIXED_PRICE, AUCTION}

  struct _tokenDetails{
      IERC721Enumerable nftContract;
      address creator;
      address owner;
      uint256 royalty;
      uint256 auctionStart;
      uint256 auctionExpire;
      uint256 salePrice;      
      SellType sellType;
      bool isActive;
      bool forSale;
  }
  mapping(IERC721Enumerable => mapping(uint256 => _tokenDetails)) public tokenDetails;

 struct _bidDetail {
     uint256 amount;
     address bidder;
     bool isActive;
 }
 mapping(uint256 => _bidDetail) public bidDetail;
    
 
  function walletOfOwner(IERC721Enumerable nftContract, address _owner) public view returns (uint256[] memory){
    uint256 ownerTokenCount = IERC721Enumerable(nftContract).balanceOf(_owner);
    uint256[] memory tokenIds = new uint256[](ownerTokenCount);
    for (uint256 i; i < ownerTokenCount; i++) {
      tokenIds[i] = IERC721Enumerable(nftContract).tokenOfOwnerByIndex(_owner, i);
    }
    return tokenIds;
  }

  function pause(bool _state) public onlyOwner {
    paused = _state;
  }

    function putonSale(uint256 tokenId,uint256 salePrice, IERC721Enumerable nftContract, SellType sellType, uint256 auctionStart, uint256 auctionExpire, uint256 royalty) public {
        require(msg.sender == IERC721Enumerable(nftContract).ownerOf(tokenId), "Caller is not token owner");
        require(
            sellType == SellType.FIXED_PRICE || sellType == SellType.AUCTION,
            "Wrong sell type"
        ); 

        
        
        tokenDetails[nftContract][tokenId].creator =(tokenDetails[nftContract][tokenId].creator == address(0)) ? msg.sender :tokenDetails[nftContract][tokenId].creator; 
        tokenDetails[nftContract][tokenId].owner = msg.sender; 
        tokenDetails[nftContract][tokenId].royalty = royalty; 

        tokenDetails[nftContract][tokenId].nftContract = nftContract; 
        tokenDetails[nftContract][tokenId].salePrice = salePrice; 
        tokenDetails[nftContract][tokenId].auctionStart = auctionStart;       
        tokenDetails[nftContract][tokenId].auctionExpire = auctionExpire;       
        tokenDetails[nftContract][tokenId].sellType = sellType;  
        tokenDetails[nftContract][tokenId].isActive = true;
        tokenDetails[nftContract][tokenId].forSale = true;
    }

    function buy(IERC721Enumerable nftContract, uint256 tokenId, uint256 token) public {
        require(tokenDetails[nftContract][tokenId].isActive, "Invalid token ID");
        require(tokenDetails[nftContract][tokenId].forSale, "Item not available for sale");        
        require(tokenDetails[nftContract][tokenId].sellType == SellType(1), "Invalid sell type");
        require(token >= tokenDetails[nftContract][tokenId].salePrice, "Insufficient amount to buy");
        require(token <= ERC20_TOKEN.balanceOf(msg.sender), "Insufficient token balance for transfer");
        require(token <= ERC20_TOKEN.allowance(msg.sender,address(this)), "Insufficient allowance for transfer");

        ERC20_TOKEN.transferFrom(msg.sender,address(this),token);
        IERC721Enumerable(nftContract).safeTransferFrom(tokenDetails[nftContract][tokenId].owner,msg.sender,tokenId);
        
        uint256 royaltyAmt;
        uint256 feeAmt;
        if(tokenDetails[nftContract][tokenId].royalty > 0){
            royaltyAmt = tokenDetails[nftContract][tokenId].salePrice.mul(tokenDetails[nftContract][tokenId].royalty).div(10000);
           // ERC20_TOKEN.transfer(tokenDetails[nftContract][tokenId].creator,royaltyAmt);
            ERC20_TOKEN.transfer(settlementFeeAddress,royaltyAmt);
            // payable(tokenDetails[nftContract][tokenId].creator).transfer(royaltyAmt);
        }
        if(settlementFeePercentage > 0){
            feeAmt = tokenDetails[nftContract][tokenId].salePrice.mul(settlementFeePercentage).div(10000);
            ERC20_TOKEN.transfer(settlementFeeAddress,feeAmt);
            // payable(settlementFeeAddress).transfer(feeAmt);
        }
        ERC20_TOKEN.transfer(tokenDetails[nftContract][tokenId].owner,tokenDetails[nftContract][tokenId].salePrice.sub(royaltyAmt).sub(feeAmt));
        // payable(tokenDetails[nftContract][tokenId].owner).transfer(tokenDetails[nftContract][tokenId].salePrice.sub(royaltyAmt).sub(feeAmt));
        tokenDetails[nftContract][tokenId].owner = msg.sender;
    }

    function buyNow(IERC721Enumerable[] memory nftContract, uint256[] memory tokenIds, uint256[] memory tokens) public {
         for (uint256 i; i < tokenIds.length; i++) {
        require(tokenDetails[nftContract[i]][tokenIds[i]].isActive, "Invalid token ID");
        require(tokenDetails[nftContract[i]][tokenIds[i]].forSale, "Item not available for sale");        
        require(tokenDetails[nftContract[i]][tokenIds[i]].sellType == SellType(1), "Invalid sell type");
        require(tokens[i] >= tokenDetails[nftContract[i]][tokenIds[i]].salePrice, "Insufficient amount to buy");
        require(tokens[i] <= ERC20_TOKEN.balanceOf(msg.sender), "Insufficient token balance for transfer");
        require(tokens[i] <= ERC20_TOKEN.allowance(msg.sender,address(this)), "Insufficient allowance for transfer");

        ERC20_TOKEN.transferFrom(msg.sender,address(this),tokens[i]);
        IERC721Enumerable(nftContract[i]).safeTransferFrom(tokenDetails[nftContract[i]][tokenIds[i]].owner,msg.sender,tokenIds[i]);
        
        uint256 royaltyAmt;
        uint256 feeAmt;
        if(tokenDetails[nftContract[i]][tokenIds[i]].royalty > 0){
            royaltyAmt = tokenDetails[nftContract[i]][tokenIds[i]].salePrice.mul(tokenDetails[nftContract[i]][tokenIds[i]].royalty).div(10000);
           // ERC20_TOKEN.transfer(tokenDetails[nftContract[i]][tokenIds[i]].creator,royaltyAmt);
            ERC20_TOKEN.transfer(settlementFeeAddress,royaltyAmt);
            // payable(tokenDetails[nftContract][tokenId].creator).transfer(royaltyAmt);
        }
        if(settlementFeePercentage > 0){
            feeAmt = tokenDetails[nftContract[i]][tokenIds[i]].salePrice.mul(settlementFeePercentage).div(10000);
            ERC20_TOKEN.transfer(settlementFeeAddress,feeAmt);
            // payable(settlementFeeAddress).transfer(feeAmt);
        }
        ERC20_TOKEN.transfer(tokenDetails[nftContract[i]][tokenIds[i]].owner,tokenDetails[nftContract[i]][tokenIds[i]].salePrice.sub(royaltyAmt).sub(feeAmt));
        // payable(tokenDetails[nftContract][tokenId].owner).transfer(tokenDetails[nftContract][tokenId].salePrice.sub(royaltyAmt).sub(feeAmt));
        tokenDetails[nftContract[i]][tokenIds[i]].owner = msg.sender;
        }
    }


  function placeBid(IERC721Enumerable nftContract, uint256 tokenId, uint256 token) external {
        require(tokenDetails[nftContract][tokenId].isActive, "Invalid token ID");
        require(tokenDetails[nftContract][tokenId].forSale, "Item not available for sale");     
        require(tokenDetails[nftContract][tokenId].auctionStart <= block.timestamp, "Auction is not started");
        require(tokenDetails[nftContract][tokenId].auctionExpire >= block.timestamp, "Auction has been closed");

        require(tokenDetails[nftContract][tokenId].sellType == SellType(2), "Invalid sell type");
        require(bidDetail[tokenId].amount < token, "Amount should be more than previous bid");
        require(token <= ERC20_TOKEN.balanceOf(msg.sender), "Insufficient token balance for transfer");
        require(token <= ERC20_TOKEN.allowance(msg.sender,address(this)), "Insufficient allowance for transfer");

        ERC20_TOKEN.transferFrom(msg.sender,address(this),token);

        if(bidDetail[tokenId].isActive){
            ERC20_TOKEN.transfer(bidDetail[tokenId].bidder,bidDetail[tokenId].amount);
            // payable(bidDetail[tokenId].bidder).transfer(bidDetail[tokenId].amount);
        }
        bidDetail[tokenId] = _bidDetail(token,msg.sender,true);
  }


  function acceptBid(IERC721Enumerable nftContract,uint256 tokenId) external {
    require(tokenDetails[nftContract][tokenId].isActive, "Invalid token ID");
    require(bidDetail[tokenId].isActive, "Bid not available for sale");
    require(tokenDetails[nftContract][tokenId].owner == msg.sender || owner() == msg.sender, "Caller is not item owner");
    require(bidDetail[tokenId].amount <= ERC20_TOKEN.balanceOf(address(this)), "Insufficient token balance in pool for transfer"); 


    IERC721Enumerable(tokenDetails[nftContract][tokenId].nftContract).safeTransferFrom(tokenDetails[nftContract][tokenId].owner,bidDetail[tokenId].bidder,tokenId);

    uint256 royaltyAmt;
    uint256 feeAmt;
    if(tokenDetails[nftContract][tokenId].royalty > 0){
        royaltyAmt = bidDetail[tokenId].amount.mul(tokenDetails[nftContract][tokenId].royalty).div(10000);
        ERC20_TOKEN.transfer(tokenDetails[nftContract][tokenId].creator,royaltyAmt);
        // payable(tokenDetails[nftContract][tokenId].creator).transfer(royaltyAmt);
       
    }
    if(settlementFeePercentage > 0){
        feeAmt = bidDetail[tokenId].amount.mul(settlementFeePercentage).div(10000);
        ERC20_TOKEN.transfer(settlementFeeAddress,feeAmt);
        //  payable(settlementFeeAddress).transfer(feeAmt);
    }
    
    ERC20_TOKEN.transfer(tokenDetails[nftContract][tokenId].owner,bidDetail[tokenId].amount.sub(royaltyAmt).sub(feeAmt));
    //  payable(tokenDetails[nftContract][tokenId].owner).transfer(bidDetail[tokenId].amount.sub(royaltyAmt).sub(feeAmt));
    tokenDetails[nftContract][tokenId].owner = bidDetail[tokenId].bidder;
    bidDetail[tokenId].amount = 0;
    tokenDetails[nftContract][tokenId].isActive = false;

  }


  function cancelOrder(IERC721Enumerable nftContract, uint256 tokenId)  external {
      require(msg.sender == IERC721Enumerable(tokenDetails[nftContract][tokenId].nftContract).ownerOf(tokenId), "Caller is not token owner");
        tokenDetails[nftContract][tokenId].forSale = false; 
  }
  
  function sendTokens(uint256 token) public {       
        require(token <= ERC20_TOKEN.balanceOf(msg.sender), "Insufficient token balance for transfer");
        require(token <= ERC20_TOKEN.allowance(msg.sender,address(this)), "Insufficient allowance for transfer");
        ERC20_TOKEN.transferFrom(msg.sender,address(this),token);    
    }

  function setsettlementFeeAddress(address _settlementFeeAddress) public onlyOwner{
      settlementFeeAddress = _settlementFeeAddress;
  }
  function setsettlementFeePercentage(uint256 _settlementFeePercentage) public onlyOwner{
      settlementFeePercentage = _settlementFeePercentage;
  }  
   
  function withdrawToken(uint256 token) public onlyOwner {
       ERC20_TOKEN.transfer(msg.sender,token);
  }
}
