    "contracts/PreSaleContract.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;

import "./interfaces/IMetaVesting.sol";
import "./interfaces/IMetaStorageRegistry.sol";
import "./interfaces/IMetaCourtToken.sol";
import {MerkleProof} from '@openzeppelin/contracts/utils/cryptography/MerkleProof.sol';
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
/**
 * @title PreSaleContract
 * @dev This contract manages the presale of the MetaCourt tokens.
 */
contract PreSaleContract is Initializable {
    ///@notice The root of the Merkle tree used for user verification.
    bytes32  public merkleRoot;
    ///@notice The denominator used for percentage calculations.
    uint256 public constant DENOMINATOR = 100;
    // The registry contract used for administrative functions.
    IMetaStorageRegistry  public registry;
    ///@notice A struct representing a user's sale data.
    struct UserSale {
        uint256 balance; // The balance of the user.
        uint256 claimed; // The amount the user has claimed.
    }
    ///@notice A struct representing a presale vesting schedule.
    struct PreSaleVesting {
        uint256 start; // The start time of the vesting schedule (TGE date).
        uint256 tgePerc; // The percentage of tokens to be released at TGE.
        uint256 cliff; // The duration from the start time until the first claim can be made.
        uint256 vestingPrc; // The percentage of tokens to be released after each vesting period (in basis points, e.g., 5% is represented as 500).
        uint256 duration; // The total duration of the vesting schedule.
        uint256 period; // The duration of each vesting period.
    }

	///@notice whitelisted users mapping
    ///@dev  A mapping from user addresses to their sale data.
    mapping(address => UserSale) public users;

    ///@notice The vesting schedule for the presale.
    PreSaleVesting public userVesting;

    /**
     * @dev Modifier to restrict access to admin-only functions.
     */
    modifier onlyAdmin() {
        require(registry.isAdmin(msg.sender), "PublicSaleContract :: Unauthorized");
        _;
    }

    /**
    * @dev A modifier that validates the user's Merkle proof.
	*/
    modifier verifyProof(bytes32[] calldata _proof,uint256 _amount){
        bytes32 node = keccak256(abi.encodePacked(msg.sender, _amount));
        require(MerkleProof.verify(_proof, merkleRoot, node), 'PublicSaleContract :: Invalid Proof');
        _;
    }
    /**
     * @notice Initializes the contract with the MetaStorageRegistry.
     * @param metaStorageRegistry_ The address of the MetaStorageRegistry contract.
     */
    constructor(address metaStorageRegistry_){
        require(metaStorageRegistry_!= address(0),"PublicSaleContract :: Address(0)");
        registry = IMetaStorageRegistry(metaStorageRegistry_);
    }
    /**
     * @notice Initializes the contract with the root of the Merkle tree.
     * @param _root The root of the Merkle tree.
     */
    function  init(bytes32 _root) external onlyAdmin  initializer{
        merkleRoot = _root;
        address _vesting = registry.vesting();
        (uint256 start,uint256 tgePerc,uint256 cliff,,,uint256 vestingPrc,uint256 duration,uint256 period) = IMetaVesting(_vesting).getAccVesting(address(this));
        require(start > 0 ,"Vesting Not Found");
        userVesting = PreSaleVesting(start,tgePerc,cliff,vestingPrc,duration,period);
    }
    /**
     * @notice Updates the root of the Merkle tree.
     * @param _rootHash The new root of the Merkle tree.
     * @return success A boolean indicating whether the operation was successful.
     */
    function updateRoot(bytes32 _rootHash) external onlyAdmin returns (bool success){
         merkleRoot = _rootHash;
         return true;
    }
    /**
     * @notice Initializes a user's sale data.
     * @param _merkleProof The Merkle proof for the user.
     * @param _amt The amount of tokens the user is allowed to purchase.
     */
    function initUserSale(bytes32[] calldata _merkleProof,uint256 _amt) external verifyProof(_merkleProof,_amt){
        require(users[msg.sender].balance == 0 ,"PublicSaleContract :: User Already Exists");
        users[msg.sender] = UserSale(_amt,0);
    }
    /**
     * @notice Allows a user to claim their tokens.
     */
    function claimTokens() external {
        uint256 _amt = getClaimableTokens(msg.sender);
        require(users[msg.sender].balance > users[msg.sender].claimed,"PublicSaleContract :: You have already claimed all the tokens.");
        require(_amt>0,"PublicSaleContract :: There are no tokens available to claim.");
        IMetaCourtToken _metaToken = IMetaCourtToken(registry.metaCourtToken());
        _metaToken.mint(msg.sender, _amt);
    }
    /**
     * @notice Calculates the amount of tokens a user can claim.
     * @param _user The address of the user.
     * @return amt The amount of tokens the user can claim.
     */
    function getClaimableTokens(address _user) public view returns(uint256 amt){
        uint256 currentTime = block.timestamp;
        if(currentTime < userVesting.start || userVesting.start == 0) return 0;
        else {
            amt += users[_user].balance * userVesting.tgePerc / DENOMINATOR;
        }
        uint256 vestedPerPeriod = ((users[_user].balance) * userVesting.vestingPrc) / DENOMINATOR;
        uint256 elapsedTime = currentTime - userVesting.start;
        if (elapsedTime >= userVesting.cliff) {
            amt += vestedPerPeriod;
			uint256 vestedAmount = ((elapsedTime - userVesting.cliff)) / userVesting.period * vestedPerPeriod;
            amt += vestedAmount;
        }
        if (elapsedTime >=  userVesting.duration + userVesting.cliff) {
            return users[_user].balance - users[_user].claimed;
        }
        amt -= users[_user].claimed;
    }


