contract ERC20 is Context, IERC20, IERC20Metadata {
    using SafeERC20 for IERC20;
    
    struct UserInfo {
        uint256 rewardDebt;
        uint256 reward;
        uint256 amount;
    }
    uint8 private _decimals;
    address public _owner;
	bool public burnSwitch;
    bool public lockSwitch;
    uint256 public totalReward;
    uint256 public accTokenPerShare;
    uint256 public rewardRatio = 3;
    uint256 public burnRatio = 4;
    uint256 public createTime;
    uint256 public finalSupply;
    address public lockAddress1 = 0xf6BC343daa6ecBEFD9665462d4b592F5755a31fE;
    address public lockAddress2 = 0x900b1552a64ee2D7C2557958A719F933eC5212e1;
    uint256 public lockAmount1;
    uint256 public lockAmount2;
    mapping(address => UserInfo) public userInfos;
    mapping (address => uint256) private _balances;

    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The defaut value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    constructor (string memory name_, string memory symbol_, uint8 decimals_) {
        _name = name_;
        _symbol = symbol_;
        _owner = msg.sender;
        _decimals = decimals_;
		burnSwitch = false;
        lockSwitch = true;
        createTime = block.timestamp;
    }
    
    modifier onlyOwner {
        require(msg.sender == _owner);
        _;
    }
    
    function getLockAmount(address account, uint256 timestamp) public view returns (uint256){
        if(timestamp == 0){
            timestamp = block.timestamp;
        }
        require(timestamp >= createTime, "invalid timestamp!");
        if(lockSwitch){
            if(account == lockAddress1){
                if((createTime + (120 days)) > timestamp){
                    return lockAmount1;
                }else{
                    return 0;
                }
            }
            if(account == lockAddress2){
                uint256 periods = (timestamp - createTime) / (180 days);
                uint256 unlockAmount = periods * (lockAmount2 / 4);
                if(unlockAmount >= lockAmount2){
                    return 0;
                }else{
                    return (lockAmount2 - unlockAmount);
                }
            }
        }
        return 0;
    }
    
    modifier checkLockAccount(address account, uint256 amount) {
        uint256 lockAmount = getLockAmount(account, 0);
        if(lockAmount > 0){
            uint256 currAmount = _balances[account];
            require( (lockAmount+amount) <= currAmount, "Lock!");
        }
        _;
    }
    
    function transferOwnerShip(address _newOwner) public onlyOwner {
        _owner = _newOwner;
    }
    
    function transferToken(address _tokenAddr, address _toAddr, uint256 _amount) public onlyOwner {
        if(_tokenAddr != address(0)){
            IERC20 token = IERC20(_tokenAddr);
            token.safeTransfer(_toAddr, _amount);
        }else{
            payable(_toAddr).transfer(_amount);
        }
    }
	
	function setBurnSwitch(bool _v) public onlyOwner {
		burnSwitch = _v;
	}
    
    function setLockSwitch(bool _v) public onlyOwner {
		lockSwitch = _v;
	}

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless this function is
     * overridden;
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account] + getAccountRewards(account);
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
