     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(address sender, address recipient, uint256 amount) checkLockAccount(sender, amount) internal virtual {
        require(sender != address(0), "transfer from the zero address");
        require(recipient != address(0), "transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        withdrawRewards(sender);
        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "transfer amount exceeds balance");
        uint256 rewardAmount = 0;
        uint256 burnAmount = 0;
        if(burnSwitch && _totalSupply > finalSupply){
            rewardAmount = amount * rewardRatio / 100;
            burnAmount = amount * burnRatio / 100;
        }
        withdrawLP(sender,amount);
        _balances[sender] = senderBalance - (amount - burnAmount);
        if(rewardAmount > 0){
            _balances[address(this)] += rewardAmount;
            addRewards(rewardAmount);
        }
        _balances[recipient] += (amount - rewardAmount - burnAmount);
        depositLP(recipient, (amount - rewardAmount - burnAmount));
        if(burnAmount > 0){
            _burn(sender, burnAmount);
        }

