    "src/WPAY.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.18;

import "./Excluder.sol";
import "./library/UniswapLibrary.sol";
import "./ERC20Upgradeable.sol";
import "./interface/IUniswapV2Pair.sol";
import "../lib/openzeppelin-contracts-upgradeable/contracts/token/ERC20/IERC20Upgradeable.sol";
// import "../lib/openzeppelin-contracts-upgradeable/contracts/token/ERC20/ERC20Upgradeable.sol";
import "../lib/openzeppelin-contracts-upgradeable/contracts/proxy/utils/UUPSUpgradeable.sol";


contract WAPY is ERC20Upgradeable,Excluder,UUPSUpgradeable{

    address public poolAddress;
    address public platformAddress;
    address public zeroAddress;
    uint256 public slippage;//0.02=20/1000

    Order[] private orders;

    bool public canSell;
    bool public canBuy;



    struct Order{
        uint256 id;
        address user;
        uint256 wpayAmount;
        uint256 stableAmount;
        uint256 timestamp;
    }

    event PlatformAddress(address platformAddress);
    event ChangeSlippage(uint256 oldSlippage,uint256 newSlippage);


    function initialize(
        string memory name_,
        string memory symbol_,
        uint256 totalSupply_,
        uint256 slippage_,
        address platformAddress_,
        address zeroAddress_,
        address stable_,
        address factory_)external initializer{
        __ERC20_init(name_,symbol_);
        __Ownable_init();

        slippage = slippage_;
        platformAddress = platformAddress_;
        zeroAddress = zeroAddress_;
        poolAddress = UniswapLibrary.pairFor(factory_,stable_,address(this));
        _mint(msg.sender,totalSupply_);
    }


    // required by the OZ UUPS module
    function _authorizeUpgrade(address) internal override onlyOwner {}

    function changeSlippage(uint256 newSlippage_) external onlyOwner{
        uint256 oldSlippage = slippage;
        require(newSlippage_ != oldSlippage,'cb');//can't be the same
        slippage = newSlippage_;
        emit ChangeSlippage(oldSlippage, newSlippage_);
    }

    function changePlatformAddress(address platformAddress_) external onlyOwner{
        platformAddress = platformAddress_;
        emit PlatformAddress(platformAddress);
    }

    function changeIsTrade(bool canSell_,bool canBuy_)external onlyOwner{
        canBuy = canBuy_;
        canSell = canSell_;
    }

    function getOrderLength()external view returns(uint256){
        return orders.length;
    }

    function getOrder(uint256 index) external view returns(Order memory){
        return orders[index];
    }


    function _beforeTokenTransfer(address from, address to, uint256 amount)internal override{
    }

    function _afterTokenTransfer(address from, address to, uint256 amount)internal override{
    }



     function _transfer(address from, address to, uint256 amount) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        address _poolAddress = poolAddress;//save gas
        if(
            from == address(this) ||
            to == address(this) ||
            to == zeroAddress ||
            _poolAddress == address(0) ||
            _excluderContains(from) ||
            _excluderContains(to)
        ){
            _normalTransfer(from,to,amount);
            return;
        }

        if(from == _poolAddress){
            require(canBuy,"can`t buy");
        }


        if(to == _poolAddress){
            require(canSell,"can`t sell");
            _pancakeSell(from,to,amount);
            return;
        }

         _normalTransfer(from,to,amount);
    }

    function _normalTransfer(address from, address to, uint256 amount) internal {
        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[from] = fromBalance - amount;
            // Overflow not possible: the sum of all balances is capped by totalSupply, and the sum is preserved by
            // decrementing then incrementing.
            _balances[to] += amount;
        }
        emit Transfer(from, to, amount);
    }

    function _pancakeSell(address from,address to,uint256 amount) internal{
        _pancakeSellToZero(amount);
        _insertRecord(from,amount);
        _pancakeSellToPlatformAddress(from,amount * slippage / 1000);
        //normal transfer
        _normalTransfer(from,to,amount - (amount * slippage / 1000));
    }

    function _insertRecord(address from,uint256 amount)internal {
        IUniswapV2Pair pair = IUniswapV2Pair(poolAddress);
        bool zeroForOne = pair.token0() == address(this);
        (uint112 tokenReserve,uint112 stableReserve) = _getReserve(pair,zeroForOne);
        uint256 amountOut = _getAmountOut(amount, tokenReserve, stableReserve);
        orders.push(Order({
            id: orders.length,
            user: from,
            wpayAmount: amount,
            stableAmount: amountOut,
            timestamp: block.timestamp
        }));
    }

    function _pancakeSellToPlatformAddress(address from,uint256 amount_)internal {
        IUniswapV2Pair pair = IUniswapV2Pair(poolAddress);
        bool zeroForOne = pair.token0() == address(this);
        _normalTransfer(from, poolAddress, amount_);
        _swapTokenToStable(pair,zeroForOne,amount_,platformAddress);
    }

    function _pancakeSellToZero(uint256 amount_)internal {
        IUniswapV2Pair pair = IUniswapV2Pair(poolAddress);

        address token0 = pair.token0();
        address token1 = pair.token1();
        bool zeroForOne = token0 == address(this);

        uint256 removeLp = _getRemoveLp(pair,zeroForOne,amount_,0);
        uint256 lpBalance = IERC20Upgradeable(poolAddress).balanceOf(address(this));
        if(lpBalance > removeLp){
            (uint256 pbcAmount,uint256 stableAmount) = _burnLP(pair,removeLp,zeroForOne);
            address _zeroAddress = zeroAddress;//save gas
            _swapStableToToken(pair,zeroForOne,stableAmount,_zeroAddress,token0,token1);

            //transfer pbc to zero address
            _balances[address(this)] -= pbcAmount;
            _balances[_zeroAddress] += pbcAmount;
            emit Transfer(address(this),_zeroAddress,pbcAmount);
        }
    }

    function _getRemoveLp(IUniswapV2Pair pair,bool zeroForOne,uint256 pbcAmount,uint256 stableAmount)internal view returns(uint256 removeLp){
        uint256 totalLp = pair.totalSupply();
        (uint112 pbcReserve,uint112 stableReserve) = _getReserve(pair,zeroForOne);
        (uint256 amount,uint112 reserve) = pbcAmount > 0 ? (pbcAmount,pbcReserve): (stableAmount,stableReserve);
        removeLp = totalLp * amount / reserve / 2;
    }

    function _burnLP(IUniswapV2Pair pair,uint256 lpAmount,bool zeroForOne) internal returns(uint256 pbcAmount,uint256 stableAmount){
        pair.transfer(poolAddress,lpAmount);
        (uint256 amount0, uint256 amount1) = pair.burn(address(this));
        pbcAmount = zeroForOne ? amount0 : amount1;
        stableAmount = zeroForOne ? amount1 : amount0;
    }

    function  _swapTokenToStable(IUniswapV2Pair pair,bool zeroForOne,uint256 tokenAmount,address to)internal returns(uint256 amountOut){
         (uint112 tokenReserve,uint112 stableReserve) = _getReserve(pair,zeroForOne);
        amountOut = _getAmountOut(tokenAmount,tokenReserve,stableReserve);

        (uint256 amount0Out, uint256 amount1Out) = zeroForOne ?  (uint256(0),amountOut) : (amountOut,uint256(0));
        pair.swap(amount0Out,amount1Out,to,new bytes(0));
    }

    function  _swapStableToToken(IUniswapV2Pair pair,bool zeroForOne,uint256 stableAmount,
                                                            address to,address token0,address token1)internal returns(uint256 amountOut){
        address stableToken = zeroForOne ? token1 : token0;
         (uint112 pbcReserve,uint112 stableReserve) = _getReserve(pair,zeroForOne);
        amountOut = _getAmountOut(stableAmount,stableReserve,pbcReserve);

        IERC20Upgradeable(stableToken).transfer(poolAddress,stableAmount);
        (uint256 amount0Out, uint256 amount1Out) = zeroForOne ? (amountOut,uint256(0)) : (uint256(0),amountOut);
        pair.swap(amount0Out,amount1Out,to,new bytes(0));
    }

    function _getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) internal pure returns (uint amountOut) {
        require(amountIn > 0, 'PancakeLibrary: INSUFFICIENT_INPUT_AMOUNT');
        require(reserveIn > 0 && reserveOut > 0, 'PancakeLibrary: INSUFFICIENT_LIQUIDITY');
        uint amountInWithFee = amountIn * (9975);
        uint numerator = amountInWithFee * (reserveOut);
        uint denominator = reserveIn *(10000) + (amountInWithFee);
        amountOut = numerator / denominator;
    }

    function _getReserve(IUniswapV2Pair pair,bool zeroForOne) internal view 
                                                    returns(uint112 tokenReserve,uint112 stableReserve){
        (uint112 reserve0,uint112 reserve1,) = pair.getReserves();
        (tokenReserve,stableReserve) = zeroForOne ? (reserve0,reserve1) : (reserve1,reserve0);
    }



}"
    },
    "src/Excluder.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.18;

import {EnumerableSetUpgradeable} from "../lib/openzeppelin-contracts-upgradeable/contracts/utils/structs/EnumerableSetUpgradeable.sol";
import {OwnableUpgradeable} from "../lib/openzeppelin-contracts-upgradeable/contracts/access/OwnableUpgradeable.sol";

contract Excluder is OwnableUpgradeable{


    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    EnumerableSetUpgradeable.AddressSet private excluders;


    function setExcluder(address[] calldata excluders_) external onlyOwner{
        _addExcluders(excluders_);
    }

    function removeExcluder(address[] calldata excluders_)external onlyOwner{
        for(uint256 i;i < excluders_.length;i++){
            excluders.remove(excluders_[i]);
        }
    }

    function getExcluder(uint256 index_) external view returns (address){
        return excluders.at(index_);
    }

    function getAllExcluder() external view returns(address[] memory){
        return excluders.values();
    }

    function excluderContains(address excluder_)external view returns(bool){
        return _excluderContains(excluder_);
    }

    function _excluderContains(address excluder_)internal view returns(bool){
        return excluders.contains(excluder_);
    }

    function _addExcluders(address[] calldata excluders_) internal{
        for(uint256 i;i < excluders_.length;i++){
            excluders.add(excluders_[i]);
        }
    }

}"
    },
    "src/library/UniswapLibrary.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;


library UniswapLibrary {


    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, 'PancakeLibrary: IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'PancakeLibrary: ZERO_ADDRESS');
    }


    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        pair = address(uint160(uint(keccak256(abi.encodePacked(
                hex'ff',
                factory,
                keccak256(abi.encodePacked(token0, token1)),
                // hex'167768d9df8901c4ceab364b7d255be7f1223af594c70ade828ecd354dd43142' // init code hash //local
                hex'd0d4c4cd0848c93cb4fd1f498d7013ee6bfb25783ea21593d5834f5d250ece66' // init code hash //bscTest
                // hex'00fb7f630766e6a796048ea87d01acd3068e8ff67d078148a3fa3f4a84f69bd5' // init code hash //bsc main

            )))));
    }

}"
    },
