interface AggregatorV3Interface {
    function decimals() external view returns (uint8);

    function description() external view returns (string memory);

    function version() external view returns (uint256);

    function getRoundData(
        uint80 _roundId
    )
        external
        view
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        );

    function latestRoundData()
        external
        view
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        );
}

interface IPin {
    function balanceOf(address) external view returns (uint);

    function burn(address, uint256) external;
}

contract STARWALLETS is ERC721Enumerable, Ownable {
    address public feesWallet;
    uint public withdrawFee;
    uint public withdrawFeeBNB;

    using Strings for uint256;
    using SafeMath for uint256;
    bool public paused = false;
    string public baseURI;
    uint planId;
    uint stakingBNBPlanId;

    uint public oneDay = 86400;

    uint public UNSTAKE_WINDOW = 86400; //  24hrs
    uint private MULTIPLE = 500; // Use for Pin calculation

    uint public totalStakers;
    address public admin;

    uint public totalBNBStakers;
    address public pinAddress;

    /**Mainnet */
    AggregatorV3Interface bnbToUsdPrice =
        AggregatorV3Interface(0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE);

    /**Testnet */
    // AggregatorV3Interface bnbToUsdPrice =
    //     AggregatorV3Interface(0x2514895c72f50D8bd4B4F9b1110F0D6bD2c97526);

    constructor(
        address _feesWallet,
        uint _withdrawFee,
        uint _withdrawFeeBNB,
        string memory _name,
        string memory _symbol
    ) ERC721(_name, _symbol) {
        feesWallet = _feesWallet;
        withdrawFee = _withdrawFee; //5% decimal number 10**10 = 100
        withdrawFeeBNB = _withdrawFeeBNB; //2%  decimal number 10**10 = 100
    }

    receive() external payable {}
    
    struct _stakingBNBPlanData {
        uint planId;
        string planName;
        uint duration; // Total duration In days
        uint percentage; // Total return
        uint PerDayReturnPercentage; // 2% decimal number 10**10 = 100
        uint256 amount;
        bool isActive;
    }
    mapping(uint => _stakingBNBPlanData) public stakingBNBPlanData;

    struct BNBStakedData {
        address staker;
        uint numberOfTokens;
        uint tokenValue;
        uint startTime;
        uint duration; // Total duration In days
        bool unstaked;
        uint PerDayReturnPercentage; // x% decimal number 100
        uint256 withdrawAmount;
        uint planId;
    }

    struct _planData {
        uint planId;
        string planName;
        uint duration; // Total duration In days
        uint percentage; // Total return
        uint PerDayReturnPercentage; // 2% decimal number 100
        uint256 amount;
        bool isActive;
    }
    mapping(uint => _planData) public planData;

    struct _stakingData {
        address walletAddress;
        uint stakingDate;
        uint256 amount;
        uint duration; // Total duration In days
        uint percentage; // Total return
        uint PerDayReturnPercentage; // x% decimal number 100
        uint256 withdrawAmount;
        bool isMinted;
        bool isActive;
        uint planId;
    }

    mapping(address => _stakingData[]) public stakingData;
    mapping(address => BNBStakedData[]) public BNBstakesInfo;
    mapping(address => bool) public isBNBStaked;
    event RewardClaim(address indexed from, address indexed to, uint256 price);
    event Unstaked(address indexed user, uint amountRefunded);
    event BNBStaked(
        address indexed staker,
        uint numberOfTokens,
        uint tokenValue,
        uint startTime
    );

    event ClaimedDailyBNBReward(address indexed user, uint numberOfTokens);
    event UnstakeBNB(address indexed user, uint numberOfTokens);
    event RestakedBNB(address indexed user, uint startTime);

    function getBNBprice() public view returns (uint256) {
        (, int256 answer, , , ) = bnbToUsdPrice.latestRoundData();
        return uint256(answer);
    }

    function setPinAddress(address _pin) external onlyOwner {
        pinAddress = _pin;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function BaseURI() public view returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory _uri) public onlyOwner {
        baseURI = _uri;
    }

    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );
        return string(abi.encodePacked(BaseURI(), tokenId.toString()));
    }

    function withdraw(uint256 amount) public onlyOwner {
        payable(msg.sender).transfer(amount);
    }

    function getCountStaking(address wallet) public view returns (uint256) {
        return stakingData[wallet].length;
    }

    function getAllStaking(
        address wallet,
        uint id
    ) public view returns (_stakingData memory) {
        return stakingData[wallet][id];
    }

    function setPlanData(
        string memory _planName,
        uint _duration,
        uint _percentage,
        uint _PerDayReturnPercentage,
        uint256 _amount
    ) public onlyOwner {
        planId = planId + 1;
        planData[planId] = _planData(
            planId,
            _planName,
            _duration,
            _percentage,
            _PerDayReturnPercentage,
            _amount,
            true
        );
    }

    function setStakingBNBPlanData(
        string memory _planName,
        uint _duration,
        uint _percentage,
        uint _PerDayReturnPercentage,
        uint256 _amount
    ) public onlyOwner {
        stakingBNBPlanId = stakingBNBPlanId + 1;
        stakingBNBPlanData[stakingBNBPlanId] = _stakingBNBPlanData(
            stakingBNBPlanId,
            _planName,
            _duration,
            _percentage,
            _PerDayReturnPercentage,
            _amount,
            true
        );
    }

    function setFeesWallet(address _walletAddress) external onlyOwner {
        feesWallet = _walletAddress;
    }

    function setWithdrawFee(uint _withdrawFee) external onlyOwner {
        withdrawFee = _withdrawFee;
    }

    function setWithdrawFeeBNB(uint _withdrawFeeBNB) external onlyOwner {
        withdrawFeeBNB = _withdrawFeeBNB;
    }

    function updatePlanData(
        uint _planId,
        string memory _planName,
        uint _duration,
        uint _percentage,
        uint _PerDayReturnPercentage,
        uint256 _amount
    ) external onlyOwner {
        require(_planId > 0, "Plan id not valid");
        require(planData[_planId].planId > 0, "Plan id invalid");
        planData[_planId] = _planData(
            _planId,
            _planName,
            _duration,
            _percentage,
            _PerDayReturnPercentage,
            _amount,
            true
        );
    }

    function updateStakingBNBPlanData(
        uint _planId,
        string memory _planName,
        uint _duration,
        uint _percentage,
        uint _PerDayReturnPercentage,
        uint256 _amount
    ) external onlyOwner {
        require(_planId > 0, "Plan id not valid");
        require(stakingBNBPlanData[_planId].planId > 0, "Plan id invalid");
        stakingBNBPlanData[_planId] = _stakingBNBPlanData(
            _planId,
            _planName,
            _duration,
            _percentage,
            _PerDayReturnPercentage,
            _amount,
            true
        );
    }

    function staking(uint _planId) external payable {
        require(msg.sender != address(0), "Wallet address not valid");
        require(_planId > 0, "Plan id not valid");
        require(planData[_planId].planId > 0, "Plan id invalid");
        uint256 BNBToUSD = getBNBprice();
        uint256 senderValue = msg.value.mul(BNBToUSD).div(10 ** 18);
        require(
            planData[_planId].amount <= senderValue,
            "Insufficient amount for buy"
        );
        stakingData[msg.sender].push(
            _stakingData(
                msg.sender,
                block.timestamp,
                msg.value,
                planData[_planId].duration,
                planData[_planId].percentage,
                planData[_planId].PerDayReturnPercentage,
                0,
                false,
                true,
                _planId
            )
        );
    }

    function getReward(
        address walletAddress,
        uint _stakingId
    ) public view returns (uint256) {
        require(
            stakingData[walletAddress][_stakingId].isActive,
            "Invalid staking id"
        );
        uint256 amountPerDay = stakingData[walletAddress][_stakingId]
            .amount
            .mul(stakingData[walletAddress][_stakingId].PerDayReturnPercentage)
            .div(10000);
        uint stakingCompletedDays = block
            .timestamp
            .sub(stakingData[walletAddress][_stakingId].stakingDate)
            .div(oneDay);
        if (
            stakingData[walletAddress][_stakingId].duration <
            stakingCompletedDays
        ) {
            stakingCompletedDays = stakingData[walletAddress][_stakingId]
                .duration;
        }
        uint256 getAmount = amountPerDay.mul(stakingCompletedDays);
        return
            getAmount.sub(
                stakingData[walletAddress][_stakingId].withdrawAmount
            );
    }

    function mint(uint _stakingId) external {
        require(
            stakingData[msg.sender][_stakingId].isActive,
            "Invalid staking id"
        );
        require(
            !stakingData[msg.sender][_stakingId].isMinted,
            "Already minted."
        );
        require(
            stakingData[msg.sender][_stakingId].walletAddress == msg.sender,
            "You are not owner of this staking"
        );

        uint stakingCompletedDays = block
            .timestamp
            .sub(stakingData[msg.sender][_stakingId].stakingDate)
            .div(oneDay);

        require(
            stakingData[msg.sender][_stakingId].duration <=
                stakingCompletedDays,
            "Staking period not completed"
        );

        _safeMint(msg.sender, totalSupply() + 1);
        stakingData[msg.sender][_stakingId].isMinted = true;
    }

    function rewardClaim(uint stakingId) public {
        require(
            stakingData[msg.sender][stakingId].isActive,
            "Invalid staking id"
        );
        require(
            stakingData[msg.sender][stakingId].walletAddress == msg.sender,
            "You are not owner of this staking"
        );
        uint256 MyReward = getReward(msg.sender, stakingId);
        require(MyReward > 0, "You don't have reward for claim");
        require(
            MyReward <= address(this).balance,
            "Insufficient fund in staking pool"
        );

        payable(msg.sender).transfer(
            MyReward.sub(MyReward.mul(withdrawFee).div(10000))
        );
        payable(feesWallet).transfer(MyReward.mul(withdrawFee).div(10000));
        stakingData[msg.sender][stakingId].withdrawAmount += MyReward;
        emit RewardClaim(address(this), msg.sender, MyReward);
    }

    /**------------------BNB Staking Part---------------------- */

    function getCountBNBStaking(address wallet) public view returns (uint256) {
        return BNBstakesInfo[wallet].length;
    }

    function getAllBNBStaking(
        address wallet,
        uint id
    ) public view returns (BNBStakedData memory) {
        return BNBstakesInfo[wallet][id];
    }

    function stakeBNB(uint256 _stakingPlanId) external payable {
        require(msg.sender != address(0), "Wallet address not valid");
        require(_stakingPlanId > 0, "Plan id not valid");
        require(
            stakingBNBPlanData[_stakingPlanId].planId > 0,
            "Plan id invalid"
        );
        uint256 BNBToUSD = getBNBprice(); //currentPrice
        uint256 senderValue = (BNBToUSD * msg.value) / 10 ** 8;
        require(
            (senderValue) >=
                stakingBNBPlanData[_stakingPlanId].amount * (10 ** 18),
            "Minimum staking amount must required"
        );

        uint256 pinValue = (senderValue / 10 ** 18) / MULTIPLE + 1;

        require(
            pinValue <= IPin(pinAddress).balanceOf(msg.sender),
            "Insufficient pin to stake"
        );
        require(msg.value > 0, "Staking amount cannot be zero");

        totalBNBStakers++;
        isBNBStaked[msg.sender] = true;
        BNBstakesInfo[msg.sender].push(
            BNBStakedData({
                staker: msg.sender,
                numberOfTokens: msg.value,
                tokenValue: senderValue,
                startTime: block.timestamp,
                duration: stakingBNBPlanData[_stakingPlanId].duration,
                unstaked: false,
                PerDayReturnPercentage: stakingBNBPlanData[_stakingPlanId]
                    .PerDayReturnPercentage,
                withdrawAmount: 0,
                planId: _stakingPlanId
            })
        );

        IPin(pinAddress).burn(msg.sender, pinValue);
        emit BNBStaked(
            msg.sender,
            msg.value, //bnb
            senderValue, //value of bnb in usd
            block.timestamp
        );
    }

    function getBnbReward(
        address walletAddress,
        uint _stakingId
    ) public view returns (uint256) {
        require(
            BNBstakesInfo[walletAddress][_stakingId].tokenValue > 0,
            "Invalid staking id"
        );
        require(
            BNBstakesInfo[walletAddress][_stakingId].unstaked == false,
            "You already unstaked"
        );

        uint256 amountPerDay = BNBstakesInfo[walletAddress][_stakingId]
            .tokenValue
            .mul(
                BNBstakesInfo[walletAddress][_stakingId].PerDayReturnPercentage
            )
            .div(10000);
        uint stakingCompletedDays = block
            .timestamp
            .sub(BNBstakesInfo[walletAddress][_stakingId].startTime)
            .div(oneDay);
        if (
            BNBstakesInfo[walletAddress][_stakingId].duration <
            stakingCompletedDays
        ) {
            stakingCompletedDays = BNBstakesInfo[walletAddress][_stakingId]
                .duration;
        }
        uint256 getAmount = amountPerDay.mul(stakingCompletedDays);
        return
            getAmount.sub(
                BNBstakesInfo[walletAddress][_stakingId].withdrawAmount
            );
    }

    function claimDailyBNBReward(uint stakingId) external {
        require(isBNBStaked[msg.sender] == true, "You are not staker");
        require(
            BNBstakesInfo[msg.sender][stakingId].unstaked == false,
            "You already unstaked"
        );
        uint256 BNBToUSD = getBNBprice();

        uint256 valueOfTokens = getBnbReward(msg.sender, stakingId);
        require(valueOfTokens > 0, "There is no amount to cliam");

        uint256 pinValue = (valueOfTokens / 10 ** 18) / MULTIPLE + 1;

        require(
            pinValue <= IPin(pinAddress).balanceOf(msg.sender),
            "Insufficient pin to stake"
        );

        uint dailyRewardValue = (valueOfTokens * 10 ** 8) / BNBToUSD;
        require(
            dailyRewardValue <= address(this).balance,
            "Insufficient fund in staking pool"
        );

        payable(msg.sender).transfer(
            dailyRewardValue.sub(
                dailyRewardValue.mul(withdrawFeeBNB).div(10000)
            )
        );
        payable(feesWallet).transfer(
            dailyRewardValue.mul(withdrawFeeBNB).div(10000)
        );

        BNBstakesInfo[msg.sender][stakingId].withdrawAmount += valueOfTokens;

        IPin(pinAddress).burn(msg.sender, pinValue);

        emit ClaimedDailyBNBReward(msg.sender, dailyRewardValue);
    }

    function unstakeBNB(uint256 stakingId) external {
        uint256 BNBToUSD = getBNBprice();
        require(isBNBStaked[msg.sender] == true, "You did not stake");
        require(
            BNBstakesInfo[msg.sender][stakingId].startTime +
                BNBstakesInfo[msg.sender][stakingId].duration <
                block.timestamp,
            "Staking period not over"
        );
        require(
            BNBstakesInfo[msg.sender][stakingId].unstaked == false,
            "Already unstaked"
        );
        uint unstakeTimeLength = BNBstakesInfo[msg.sender][stakingId].duration +
            UNSTAKE_WINDOW; // 45Days + 24Hr = 46 Days

        uint remaining = (block.timestamp -
            BNBstakesInfo[msg.sender][stakingId].startTime) % unstakeTimeLength;
        require(
            BNBstakesInfo[msg.sender][stakingId].duration < remaining &&
                remaining < unstakeTimeLength,
            "You are not able to unstake for now"
        );

        uint256 rewards = getBnbReward(msg.sender, stakingId);

        require(rewards == 0, "Please claim all the reward first");
        uint valueOfTokens = (BNBstakesInfo[msg.sender][stakingId].tokenValue);

        uint256 pinValue = (valueOfTokens / 10 ** 18) / MULTIPLE + 1;

        require(
            pinValue <= IPin(pinAddress).balanceOf(msg.sender),
            "Insufficient pin to stake"
        );

        uint refundTokens = (valueOfTokens * (10 ** 8)) / BNBToUSD;
        require(
            refundTokens <= address(this).balance,
            "Insufficient fund in staking pool"
        );

        payable(msg.sender).transfer(refundTokens);

        IPin(pinAddress).burn(msg.sender, pinValue);
        BNBstakesInfo[msg.sender][stakingId].unstaked = true;
        emit UnstakeBNB(msg.sender, refundTokens);
    }

    function stakingTransferArray(
        address[] memory walletAddress,
        uint256[] memory stakingDate,
        uint256[] memory amount,
        uint256[] memory duration,
        uint256[] memory percentage,
        uint256[] memory PerDayReturnPercentage,
        uint256[] memory withdrawAmount,
        bool[] memory isMinted,
        bool[] memory isActive,
        uint256[] memory _planId
    ) external onlyOwner {
        for (uint i = 0; i < walletAddress.length; i++) {
            address new_walletAddress = walletAddress[i];
            uint new_stakingDate = uint(stakingDate[i]);
            uint256 new_amount = amount[i];
            uint new_duration = uint(duration[i]);
            uint new_percentage = uint(percentage[i]);
            uint new_PerDayReturnPercentage = uint(PerDayReturnPercentage[i]);
            uint256 new_withdrawAmount = withdrawAmount[i];
            bool new_isMinted = isMinted[i];
            bool new_isActive = isActive[i];
            uint new_planId = uint(_planId[i]);

            stakingData[new_walletAddress].push(
                _stakingData(
                    new_walletAddress,
                    new_stakingDate,
                    new_amount,
                    new_duration,
                    new_percentage,
                    new_PerDayReturnPercentage,
                    new_withdrawAmount,
                    new_isMinted,
                    new_isActive,
                    new_planId
                )
            );
        }
    }

    function stakingTransfer(
        address walletAddress,
        uint stakingDate,
        uint256 amount,
        uint duration,
        uint percentage,
        uint PerDayReturnPercentage,
        uint256 withdrawAmount,
        bool isMinted,
        bool isActive,
        uint _planId
    ) external onlyOwner {
        stakingData[walletAddress].push(
            _stakingData(
                walletAddress,
                stakingDate,
                amount,
                duration,
                percentage,
                PerDayReturnPercentage,
                withdrawAmount,
                isMinted,
                isActive,
                _planId
            )
        );
    }
}
