    "contracts/gov/ERC721/multipliers/AbstractERC721Multiplier.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@solarity/solidity-lib/utils/BlockGuard.sol";

import "../../../interfaces/gov/IGovPool.sol";
import "../../../interfaces/gov/ERC721/multipliers/IAbstractERC721Multiplier.sol";

import "../../../core/Globals.sol";

abstract contract AbstractERC721Multiplier is
    IAbstractERC721Multiplier,
    ERC721EnumerableUpgradeable,
    ERC721URIStorageUpgradeable,
    OwnableUpgradeable,
    BlockGuard
{
    string public constant LOCK_UNLOCK = "LOCK_UNLOCK";

    mapping(uint256 => NftInfo) internal _tokens;
    mapping(address => uint256) internal _latestLockedTokenIds;

    event Minted(uint256 tokenId, address to, uint256 multiplier, uint256 duration);
    event Locked(uint256 tokenId, address sender, bool isLocked);
    event Changed(uint256 tokenId, uint256 multiplier, uint256 duration);

    function __ERC721Multiplier_init(
        string calldata name,
        string calldata symbol
    ) public initializer {
        __Ownable_init();
        __ERC721Enumerable_init();
        __ERC721_init(name, symbol);
    }

    function lock(uint256 tokenId) external {
        _lockBlock(LOCK_UNLOCK, msg.sender);

        _onlyTokenOwner(tokenId);

        require(
            !isLocked(_latestLockedTokenIds[msg.sender]),
            "ERC721Multiplier: Cannot lock more than one nft"
        );

        _latestLockedTokenIds[msg.sender] = tokenId;

        _afterTokenLock(tokenId);

        emit Locked(tokenId, msg.sender, true);
    }

    function unlock() external {
        _checkBlock(LOCK_UNLOCK, msg.sender);

        uint256 tokenId = _latestLockedTokenIds[msg.sender];

        _onlyTokenOwner(tokenId);

        require(
            IGovPool(owner()).getUserActiveProposalsCount(msg.sender) == 0,
            "ERC721Multiplier: Cannot unlock with active proposals"
        );

        delete _latestLockedTokenIds[msg.sender];

        _afterTokenUnlock(tokenId);

        emit Locked(tokenId, msg.sender, false);
    }

    function setTokenURI(uint256 tokenId, string calldata uri_) external onlyOwner {
        _setTokenURI(tokenId, uri_);
    }

    function tokenURI(
        uint256 tokenId
    )
        public
        view
        override(ERC721URIStorageUpgradeable, ERC721Upgradeable)
        returns (string memory)
    {
        return ERC721URIStorageUpgradeable.tokenURI(tokenId);
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(ERC721URIStorageUpgradeable, ERC721EnumerableUpgradeable, IERC165Upgradeable)
        returns (bool)
    {
        return
            interfaceId == type(IAbstractERC721Multiplier).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function isLocked(uint256 tokenId) public view returns (bool) {
        return tokenId != 0 && _latestLockedTokenIds[ownerOf(tokenId)] == tokenId;
    }

    function _mint(address to, uint256 multiplier, uint64 duration, string memory uri_) internal {
        uint256 currentTokenId = totalSupply() + 1;

        _mint(to, currentTokenId);
        _setTokenURI(currentTokenId, uri_);

        _tokens[currentTokenId] = NftInfo({
            multiplier: multiplier,
            duration: duration,
            mintedAt: uint64(block.timestamp)
        });

        emit Minted(currentTokenId, to, multiplier, duration);
    }

    function _changeToken(uint256 tokenId, uint256 multiplier, uint64 duration) internal {
        _requireMinted(tokenId);

        NftInfo storage token = _tokens[tokenId];

        token.multiplier = multiplier;
        token.duration = duration;

        emit Changed(tokenId, multiplier, duration);
    }

    function _afterTokenLock(uint256 tokenId) internal virtual {}

    function _afterTokenUnlock(uint256 tokenId) internal virtual {}

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override(ERC721EnumerableUpgradeable, ERC721Upgradeable) {
        if (from != address(0)) {
            require(!isLocked(tokenId), "ERC721Multiplier: Cannot transfer locked token");
        }

        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function _burn(
        uint256 tokenId
    ) internal override(ERC721URIStorageUpgradeable, ERC721Upgradeable) {
        ERC721URIStorageUpgradeable._burn(tokenId);
    }

    function _getCurrentMultiplier(
        address whose
    ) internal view returns (uint256 tokenId, uint256 multiplier, uint256 timeLeft) {
        uint256 latestLockedTokenId = _latestLockedTokenIds[whose];

        if (!isLocked(latestLockedTokenId)) {
            return (0, 0, 0);
        }

        NftInfo memory info = _tokens[latestLockedTokenId];

        if (uint256(info.mintedAt) + info.duration < block.timestamp) {
            return (0, 0, 0);
        }

        return (
            latestLockedTokenId,
            info.multiplier,
            info.mintedAt + info.duration - block.timestamp
        );
    }

    function _onlyTokenOwner(uint256 tokenId) internal view {
        require(ownerOf(tokenId) == msg.sender, "ERC721Multiplier: not the nft owner");
    }

    uint256[47] private _gap;
}
"
    },
    "contracts/gov/ERC721/multipliers/DexeERC721Multiplier.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "../../../interfaces/gov/ERC721/multipliers/IDexeERC721Multiplier.sol";

import "../../../libs/math/MathHelper.sol";

import "./AbstractERC721Multiplier.sol";

contract DexeERC721Multiplier is IDexeERC721Multiplier, AbstractERC721Multiplier, UUPSUpgradeable {
    using MathHelper for uint256;
    using Math for uint256;

    uint256 public constant MULTIPLIER_SLASHING_PERCENTAGE = PERCENTAGE_100 / 10; // 10%

    mapping(address => uint256) internal _averageBalances; // user => average balance
    mapping(uint256 => uint256) internal _multiplierSlashing; // token => slashing percentage

    event AverageBalanceChanged(address user, uint256 averageBalance);

    function mint(
        address to,
        uint256 multiplier,
        uint64 duration,
        uint256 averageBalance,
        string calldata uri_
    ) external onlyOwner {
        _mint(to, multiplier, duration, uri_);

        _averageBalances[to] = averageBalance;

        emit AverageBalanceChanged(to, averageBalance);
    }

    function changeToken(
        uint256 tokenId,
        uint256 multiplier,
        uint64 duration,
        uint256 averageBalance
    ) external onlyOwner {
        _changeToken(tokenId, multiplier, duration);

        address owner = ownerOf(tokenId);

        _averageBalances[owner] = averageBalance;

        emit AverageBalanceChanged(owner, averageBalance);
    }

    function getExtraRewards(
        address whose,
        uint256 rewards
    ) external view override returns (uint256) {
        (uint256 multiplier, ) = getCurrentMultiplier(whose, rewards);

        return rewards.ratio(multiplier, PRECISION);
    }

    function getCurrentMultiplier(
        address whose,
        uint256 rewards
    ) public view returns (uint256 multiplier, uint256 timeLeft) {
        uint256 tokenId;
        (tokenId, multiplier, timeLeft) = _getCurrentMultiplier(whose);

        uint256 averageBalance = _averageBalances[whose];

        if (multiplier == 0 || averageBalance == 0) {
            return (multiplier, timeLeft);
        }

        multiplier = multiplier.percentage(PERCENTAGE_100 - _multiplierSlashing[tokenId]);

        uint256 coefficient = rewards.ratio(PRECISION, averageBalance).ratio(
            PRECISION,
            multiplier
        );

        if (coefficient > PRECISION) {
            multiplier = multiplier.ratio(PRECISION, coefficient);
        }

        multiplier = multiplier.max(PRECISION) - PRECISION;
    }

    function _afterTokenUnlock(uint256 tokenId) internal override {
        super._afterTokenUnlock(tokenId);

        _multiplierSlashing[tokenId] = (_multiplierSlashing[tokenId] +
            MULTIPLIER_SLASHING_PERCENTAGE).min(PERCENTAGE_100);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}
"
    },
    "contracts/interfaces/core/ICoreProperties.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * This is the central contract of the protocol which stores the parameters that may be modified by the DAO.
 * These are commissions percentages and pools parameters
 */
interface ICoreProperties {
    /// @notice The struct that stores vital platform's parameters that may be modified by the OWNER
    /// The struct that stores GovPool parameters
    /// @param govVotesLimit the maximum number of simultaneous votes of the voter
    /// @param tokenSaleProposalCommissionPercentage the commission percentage for the token sale proposal
    /// @param micropoolVoteRewardsPercentage the percentage of the rewards for the micropool voters
    /// @param treasuryVoteRewardsPercentage the percentage of the rewards for the treasury voters
    struct CoreParameters {
        uint128 govVotesLimit;
        uint128 govCommissionPercentage;
        uint128 tokenSaleProposalCommissionPercentage;
        uint128 micropoolVoteRewardsPercentage;
        uint128 treasuryVoteRewardsPercentage;
    }

    /// @notice The function to set CoreParameters
    /// @param _coreParameters the parameters
    function setCoreParameters(CoreParameters calldata _coreParameters) external;

    /// @notice The function to modify the platform's commission percentages
    /// @param govCommission the gov percentage commission. Should be multiplied by 10**25
    function setDEXECommissionPercentages(uint128 govCommission) external;

    /// @notice The function to set new token sale proposal commission percentage
    /// @param tokenSaleProposalCommissionPercentage the new commission percentage
    function setTokenSaleProposalCommissionPercentage(
        uint128 tokenSaleProposalCommissionPercentage
    ) external;

    /// @notice The function to set new vote rewards percentages
    /// @param micropoolVoteRewardsPercentage the percentage of the rewards for the micropool voters
    /// @param treasuryVoteRewardsPercentage the percentage of the rewards for the treasury voters
    function setVoteRewardsPercentages(
        uint128 micropoolVoteRewardsPercentage,
        uint128 treasuryVoteRewardsPercentage
    ) external;

    /// @notice The function to set new gov votes limit
    /// @param newVotesLimit new gov votes limit
    function setGovVotesLimit(uint128 newVotesLimit) external;

    /// @notice The function to get commission percentage and receiver
    /// @return govPercentage the overall gov commission percentage
    /// @return treasuryAddress the address of the treasury commission
    function getDEXECommissionPercentages()
        external
        view
        returns (uint128 govPercentage, address treasuryAddress);

    /// @notice The function to get the token sale proposal commission percentage
    /// @return the commission percentage
    function getTokenSaleProposalCommissionPercentage() external view returns (uint128);

    /// @notice The function to get the vote rewards percentages
    /// @return micropoolVoteRewardsPercentage the percentage of the rewards for the micropool voters
    /// @return treasuryVoteRewardsPercentage the percentage of the rewards for the treasury voters
    function getVoteRewardsPercentages() external view returns (uint128, uint128);

    /// @notice The function to get max votes limit of the gov pool
    /// @return votesLimit the votes limit
    function getGovVotesLimit() external view returns (uint128 votesLimit);
}
"
