    "contracts/sGEMS.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface IStake{   
    function takeFeeForSTokenTransaction(uint256 amount) external;
}
contract sGEMS is IERC20, Ownable {
    using SafeMath for uint256;   

    event ExcludeFromReward(address account);
    event IncludeInReward(address account);
    event SetStakingAddress(address stakingContract);
    event DistributeRewards(uint256 amount);
    event SetTransferFee(uint256 transferFee);  
    event ExcludedFromFee(address account, bool isExcludedFromFee);

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) private _isExcluded;
    mapping(address => bool) public isExcludedFromFee;
    address[] private _excluded;
    IStake public stakingContract;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal=100000000*10**9;
    uint256 private _rTotal= (MAX - (MAX % _tTotal));
    uint256 private _tRewardTotal;
    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 public transferFee;
    bool inTakeFee;

    
    constructor(
        string memory __name,
        string memory __symbol,
        address _stakingContract,
        uint256 _transferFee  
    ) {
        _name = __name;
        _symbol = __symbol;
        _decimals = 9;
        _rOwned[_stakingContract] = _rTotal;
        _tOwned[_stakingContract] = _tTotal;
        stakingContract=IStake(_stakingContract);
        _isExcluded[_stakingContract]=true;
        _excluded.push(_stakingContract);
        isExcludedFromFee[_stakingContract] = true;
        transferFee=_transferFee;
        emit Transfer(address(0), _stakingContract, _tTotal);
    }

    function name() external view returns (string memory) {
        return _name;
    }

    function symbol() external view returns (string memory) {
        return _symbol;
    }

    function decimals() external view returns (uint8) {
        return _decimals;
    }

    function totalSupply() external view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount)
        external
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        external
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        external
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        external
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function excludeFromFee(address account) external onlyOwner {
        isExcludedFromFee[account] = true;
        emit ExcludedFromFee(account, true);
    }

    function includeInFee(address account) external onlyOwner {
        isExcludedFromFee[account] = false;
        emit ExcludedFromFee(account, false);
    }
    function isExcludedFromReward(address account)
        external
        view
        returns (bool)
    {
        return _isExcluded[account];
    }

    function totalRewards() external view returns (uint256) {
        return _tRewardTotal;
    }

    function tokenFromReflection(uint256 rAmount)
        public
        view
        returns (uint256)
    {
        require(
            rAmount <= _rTotal,
            "Amount must be less than total reflections"
        );
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner {
        require(!_isExcluded[account], "Account is already excluded");
        require(
            _excluded.length + 1 <= 50,
            "Cannot exclude more than 50 accounts.  Include a previously excluded address."
        );
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
        emit ExcludeFromReward(account);
    }

    function includeInReward(address account) public onlyOwner {
        require(_isExcluded[account], "Account is not excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
        emit IncludeInReward(account);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (
                _rOwned[_excluded[i]] > rSupply ||
                _tOwned[_excluded[i]] > tSupply
            ) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function setStakingAddress(address _stakingContract) external onlyOwner {
        require(
            _stakingContract != address(0),
            "staking address can not be 0 address!"
        );
        stakingContract = IStake(_stakingContract);
        excludeFromReward(_stakingContract);
        isExcludedFromFee[_stakingContract] = true;
        emit SetStakingAddress(_stakingContract);
    }


    function setTransferFee(uint256 _transferFee) external onlyOwner {
        require(
            _transferFee <1000,
            "Transfer fee < 100%!"
        );
        transferFee = _transferFee;
        emit SetTransferFee(transferFee);
    }
    function transferForeignToken(address _token, address _to)
        external
        onlyOwner
        returns (bool _sent)
    {
        require(_token != address(this), "Can't withdraw native tokens");
        uint256 _contractBalance = IERC20(_token).balanceOf(address(this));
        _sent = IERC20(_token).transfer(_to, _contractBalance);
    }

    function distributeRewards(uint256 amount)
        external
        returns (bool _sent)
    {
        require(_isExcluded[_msgSender()], "not allowed");
        require(amount > 0 && amount<balanceOf(_msgSender()), "No balance for reward");
        _tOwned[_msgSender()]=_tOwned[_msgSender()].sub(amount);
        _tRewardTotal=_tRewardTotal.add(amount);
        emit DistributeRewards(amount);
        _sent=true;

    }    

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        uint256 currentRate = _getRate();
        uint256 rAmount = amount.mul(currentRate);
        uint256 _tTransferFeeToken;
        uint256 _rTransferFeeToken;
        if (!inTakeFee && !isExcludedFromFee[from] && !isExcludedFromFee[to]) {
            _tTransferFeeToken=amount.mul(transferFee).div(1000);
            _rTransferFeeToken=_tTransferFeeToken.mul(currentRate);
        }
        
        if (_isExcluded[from] && !_isExcluded[to]) {
            _transferFromExcluded(from, to, amount, rAmount, _tTransferFeeToken, _rTransferFeeToken);
        } else if (!_isExcluded[from] && _isExcluded[to]) {
            _transferToExcluded(from, to, amount, rAmount, _tTransferFeeToken, _rTransferFeeToken);
        } else if (_isExcluded[from] && _isExcluded[to]) {
            _transferBothExcluded(from, to, amount, rAmount, _tTransferFeeToken, _rTransferFeeToken);
        } else {
            _transferStandard(from, to, amount, rAmount, _tTransferFeeToken, _rTransferFeeToken);
        }
        if (
            !inTakeFee &&
            _tTransferFeeToken>0
        ) {
            inTakeFee=true;
            stakingContract.takeFeeForSTokenTransaction(_tTransferFeeToken);
            inTakeFee=false;
        }
        
    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 tAmount, 
        uint256 rAmount,
        uint256 _tTransferFeeToken,
        uint256 _rTransferFeeToken
    ) private {
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rAmount).sub(_rTransferFeeToken);
        emit Transfer(sender, recipient, tAmount.sub(_tTransferFeeToken));
    }

    function _transferToExcluded(
        address sender,
        address recipient,
        uint256 tAmount, 
        uint256 rAmount,
        uint256 _tTransferFeeToken,
        uint256 _rTransferFeeToken
    ) private {
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tAmount).sub(_tTransferFeeToken);
        _rOwned[recipient] = _rOwned[recipient].add(rAmount).sub(_rTransferFeeToken);
        emit Transfer(sender, recipient, tAmount.sub(_tTransferFeeToken));
    }

    function _transferFromExcluded(
        address sender,
        address recipient,
        uint256 tAmount, 
        uint256 rAmount,
        uint256 _tTransferFeeToken,
        uint256 _rTransferFeeToken
    ) private {
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rAmount).sub(_rTransferFeeToken);
        emit Transfer(sender, recipient,  tAmount.sub(_tTransferFeeToken));
    }

    function _transferBothExcluded(
        address sender,
        address recipient,
        uint256 tAmount, 
        uint256 rAmount,
        uint256 _tTransferFeeToken,
        uint256 _rTransferFeeToken
    ) private {
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tAmount).sub(_tTransferFeeToken);
        _rOwned[recipient] = _rOwned[recipient].add(rAmount).sub(_rTransferFeeToken);
        emit Transfer(sender, recipient, tAmount.sub(_tTransferFeeToken));
    }

    
    receive() external payable {}

}
