// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IRouter {
    function addLiquidityETH(address token, uint amountTokenDesired, uint amountTokenMin, uint amountETHMin, address to, uint deadline) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function swapExactTokensForETHSupportingFeeOnTransferTokens(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external;
}

contract MyToken {
    string public name = "Soul Token";
    string public symbol = "SOUK";
    uint8 public decimals = 18;
    uint256 public totalSupply = 1000000 * (10 ** uint256(decimals));

    address public owner;
    address public liquidityRouter;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Mint(address indexed to, uint256 amount);
    event Burn(address indexed from, uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(address _router) {
        owner = msg.sender;
        liquidityRouter = _router;
        balanceOf[msg.sender] = totalSupply;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");
        uint256 tax = _value / 100;
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value - tax;
        balanceOf[address(this)] += tax;
        emit Transfer(msg.sender, _to, _value - tax);
        emit Transfer(msg.sender, address(this), tax);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(allowance[_from][msg.sender] >= _value, "Allowance exceeded");
        require(balanceOf[_from] >= _value, "Insufficient balance");
        uint256 tax = _value / 100;
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value - tax;
        balanceOf[address(this)] += tax;
        allowance[_from][msg.sender] -= _value;
        emit Transfer(_from, _to, _value - tax);
        emit Transfer(_from, address(this), tax);
        return true;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function mint(uint256 amount) external onlyOwner {
        totalSupply += amount;
        balanceOf[owner] += amount;
        emit Mint(owner, amount);
    }

    function burn(uint256 amount) external {
        require(balanceOf[msg.sender] >= amount, "Insufficient balance");
        totalSupply -= amount;
        balanceOf[msg.sender] -= amount;
        emit Burn(msg.sender, amount);
    }

    function addLiquidity(uint256 tokenAmount) external onlyOwner payable {
        IRouter(liquidityRouter).addLiquidityETH{value: msg.value}(address(this), tokenAmount, 0, 0, owner, block.timestamp + 600);
    }

    function swapTokensForBNB(uint256 tokenAmount, uint256 amountOutMin) external onlyOwner {
        require(balanceOf[address(this)] >= tokenAmount, "Insufficient tokens in contract");
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c; // WBNB address on BSC
        IRouter(liquidityRouter).swapExactTokensForETHSupportingFeeOnTransferTokens(tokenAmount, amountOutMin, path, owner, block.timestamp + 600);
    }

    function withdrawBNB(uint256 amount) external onlyOwner {
        require(address(this).balance >= amount, "Insufficient BNB in contract");
        payable(owner).transfer(amount);
    }
}
