// SPDX-License-Identifier: MIT

pragma solidity ^0.8.22;

contract LOOPYToken {
    string public name = "LOOPY";
    string public symbol = "LOOPY";
    uint256 public totalSupply = 10000000000000000000000;
    uint8 public decimals = 9;
    string public LOOPYwebsite = "https://LOOPY.io/";
    string public constant LOOPYtelegram = "https://t.me/LOOPY";
    string public constant LOOPYaudited = "LOOPY is audited by: https://www.certik.com/";
    address private marketingAddress = 0x128aE9175a922b812c4Cfc9e072C56c2317c0B29;
    string public LOOPYtwitter = "https://twitter.com/LOOPY";
            function getLOOPYtwitter() public view returns (string memory) {
        return LOOPYtwitter;
    }
    event Transfer(address indexed _from, address indexed _to, uint256 _value);

    event Approval(
        address indexed _ownerLOOPY,
        address indexed spenderLOOPY,
        uint256 _value
    );

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    address private owner;
    event OwnershipRenounced();

    constructor() {
        balanceOf[msg.sender] = totalSupply;
        owner = msg.sender;
    }


    function transfer(address _to, uint256 _value)
        public
        returns (bool success)
    {
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    function approve(address spenderLOOPY, uint256 _value)
        public
        returns (bool success)
    {
        require(address(0) != spenderLOOPY);
        allowance[msg.sender][spenderLOOPY] = _value;
        emit Approval(msg.sender, spenderLOOPY, _value);
        return true;
    }

    /**
     * @dev Moves `amount` tokens from `sender` to `recipient` using the
     * allowance mechanism. `amount` is then deducted from the caller's
     * allowance.
     *
     * Returns a boolean value indicating whether the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transferFrom(
        address _from,
        address _to,
        uint256 _value
    ) public returns (bool success) {
        require(_value <= balanceOf[_from]);
        require(_value <= allowance[_from][msg.sender]);
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        emit Transfer(_from, _to, _value);
        return true;
    }
            function getLOOPYwebsite() public view returns (string memory) {
        return LOOPYwebsite;
    }
    function renounceOwnership() public {
        require(msg.sender == owner);
        emit OwnershipRenounced();
        owner = address(0);
    }
}
