/**
 * @dev Mystery Boxer NFT contract
 */
contract MysteryBoxerNFT is ERC721 {

    constructor() public ERC721("FISTIANA Mystery Boxer","Fistiana Box"){
        _sellActualAmount = new uint256[](5);
        _sellExpectAmount = new uint256[](5);
    }

    // Expected to sell
    uint256[] private _sellExpectAmount;

    // The actual sale
    uint256[] private _sellActualAmount;

    function updateSellExpectAmount(uint256 mysteryBoxerQuality, uint256 mysteryBoxerAmount) external onlyOwner {
        require(mysteryBoxerQuality < 5, "Mystery Boxer NFT: Mystery Boxer quality must be less than 5");
        require(mysteryBoxerAmount >= _sellActualAmount[mysteryBoxerQuality], "Mystery Boxer NFT: The latest sales total is larger than the previous  actual sales amount");
        _sellExpectAmount[mysteryBoxerQuality] = mysteryBoxerAmount;
    }

    /**
     * @dev Public function to update the base URI for all token.
     */
    function updateBaseURI(string memory baseURI_) public onlyOwner{
        _baseURI = baseURI_;
    }

    function getSaleAmount() external view returns(uint256[] memory, uint256[] memory) {
        return(_sellActualAmount, _sellExpectAmount);
    }

    function buyOfficial(address to, uint256 tokenQuality) external onlyDesigneeContract returns(uint256 tokenId) {
        require(tokenQuality < 5, "Mystery Boxer NFT: boxer quality must be less than 5");
        require(_sellActualAmount[tokenQuality] < _sellExpectAmount[tokenQuality], "Mystery Boxer NFT: Sold out");

        tokenId = _totalSupply;
        _safeMint(to, tokenId, tokenQuality, true, 0, 0, 0, 0, "");

        _sellActualAmount[tokenQuality] = _sellActualAmount[tokenQuality].add(1);
    }

    function safeMint(address to, uint256 tokenQuality, uint256 fatherBoxerType, uint256 motherBoxerType,
        uint256 fatherBoxerQuality, uint256 motherBoxerQuality) external onlyDesigneeContract returns(uint256 tokenId){
        require(tokenQuality < 5, "Mystery Boxer NFT: boxer quality must be less than 5");
        tokenId = _totalSupply;
        _safeMint(to, tokenId, tokenQuality, false, fatherBoxerType, motherBoxerType, fatherBoxerQuality, motherBoxerQuality, "");
    }

    function safeMint(address to, uint256 tokenQuality) external onlyDesigneeContract returns(uint256 tokenId) {
        require(tokenQuality < 5, "Mystery Boxer NFT: boxer quality must be less than 5");
        tokenId = _totalSupply;
        _safeMint(to, tokenId, tokenQuality, true, 0, 0, 0, 0, "");
    }

    function burn(uint256 tokenId) external onlyDesigneeContract{
        _burn(tokenId);
    }

}
