pragma solidity 0.8.9;
// ROCNFTWithdrawV2 contract
contract ROCNFTWithdrawV2 is IROCNFTWithdrawV2, TwoProfitReceiverRescue, Pausable {
    using ECDSA for bytes32;

    event SignerChanged(
        address _signer
    );
    event ExecutorChanged(
        address _executor
    );

    IERC1155 public rocNFT;
    address public nftWalletAddress;
    address public signer;
    address public executor;
    mapping(address => bool) public multiSigSignerMapping;
    address[] public multiSigSigners;
    mapping(uint64 => bool) public isWithdrawUsed;

    constructor(address payable _profitReceiver1, address payable _profitReceiver2, IERC1155 _rocNFT, address _nftWalletAddress, address _signer, address _executor)
            TwoProfitReceiverRescue(_profitReceiver1, _profitReceiver2) {
        rocNFT = _rocNFT;
        nftWalletAddress = _nftWalletAddress;
        setSigner(_signer);
        setExecutor(_executor);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    
    function setSigner(address _signer)
        public
        onlyOwner
    {
        signer = _signer;
        emit SignerChanged(signer);
    }

    function setExecutor(address _executor)
        public
        onlyOwner
    {
        executor = _executor;
        emit ExecutorChanged(executor);
    }
    
    function getMultiSigSignersCount() public view returns (uint256) {
        return multiSigSigners.length;
    }
    
    function getMultiSigSigners() public override view returns (address[] memory) {
        return multiSigSigners;
    }

    function addMultiSigSigners(address[] memory _signers)
        public
        onlyOwner
    {
        for (uint i=0; i<_signers.length; i++) {
            if (!multiSigSignerMapping[_signers[i]]) {
                multiSigSignerMapping[_signers[i]] = true;
                multiSigSigners.push(_signers[i]);
            }
        }
    }

    function removeMultiSigSigners(address[] memory _signers)
        public
        onlyOwner
    {
        for (uint i=0; i<_signers.length; i++) {
            if (multiSigSignerMapping[_signers[i]]) {
                multiSigSignerMapping[_signers[i]] = false;
                for (uint j=0; j<multiSigSigners.length; j++) {
                    if (multiSigSigners[j] == _signers[i]) {
                        if (j < multiSigSigners.length - 1) {
                            multiSigSigners[j] = multiSigSigners[multiSigSigners.length - 1];
                        }
                        multiSigSigners.pop();
                        break;
                    }
                }
            }
        }
    }


    function verifyWithdrawInfoSignature(WithdrawInfo calldata _withdrawInfo, address _signer) internal pure returns (bool) {
        return keccak256(abi.encodePacked(
                _withdrawInfo.withdrawId,
                _withdrawInfo.toWallet,
                _withdrawInfo.expTs,
                keccak256(abi.encodePacked(_withdrawInfo.nftTokenIds)),
                _withdrawInfo.needMultiSigCount
            )).toEthSignedMessageHash().recover(_withdrawInfo.signature) == _signer;
    }

    function indexOfMultiSigSigner(address _signer, uint8 _startInx) internal view returns (int8) {
        for (uint8 i=_startInx; i<multiSigSigners.length; i++) {
            if (multiSigSigners[i] == _signer) {
                return int8(i);
            }
        }
        return -1;
    }

    function verifyMultiSig(WithdrawMultiSig[] calldata _multiSigs, bytes32 _withdrawInfoHash) internal view returns (bool) {
        int8 multiSigIndex = 0;
        for (uint i=0; i<_multiSigs.length; i++) {
            multiSigIndex = indexOfMultiSigSigner(_multiSigs[i].signer, uint8(multiSigIndex));
            if (multiSigIndex == -1) {
                return false;
            }
            if (_multiSigs[i].expTs < block.timestamp) {
                return false;
            }

            bytes32 root = MerkleProof.processProof(_multiSigs[i].proof, _withdrawInfoHash);
            if (keccak256(abi.encodePacked(root, _multiSigs[i].expTs)).toEthSignedMessageHash().recover(_multiSigs[i].signature) != _multiSigs[i].signer) {
                return false;
            }

            multiSigIndex++;
        }
        return true;
    }

    function withdraw(WithdrawInfo calldata _withdrawInfo, WithdrawMultiSig[] calldata _multiSigs) external override whenNotPaused {
        if (msg.sender != executor) {
            require(msg.sender == _withdrawInfo.toWallet, "ROCNFTWithdrawV2: sender is not toWallet");
        }
        require(
            !isWithdrawUsed[_withdrawInfo.withdrawId],
            "ROCNFTWithdrawV2: withdrawId is already used"
        );
        require(
            block.timestamp <= _withdrawInfo.expTs,
            "ROCNFTWithdrawV2: signature expired"
        );
        
        require(
            verifyWithdrawInfoSignature(_withdrawInfo, signer),
            "ROCNFTWithdrawV2: signature is not valid"
        );


        require(
            _multiSigs.length == _withdrawInfo.needMultiSigCount,
            "ROCNFTWithdrawV2: multiSig count is not valid"
        );

        if (_multiSigs.length > 0) {
            require(
                verifyMultiSig(_multiSigs, keccak256(abi.encodePacked(
                    _withdrawInfo.withdrawId,
                    _withdrawInfo.toWallet,
                    keccak256(abi.encodePacked(_withdrawInfo.nftTokenIds))
                ))),
                "ROCNFTWithdrawV2: multiSig verification failed"
            );
        }

        isWithdrawUsed[_withdrawInfo.withdrawId] = true;
        {
            uint8 transferCount = 0;
            uint8 mintCount = 0;
            uint256[] memory balanceArray = new uint256[](_withdrawInfo.nftTokenIds.length);
            for(uint inx=0; inx<balanceArray.length; inx++) {
                balanceArray[inx] = rocNFT.balanceOf(nftWalletAddress, _withdrawInfo.nftTokenIds[inx]);
                if (balanceArray[inx] == 0) {
                    mintCount++;
                } else {
                    transferCount++;
                }
            }
            uint256[] memory transferIds = new uint256[](transferCount);
            uint256[] memory transferValues = new uint256[](transferCount);
            uint256[] memory mintIds = new uint256[](mintCount);
            uint256[] memory mintValues = new uint256[](mintCount);
            {
                uint8 mintIndex = 0;
                uint8 transferIndex = 0;
                for(uint inx=0; inx<balanceArray.length; inx++) {
                    if (balanceArray[inx] == 0) {
                        mintIds[mintIndex] = _withdrawInfo.nftTokenIds[inx];
                        mintValues[mintIndex] = 1;
                        mintIndex++;
                    } else {
                        transferIds[transferIndex] = _withdrawInfo.nftTokenIds[inx];
                        transferValues[transferIndex] = balanceArray[inx];
                        transferIndex++;
                    }
                }
            }
            if (mintCount > 0) {
                IROCNFTMint(address(rocNFT)).mintBatch(_withdrawInfo.toWallet, mintIds, mintValues, "");
            }
            if (transferCount > 0) {
                rocNFT.safeBatchTransferFrom(nftWalletAddress, _withdrawInfo.toWallet, transferIds, transferValues, "");
            }
        }

        emit NFTWithdrawn(
            _withdrawInfo.withdrawId,
            _withdrawInfo.toWallet,
            _withdrawInfo.nftTokenIds,
            _withdrawInfo.needMultiSigCount
        );
    }
}
