    "contracts/BulkTransfer/jetonTransferTool.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.2;

/**
 * @title JetonTrnasferTool
 * @dev
 */
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

import "../libraries/TransferHelper.sol";


contract jetonTransferTool is AccessControlEnumerable, ReentrancyGuard{
    // using Address for address payable;

    enum AssetType { ERC721, ERC1155 }

    address private refundAddress;

    bool    public isFeeActive;


    bytes32 public constant EXECUTE_ROLE = keccak256("EXECUTE_ROLE");

    struct BatchTransferNFT {
        address[] collections; //批量转nft token的地址，多个合集的合约地址
        address[] targets;      //给哪些地址转
        uint256[] tokenIds;     //每个合约对应的哪个tokenId
        uint256[] amounts;      //对应的合约和对应的tokenId 转移多少个nft，721的话写一个，1155的话写对应的数量
        AssetType[] types;      //每个nft类型是什么，721就写0， 1155就写1  比如转了3个nft，前两个是721类型，后边一个是1155，就写[0,0,1]
    }


    constructor(){
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(EXECUTE_ROLE, _msgSender());
    }


    function feeSwitch() external onlyAdmin {
        isFeeActive = !isFeeActive;
    }


    modifier onlyAdmin() {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "The caller is not admin"
        );
        _;
    }

     function grantEexcuteRole(address to) external onlyAdmin {
        _grantRole(EXECUTE_ROLE, to);
    }

    function removeEexcuteRole(address to) external onlyAdmin {
        _revokeRole(EXECUTE_ROLE,to);
    }


    function batchTransferEth(address[] calldata targets,uint[] calldata amounts, uint feeAmount, bytes calldata sig) external payable  nonReentrant{
        require(targets.length == amounts.length, "mismatch");

        uint reserveETHAmount = msg.value;

        if(isFeeActive) {
            verifySignatureForETH(msg.sender,targets,amounts,feeAmount, sig);
            reserveETHAmount -= feeAmount;
        }

        for(uint i; i < targets.length; i++){
            sendValue(payable(targets[i]),amounts[i]);
            reserveETHAmount -= amounts[i];
        }
        sendValue(payable(msg.sender),reserveETHAmount);
    }

    function batchTransferERC20(
        address token,
        address[] calldata targets,
        uint[] calldata amounts,
        uint feeAmount, 
        bytes calldata sig) 
        external 
        payable 
        nonReentrant{

        require(targets.length == amounts.length, "mismatch");

        if(isFeeActive) {
            verifySignatureForETH(msg.sender,targets,amounts,feeAmount, sig);
            require(msg.value >= feeAmount);
        }

        for(uint i; i < targets.length; i++){
            sendERC20(token, targets[i], amounts[i]);
        }
    }

    function batchTransferNFT(
        BatchTransferNFT calldata transferNftData,
        uint feeAmount, 
        bytes calldata sig) external payable nonReentrant{

        BatchTransferNFT memory order = transferNftData;

        if(isFeeActive) {
             verifySignatureForETH(msg.sender,transferNftData.targets,transferNftData.amounts,feeAmount, sig);
            require(msg.value >= feeAmount);
        }



        require(order.collections.length == order.targets.length && 
                order.targets.length == order.tokenIds.length &&
                order.tokenIds.length == order.amounts.length
                , "Length mismatch");

            for (uint i; i < order.collections.length; i++){

                _executeNftTransfer(
                    order.collections[i],
                    msg.sender,
                    order.targets[i],
                    order.tokenIds[i],
                    order.amounts[i],
                    order.types[i]
                );

            }

    }

    function withDraw() external onlyAdmin() {
        payable(msg.sender).transfer(address(this).balance);
    }

    function sendValue(address payable recipient, uint256 amount) internal {
        require(address(this).balance >= amount, "insufficient balance");

        (bool success, ) = recipient.call{gas:25000,value: amount}("");
        require(success, "recipient may have reverted");
    }


    function sendERC20(address token, address recipient, uint256 amount) internal {
        TransferHelper.safeTransferFrom(token, msg.sender, recipient, amount);
    }


    function _executeNftTransfer(
        address collection,
        address from,
        address to,
        uint256 tokenId,
        uint256 amount,
        AssetType assetType
    ) internal {
        if (assetType == AssetType.ERC721) {
            _transferERC721(collection, from, to, tokenId);
        } else if (assetType == AssetType.ERC1155) {
            _transferERC1155(collection, from, to, tokenId, amount);
        }
    }


    function _transferERC721(address collection, address from, address to, uint256 tokenId)
        internal
    {
        IERC721(collection).safeTransferFrom(from, to, tokenId);
    }

    function _transferERC1155(address collection, address from, address to, uint256 tokenId, uint256 amount)
        internal
    {
        IERC1155(collection).safeTransferFrom(from, to, tokenId, amount, "");
    }


    function verifySignatureForETH(
        address from,
        address[] memory _targets,
        uint256[] memory _amounts,
        uint256 _fee,
        bytes memory _signature
    ) internal {

        bytes32 _targetData = keccak256(abi.encodePacked(_targets));
        bytes32 _amountsData = keccak256(abi.encodePacked(_amounts));

        bytes32 _signHash = getEthSignHash(
            from, 
            _targetData,
            _amountsData,
            _fee);
        bool success = validateSignatures(_signHash, _signature);
        require(success, "Signature verify failed");
    }

    function getEthSignHash(
        address from,
        bytes32 _targetData,
        bytes32 _amountsData,
        uint256 _fee
    )  internal view returns (bytes32) {

        return keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encodePacked(
                    bytes1(0x19),
                    bytes1(0),
                    address(this),
                    block.chainid,
                    from,
                    _targetData,
                    _amountsData,
                    _fee))
        ));
    }

    function validateSignatures(bytes32 _signHash, bytes memory _signatures) internal view returns (bool){
        address _signer = ECDSA.recover(_signHash, _signatures);
        require(hasRole(EXECUTE_ROLE, _signer), "Signer is illegal");
        return true;
    }

    function seeBalance() external view returns(uint256) {
        return address(this).balance;
    }


}"
    },
