// File contracts/rewardsManager.sol


pragma solidity ^0.8.6;
//import "hardhat/console.sol";


interface IRewardManager {
    function setAccount(address sender, uint256 wDexAmount, uint256 totalWDex) external returns(bool);
    function setRewardToken(address rewardTokenAddress) external returns (bool);
    function collectRewards(address account, address rewardToken) external returns(uint256);
}

contract RewardsManager is Wrappable, IRewardManager, AccessOwnable {
    bytes32 public constant MANAGE_REWARDS_ROLE = keccak256("MANAGE_REWARDS_ROLE");

    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event SetAccount(address sender, uint256 wDexAmount, uint256 totalWDex);

    event CollectReward(address account, address rewardToken, uint256 amount, bool administrative);
    event LogCollectReward(address account, address rewardToken, uint256 amount);
    event AddAllocation(address rewardToken, address account, uint256 amount);

    mapping(address => mapping (address => uint256)) allocatedRewards;
    mapping(address => mapping (address => uint256)) claimedRewards;
    mapping(address => uint256) totalRewards;
    mapping(address => uint256) totalClaimed;
    IERC20 wDexToken;
    address rewardToken;

    constructor (address wrapper) {
        wDexToken = IERC20(wrapper);
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function grantAdminRoles(address admin) internal override {
        grantRole(MANAGE_REWARDS_ROLE, admin);
    }

    function revokeAdminRoles(address admin) internal override {
        revokeRole(MANAGE_REWARDS_ROLE, admin);
    }

    function claimableRewards(address account) external view returns(uint256) {
        return _claimableRewards(account, rewardToken);
    }

    function claimableRewardsForToken(address account, address _rewardToken) external view returns(uint256) {
        return _claimableRewards(account, _rewardToken);
    }

    function collectRewards(address account, address _rewardToken) external override onlyWrapper returns(uint256) {
        uint256 amount = _claimableRewards(account, _rewardToken);
        claimedRewards[_rewardToken][account] += amount;
        totalClaimed[_rewardToken] += amount;
        emit CollectReward(account, _rewardToken, amount, false);
        return amount;
    }

    function _claimableRewards(address account, address _rewardToken) internal view returns(uint256) {
        return allocatedRewards[_rewardToken][account] - claimedRewards[_rewardToken][account];
    }

    function setAccount(address sender, uint256 wDexAmount, uint256 _totalWDex) external override onlyWrapper returns(bool) {
        emit SetAccount(sender, wDexAmount, _totalWDex);
        return true;
    }

    // not currently supported
    function setRewardToken(address _rewardTokenAddress) external override onlyWrapper returns (bool) {
        rewardToken = _rewardTokenAddress;
        return true;
    }

    // #########################
    // ##                     ##
    // ##      Automation     ##
    // ##                     ##
    // #########################
    function addSpecificAllocations(address _rewardToken, address[] calldata _addresses, uint256[] calldata _values) external onlyRole(MANAGE_REWARDS_ROLE) {
        _addAllocations(_rewardToken, _addresses, _values);
    }
    function addAllocations(address[] calldata _addresses, uint256[] calldata _values) external onlyRole(MANAGE_REWARDS_ROLE) {
        _addAllocations(rewardToken, _addresses, _values);
    }

    function _addAllocations(address _rewardToken, address[] calldata _addresses, uint256[] calldata _values) internal {
        for (uint i=0; i < _addresses.length; i++) {
            allocatedRewards[_rewardToken][_addresses[i]] += _values[i];
            emit AddAllocation(_rewardToken, _addresses[i], _values[i]);
        }
    }

    function administrativeClaim(address[] calldata _addresses, uint256[] calldata _values) external onlyRole(MANAGE_REWARDS_ROLE) {
        _administrativeClaim(rewardToken, _addresses, _values);
    }

    function administrativeClaimSpecific(address _rewardToken, address[] calldata _addresses, uint256[] calldata _values) external onlyRole(MANAGE_REWARDS_ROLE) {
        _administrativeClaim(_rewardToken, _addresses, _values);
    }

    function _administrativeClaim(address _rewardToken, address[] calldata _addresses, uint256[] calldata _values) internal {
        for (uint i=0; i < _addresses.length; i++) {
            claimedRewards[_rewardToken][_addresses[i]] += _values[i];
            emit CollectReward(_addresses[i], _rewardToken, _values[i], true);
        }
    }
}


// File contracts/ratioCalculator.sol


pragma solidity ^0.8.6;
//import "hardhat/console.sol";

interface IRatioCalculator {
    /**
     * @dev Calculates how many a and b tokens correspond to `amount` of wrapper tokens
     *
     * Returns a uint256 for token a, and a second for token b
     */
    function calculateUnwrap(address calculateAddress, uint256 amount) external view returns (uint256, uint256);

    /**
     * @dev Calculates how many a and b tokens it takes to get 1 wrap token;
     *
     * Returns a uint256 for token a, and a second for token b
     */
    function calculateWrap(uint256 amount) external view returns (uint256, uint256);

    /**
     * @dev Calculate the max amount of wrapped dex based on current balance
     *
     * Returns a uint256 indicating the max amount of wDEX the address can create
     */
    function calculateMaxWrappable(address walletAddress) external view returns (uint256);
}

contract RatioCalculator is IRatioCalculator, AccessOwnable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    bytes32 public constant MANAGE_AMOUNTS_ROLE = keccak256("MANAGE_AMOUNTS_ROLE");

    IERC20 a;
    IERC20 b;
    IERC20 wrapper;

    int256 public aAmount;
    int256 public bAmount;

    constructor(address _a, address _b, address _wrapper) {
        a = IERC20(_a);
        b = IERC20(_b);
        wrapper = IERC20(_wrapper);

        // default this
        aAmount = 1;
        bAmount = 1;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function grantAdminRoles(address admin) internal override {
        grantRole(MANAGE_AMOUNTS_ROLE, admin);
    }

    function revokeAdminRoles(address admin) internal override {
        revokeRole(MANAGE_AMOUNTS_ROLE, admin);
    }

    function setAmounts(int256 _a, int256 _b) external onlyRole(MANAGE_AMOUNTS_ROLE) {
        aAmount = _a;
        bAmount = _b;
    }

    function calculateWrap(uint256 amount) external override view returns (uint256, uint256) {
        return (_wrapForToken(aAmount, amount), _wrapForToken(bAmount, amount));
    }

    function calculateUnwrap(address calculateAddress, uint256 amount) external override view returns (uint256, uint256) {
        return _calculateUnwrap(calculateAddress, amount);
    }

    function _calculateUnwrap(address calculateAddress, uint256 amount) internal view returns (uint256, uint256) {
        uint256 totalWrappedBalance = wrapper.totalSupply();

        uint256 balanceA = a.balanceOf(calculateAddress);
        uint256 balanceB = b.balanceOf(calculateAddress);

        uint256 unwrapA = amount.mul(balanceA).div(totalWrappedBalance);
        uint256 unwrapB = amount.mul(balanceB).div(totalWrappedBalance);

        return (unwrapA, unwrapB);
    }

    function calculateMaxWrappable(address walletAddress) external override view returns (uint256) {
        uint256 balanceA = a.balanceOf(walletAddress);
        uint256 balanceB = b.balanceOf(walletAddress);

        // if zero balance of either token, then wrapping is impossible
        if (balanceA == 0 || balanceB == 0) {
            return 0;
        }

        // how much wDEX can we create for each token
        uint256 aMax = _maxWrapForToken(aAmount, balanceA);
        uint256 bMax = _maxWrapForToken(bAmount, balanceB);

        if (aMax < bMax) {
            return aMax;
        } else {
            return bMax;
        }
    }

    function _maxWrapForToken(int256 tokenRatio, uint256 balance) private pure returns (uint256) {
        if (tokenRatio < 0) {
            return balance.mul(uint256(- tokenRatio));
        } else {
            return balance.div(uint256(tokenRatio));
        }
    }

    function _wrapForToken(int256 tokenRatio, uint256 balance) private pure returns (uint256) {
        if (tokenRatio < 0) {
            return balance.div(uint256(- tokenRatio));
        } else {
            return balance.mul(uint256(tokenRatio));
        }
    }
}


// File contracts/wrappedDex.sol


pragma solidity ^0.8.6;
//import "hardhat/console.sol";

interface DEXIRA {
    function setRewardToken(address rewardTokenAddress) external returns (bool);
}

contract WrappedDex is ERC20, Pausable, ReentrancyGuard, AccessOwnable {
    using SafeMath for uint;
    using SafeERC20 for IERC20;

    bytes32 public constant MANAGEMENT_ROLE = keccak256("MANAGEMENT_ROLE");
    bytes32 public constant STAKING_ROLE = keccak256("STAKING_ROLE");

    mapping(address => uint256) public claimBalance;

    event Unwrapped(address indexed sender, uint amount, uint dexIra, uint dexShare);
    event Wrapped(address indexed sender, uint amount, uint dexIra, uint dexShare);
    event CollectRewards(address indexed to, address token, uint amount);
    event DrainedRewards(address to, uint256 amount);
    event DrainedTokenRewards(address to, address token, uint256 amount);
    event CurrentClaimBalance(address account, uint256 amount);

    uint8 decimalsValue;
    IERC20 dexIRA;
    IERC20 dexShare;
    address reward;
    IRatioCalculator ratioManager;
    IRewardManager rewardsManager;

    constructor(
        address _dexIra,
        address _dexShare,
        address _rewardToken,
        uint8 _decimals
    ) ERC20("wDEX-SHARE", "wDEX-SHARE") Ownable() {
        decimalsValue = _decimals;

        dexIRA = IERC20(_dexIra);
        dexShare = IERC20(_dexShare);

        DEXIRA dexProxy = DEXIRA(_dexIra);
        if (_rewardToken != address(0)) {
            dexProxy.setRewardToken(_rewardToken);
        }
        reward = _rewardToken;
    }

    function grantAdminRoles(address admin) internal override {
        grantRole(MANAGEMENT_ROLE, admin);
    }

    function revokeAdminRoles(address admin) internal override {
        revokeRole(MANAGEMENT_ROLE, admin);
    }

    function decimals() public override(ERC20) view returns (uint8) {
        return decimalsValue;
    }

    function getClaimBalance(address _address) external view returns(uint256) {
        return claimBalance[_address];
    }

    // #########################
    // ##                     ##
    // ##    ERC20            ##
    // ##                     ##
    // #########################
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (to == address(0)) {
            // burning token, remove claim balance
            claimBalance[from] -= amount;
            emit CurrentClaimBalance(from, claimBalance[from]);
        } else if (from == address(0)) {
            // mint token, add to claim balance
            claimBalance[to] += amount;
            emit CurrentClaimBalance(to, claimBalance[to]);
        } else if (hasRole(STAKING_ROLE, to) || hasRole(STAKING_ROLE, from)) {
            // do nothing, transfer tokens only, keep the claim balance where it is.
        } else {
            // move the claim balance along with the token.
            claimBalance[from] -= amount;
            claimBalance[to] += amount;
            emit CurrentClaimBalance(from, claimBalance[from]);
            emit CurrentClaimBalance(to, claimBalance[to]);
        }
    }

    // #########################
    // ##                     ##
    // ##    wDEX Public      ##
    // ##                     ##
    // #########################
    function wrap(uint amount) external whenNotPaused nonReentrant {
        _collectRewards(reward,  _msgSender());

        _mint(_msgSender(), amount);

        // call ratio calculator
        uint256 dexIraAmount;
        uint256 dexShareAmount;
        (dexIraAmount, dexShareAmount) = ratioManager.calculateWrap(amount);

        // must be after the mint so the totals are right
        require(rewardsManager.setAccount(_msgSender(), balanceOf(_msgSender()), totalSupply()));

        // message call transfers (must be approved by user before this)
        require(dexIRA.transferFrom(_msgSender(), address(this), dexIraAmount));
        require(dexShare.transferFrom(_msgSender(), address(this), dexShareAmount));
        emit Wrapped(_msgSender(), amount, dexIraAmount, dexShareAmount);
    }

    function unwrap(uint amount) external whenNotPaused nonReentrant {
        uint256 dexIraAmount;
        uint256 dexShareAmount;
        (dexIraAmount, dexShareAmount)  = ratioManager.calculateUnwrap(address(this), amount);
        require(dexIRA.transfer(_msgSender(), dexIraAmount));
        require(dexShare.transfer(_msgSender(), dexShareAmount));

        // we don't require this because it is acceptable to not have anything to collect
        _collectRewards(reward, _msgSender());

        // this must be done after the calculate supply routine
        _burn(_msgSender(), amount);
        // this needs to be after the burn so that we set the balance and total supply correctly.
        require(rewardsManager.setAccount(_msgSender(), balanceOf(_msgSender()), totalSupply()));
        emit Unwrapped(_msgSender(), amount, dexIraAmount, dexShareAmount);
    }

    // Safety method to allow reclaiming tokens if the reward claiming is buggered.
    function unwrapNoCollect(uint amount) external whenNotPaused {
        uint256 dexIraAmount;
        uint256 dexShareAmount;
        (dexIraAmount, dexShareAmount)  = ratioManager.calculateUnwrap(address(this), amount);
        require(dexIRA.transfer(_msgSender(), dexIraAmount));
        require(dexShare.transfer(_msgSender(), dexShareAmount));

        // this must be done after the calculate supply routine
        _burn(_msgSender(), amount);
        emit Unwrapped(_msgSender(), amount, dexIraAmount, dexShareAmount);
    }

    function collectRewards() external whenNotPaused nonReentrant {
        require(_collectRewards(reward, _msgSender()), "Nothing to collect");
    }

    function pushRewards(address[] calldata _addresses) external whenNotPaused nonReentrant onlyRole(MANAGEMENT_ROLE) {
        for (uint i=0; i < _addresses.length; i++) {
            _collectRewards(reward, _addresses[i]);
        }
    }

    function collectSpecificRewards(address _reward) external whenNotPaused nonReentrant {
        require(_collectRewards(_reward, _msgSender()), "Nothing to collect");
    }

    function _collectRewards(address _reward, address _sender) private returns(bool) {
        uint256 amount = rewardsManager.collectRewards(_sender, _reward);
        if (amount > 0) {
            if (_reward != address(0)) {
                IERC20 rewardERC20 = IERC20(_reward);
                require(rewardERC20.balanceOf(address(this)) >= amount, "contract doesn't have balance to cover collection");
                require(rewardERC20.transfer(_sender, amount), "Unable to collect");
            } else {
                require(address(this).balance >= amount, "contract doesn't have balance to cover collection");
                address payable recipient = payable(_sender);
                require(recipient.send(amount), "Unable to collect");
            }
            emit CollectRewards(_sender, _reward, amount);
            return true;
        } else {
            return false;
        }
    }

    /* @dev set the reward token for the entire wrapped tokenset
     */
    function setRewardToken(address _reward) external onlyRole(MANAGEMENT_ROLE) {
        // dexIRA
        DEXIRA dexIraToken = DEXIRA(address(dexIRA));
        require(dexIraToken.setRewardToken(_reward), "Unable to set reward token");
        require(rewardsManager.setRewardToken(_reward), "Unable to set reward token on reward manager");
        reward = _reward;
    }

    function setRewardsManager(address _rewardsManager) external onlyRole(MANAGEMENT_ROLE) {
        rewardsManager = IRewardManager(_rewardsManager);
        rewardsManager.setRewardToken(address(reward));
    }

    function getRewardsManager() external view returns(address) {
        return address(rewardsManager);
    }

    function setRatioManager(address _ratioManager) external onlyRole(MANAGEMENT_ROLE) {
        ratioManager = IRatioCalculator(_ratioManager);
    }

    function getRatioManager() external view returns(address) {
        return address(ratioManager);
    }

    receive() external payable {
    }

    function pause() external onlyOwner {
        _pause();
    }
    function unpause() external onlyOwner {
        _unpause();
    }

    // Withdraw reward tokens. EMERGENCY ONLY.
    function drainReward(address payable to) external onlyOwner {
        uint256 amount = address(this).balance;
        require(to.send(amount), "Unable to collect");
        emit DrainedRewards(to, amount);
    }

    function drainTokenReward(address to, address token) external onlyOwner {
        IERC20 rewardERC20 = IERC20(token);
        uint256 amount = rewardERC20.balanceOf(address(this));
        rewardERC20.transfer(to, amount);

        emit DrainedTokenRewards(to, token, amount);
    }
}
