contract BabyShidoCoin is ERC20Burnable, Ownable {
    using SafeMath for uint256;
    
    uint256 private _reserveSupply  = 250000000 * 10**18;
    uint256 private _presaleSupply  = 550000000 * 10**18;
    uint256 private _stakingReward  = 200000000 * 10**18;
 
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    uint public sellTaxPercent = 10;
    mapping(address => bool) public taxExempt;

    constructor () ERC20("BabyShido", "BSO") {
        address _routerAddress;
        if (block.chainid == 56) {
            _routerAddress = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        } else if (block.chainid == 97) {
            _routerAddress = 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3;
        }
        uniswapV2Router = IUniswapV2Router02(_routerAddress);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());

        taxExempt[address(this)] = true;

        _mint(0x54d962B029562f780795CCD98Ee591dFF63B811A, _reserveSupply);
        _mint(address(this), _presaleSupply);
        _mint(msg.sender, _stakingReward);
    }

    // Tax burn
    function addAndRemoveTaxExempt(address _address, bool _isAdding) public onlyOwner{
        taxExempt[_address] = _isAdding;
    }

    function setSellTaxPercent(uint _sellTaxPercent) public onlyOwner {
        sellTaxPercent = _sellTaxPercent;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override {

        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        // Tax
        uint256 finalAmount = amount;
        // if any account belongs to taxExempt then remove the fee
        if(taxExempt[sender] || taxExempt[recipient]){
            // No tax
        }else{
            if(recipient == uniswapV2Pair){ // Sell Token
                uint256 sellTax = sellTaxPercent * amount / 100;
                if(sellTax > 0)
                    super._transfer(sender, owner(), sellTax);
                
                finalAmount = finalAmount - sellTax;
            }
        }   
        super._transfer(sender, recipient, finalAmount);
    }

   receive() external payable {
       if(msg.value > 0)
            buyToken();
    }

    /* PRESALE */
    bool public crowdSaleEnabled = false;
    uint256 public PRESALE_TOKEN_SOLD = 0;
    uint256 public RATE = 500000; // Token price: 1BNB=  500000 BSO
    uint256 public MAX_PURCHASE = 100 * 10**18; //Maximum BNB per wallet: 100 BNB
    mapping(address => uint256) public totalPurchased;

    event CrowdSaleStart(uint256 PRESALE_TOKEN, uint256 rate);
    event CrowdSalePause(uint256 PRESALE_TOKEN, uint256 rate);
    event CrowdSaleEnd(uint256 PRESALE_TOKEN, uint256 rate);
    event TokenPurchase(address buyer, uint256 amount);

    function startCrowdsale() public onlyOwner {
        crowdSaleEnabled = true;
        emit CrowdSaleStart(_presaleSupply, RATE);
    }

    function pauseCrowdsale() public onlyOwner {
        crowdSaleEnabled = false;
        emit CrowdSalePause(_presaleSupply, RATE);
    }

    function endCrowdsale() public onlyOwner {
        crowdSaleEnabled = false;
        _transfer(address(this), owner(), balanceOf(address(this)));
    
        emit CrowdSaleEnd(_presaleSupply, RATE);
    }

    function buyToken() public payable {
        
        require(crowdSaleEnabled, "Not started");
        require(msg.value >= 10**16, "Minimum is 0.01 BNB to buy presale"); //Minimum BNB per wallet: 0.01 BNB
        require(totalPurchased[msg.sender].add(msg.value) <= MAX_PURCHASE, "limited 100 BNB each wallet");

        uint256 amountToken = RATE.mul(msg.value);
        require(amountToken <= balanceOf(address(this)), "Buy amount over remaining of the sale");

        _transfer(address(this), address(msg.sender), amountToken);
        PRESALE_TOKEN_SOLD = PRESALE_TOKEN_SOLD.add(amountToken);
        totalPurchased[msg.sender] = totalPurchased[msg.sender].add(msg.value);
  
        payable(0x54d962B029562f780795CCD98Ee591dFF63B811A).transfer(msg.value.mul(40).div(100));
        payable(0x2A63921893F93030EDDb3af5F7c0DcbA145216ac).transfer(msg.value.mul(40).div(100));
        payable(0x99B390977Ca02fCDCbbA79E9074557B7D3138e1c).transfer(msg.value.mul(20).div(100));

        emit TokenPurchase(msg.sender, amountToken);
    }
}
