      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Dividend {
    using SafeMath for uint256;

    IERC20 public immutable mac;

    address public immutable owner;

    uint256 public top25DividendAmount;

    uint256 public top50DividendAmount;

    uint256 public bottom50DividendAmount;

    constructor(IERC20 mac_, address owner_) {
        mac = mac_;
        owner = owner_;
    }

    event Handle(
        address[] top25Addresses,
        address[] top50Addresses,
        address[] bottom50Addresses,
        uint256 top25DividendAmount,
        uint256 top50DividendAmount,
        uint256 bottom50DividendAmount
    );

    function incTop25DividendAmount(
        uint256 amount
    ) external onlyMac returns (bool) {
        top25DividendAmount += amount;
        return true;
    }

    function incTop50DividendAmount(uint256 amount) external onlyMac {
        top50DividendAmount += amount;
    }

    function incBottom50DividendAmount(uint256 amount) external onlyMac {
        bottom50DividendAmount += amount;
    }

    function handle(
        address[] calldata _top25Addresses,
        address[] calldata _top50Addresses,
        address[] calldata _bottom50Addresses
    ) external onlyOwner {
        require(
            _top25Addresses.length > 0 &&
                _top25Addresses.length <= 25 &&
                _top50Addresses.length >= _top25Addresses.length &&
                _bottom50Addresses.length == _top50Addresses.length
        );
        if (top25DividendAmount > 0)
            _dividend(_top25Addresses, top25DividendAmount);
        if (top50DividendAmount > 0)
            _dividend(_top50Addresses, top50DividendAmount);
        if (bottom50DividendAmount > 0)
            _dividend(_bottom50Addresses, bottom50DividendAmount);
        emit Handle(
            _top25Addresses,
            _top50Addresses,
            _bottom50Addresses,
            top25DividendAmount,
            top50DividendAmount,
            bottom50DividendAmount
        );
        bottom50DividendAmount = top25DividendAmount = top50DividendAmount = 0;
    }

    function _dividend(
        address[] calldata addresses,
        uint256 dividendAmount
    ) private {
        uint256 addressesCount = addresses.length;
        uint256 avgDividendAmount = dividendAmount.div(addressesCount);
        for (uint256 i = 0; i < addressesCount; i++) {
            mac.transfer(addresses[i], avgDividendAmount);
        }
    }

    function withdraw(address _token, address payable _to) external onlyOwner {
        if (_token == address(0x0)) {
            payable(_to).transfer(address(this).balance);
        } else {
            IERC20(_token).transfer(
                _to,
                IERC20(_token).balanceOf(address(this))
            );
        }
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    modifier onlyMac() {
        require(msg.sender == address(mac));
        _;
    }
}

contract MAC is ERC20, Ownable {
    using SafeMath for uint256;

    address constant DEAD = 0x000000000000000000000000000000000000dEaD;

    address public immutable pair;
    Dividend public dividend;

    uint256 public immutable buyJointCommunityFeeRate = 1;
    uint256 public immutable buyBottom50DividendFeeRate = 1;
    uint256 public immutable buyTop50DividendFeeRate = 2;
    uint256 public immutable buyBurnFeeRate = 2;

    uint256 public immutable sellMarketingFeeRate = 2;
    uint256 public immutable sellTop25DividendFeeRate = 2;
    uint256 public immutable sellBurnFeeRate = 1;
    uint256 public immutable sellSermonFeeRate = 1;

    uint256 public immutable extraFeeRate = 20;

    address public sermonWallet;
    address public marketingWallet;
    address public jointCommunityWallet;
    address public extraWallet;

    mapping(address => bool) private _isExcludedFromFees;

    uint256 public immutable tradeStartTime;

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 totalSupply_,
        IERC20 usdt_,
        IUniswapV2Router02 pancakeSwapRouter_,
        uint256 tradeStartTime_,
        address sermonWallet_,
        address marketingWallet_,
        address jointCommunityWallet_,
        address extraWallet_
    ) payable ERC20(name_, symbol_) {
        extraWallet = extraWallet_;
        sermonWallet = sermonWallet_;
        marketingWallet = marketingWallet_;
        jointCommunityWallet = jointCommunityWallet_;
        tradeStartTime = tradeStartTime_;
        pair = IUniswapV2Factory(pancakeSwapRouter_.factory()).createPair(
            address(usdt_),
            address(this)
        );
        dividend = new Dividend(IERC20(address(this)), owner());
        _isExcludedFromFees[owner()] = _isExcludedFromFees[
            address(dividend)
        ] = _isExcludedFromFees[jointCommunityWallet] = _isExcludedFromFees[
            marketingWallet
        ] = _isExcludedFromFees[sermonWallet] = _isExcludedFromFees[
            extraWallet
        ] = true;
        _mint(msg.sender, totalSupply_ * 10 ** decimals());
    }

    function isExcludedFromFees(address account) external view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
        require(_isStartTrade(from, to), "Not start trade");
        if (_shouldTakeFee(from, to)) amount = _takeFee(from, amount);
        super._transfer(from, to, amount);
    }

    function _isStartTrade(
        address _from,
        address _to
    ) private view returns (bool) {
        return
            tradeStartTime <= block.timestamp ||
            (_from != pair && _to != pair) ||
            (_isExcludedFromFees[_to] || _isExcludedFromFees[_from]);
    }

    function _shouldTakeFee(
        address _from,
        address _to
    ) private view returns (bool) {
        return !(_isExcludedFromFees[_from] || _isExcludedFromFees[_to]);
    }

    function _shouldTakeExtraFee() private view returns (bool) {
        uint256 time = block.timestamp;
        return tradeStartTime <= time && tradeStartTime.add(10 minutes) >= time;
    }

    function _takeFee(
        address _from,
        uint256 _amount
    ) private returns (uint256 result) {
        result = _amount;
        if (_shouldTakeExtraFee()) {
            uint256 EFee = _amount.mul(extraFeeRate).div(100);
            result = result.sub(EFee);
            if (EFee > 0) super._transfer(_from, extraWallet, EFee);
        }
        if (_from == pair) {
            //buy
            //Burn
            uint256 BFee = _amount.mul(buyBurnFeeRate).div(100);
            result = result.sub(BFee);
            if (BFee > 0) super._transfer(_from, DEAD, BFee);

            //JointCommunity
            uint256 JFee = _amount.mul(buyJointCommunityFeeRate).div(100);
            result = result.sub(JFee);
            if (JFee > 0) super._transfer(_from, jointCommunityWallet, JFee);

            //Dividend
            uint256 T50Fee = _amount.mul(buyTop50DividendFeeRate).div(100);
            uint256 B50Fee = _amount.mul(buyBottom50DividendFeeRate).div(100);
            uint256 totalDividendFee = B50Fee.add(T50Fee);
            result = result.sub(totalDividendFee);
            if (totalDividendFee > 0)
                super._transfer(_from, address(dividend), totalDividendFee);
            if (T50Fee > 0) dividend.incTop50DividendAmount(T50Fee);
            if (B50Fee > 0) dividend.incBottom50DividendAmount(B50Fee);
        } else {
            //sell or transfer

            //Burn
            uint256 BFee = _amount.mul(sellBurnFeeRate).div(100);
            result = result.sub(BFee);
            if (BFee > 0) super._transfer(_from, DEAD, BFee);

            //Marketing
            uint256 MFee = _amount.mul(sellMarketingFeeRate).div(100);
            result = result.sub(MFee);
            if (MFee > 0) super._transfer(_from, marketingWallet, MFee);

            //Sermon
            uint256 SFee = _amount.mul(sellSermonFeeRate).div(100);
            result = result.sub(SFee);
            if (SFee > 0) super._transfer(_from, sermonWallet, SFee);

            //Top25Dividend
            uint256 T25Fee = _amount.mul(sellTop25DividendFeeRate).div(100);
            result = result.sub(T25Fee);
            if (T25Fee > 0) {
                super._transfer(_from, address(dividend), T25Fee);
                dividend.incTop25DividendAmount(T25Fee);
            }
        }
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        if (_isExcludedFromFees[account] != excluded) {
            _isExcludedFromFees[account] = excluded;
            emit ExcludeFromFees(account, excluded);
        }
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    // function setTradeStartTime(uint256 _v) external onlyOwner {
    //     tradeStartTime = _v;
    // }

    function setDividend(Dividend _dividend) external onlyOwner {
        dividend = _dividend;
    }

    function setExtraWallet(address _wallet) external onlyOwner {
        extraWallet = _wallet;
    }

    function setJointCommunityWallet(address _wallet) external onlyOwner {
        jointCommunityWallet = _wallet;
    }

    function setMarketingWallet(address _wallet) external onlyOwner {
        marketingWallet = _wallet;
    }

    function setSermonWallet(address _wallet) external onlyOwner {
        sermonWallet = _wallet;
    }

    function withdraw(address _token) external {
        if (_token == address(0x0)) {
            payable(marketingWallet).transfer(address(this).balance);
        } else {
            IERC20(_token).transfer(
                marketingWallet,
                IERC20(_token).balanceOf(address(this))
            );
        }
    }

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
}
