      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Staking {

    struct Reward {
        uint256 id;
        string name;
        uint256 percentage;
        uint256 timePeriod;
        bool allowed;
    }

    struct StakingInfo {
        uint256 id;
        uint256 amount;
        uint256 stakingDate;
    }

    Reward[] public rewards;
    uint256 rewardsCounter;

    // Library usage
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // Contract owner
    address public owner;

    bool public active;

    // Token amount variables
    mapping(address => mapping(uint256 => StakingInfo[])) userStakedToken;

    uint256 public contractBalance;

    // ERC20 contract address
    IERC20 public erc20Contract;

    // Events
    event tokensStaked(address from, uint256 amount);
    event TokensUnstaked(address to, uint256 amount);

    /// @dev Deploys contract and links the ERC20 token which we are staking, also sets owner as msg.sender and sets timestampSet bool to false.
    /// @param _erc20_contract_address.
    constructor(IERC20 _erc20_contract_address) {
        // Set contract owner
        owner = msg.sender;

        // Set the erc20 contract address which this timelock is deliberately paired to
        require(address(_erc20_contract_address) != address(0), "_erc20_contract_address address can not be zero");
        erc20Contract = _erc20_contract_address;

        active = true;

        // Fix percentage
        // rewards.push(Reward(0,'bronze',8,7776000,true));
        // rewards.push(Reward(1,'silver',10,15552000,true));
        // rewards.push(Reward(2,'gold',16,31104000,true));

        rewards.push(Reward(0,'bronze',2,7776000,true));
        rewards.push(Reward(1,'silver',6,15552000,true));
        rewards.push(Reward(2,'gold',16,31104000,true));
        rewardsCounter = 2;
    }

    // Modifier
    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Message sender must be the contract's owner.");
        _;
    }

    modifier isActive() {
        require(active, "the staking contract is not active");
        _;
    }

    /// @dev Allows the contract owner to allocate official ERC20 tokens to each future recipient (only one at a time).
    /// @param token, the official ERC20 token which this contract exclusively accepts.
    /// @param amount to allocate to recipient.
    function stakeTokens(IERC20 token, uint256 amount, uint256 rewardId) public {
        require(token == erc20Contract, "You are only allowed to stake the official erc20 token address which was passed into this contract's constructor");
        require(amount <= token.balanceOf(msg.sender), "Not enough STATE tokens in your wallet, please try lesser amount");

        StakingInfo memory temp = StakingInfo(userStakedToken[msg.sender][rewardId].length,amount,(block.timestamp + rewards[rewardId].timePeriod));

        userStakedToken[msg.sender][rewardId].push(temp);
        token.safeTransferFrom(msg.sender, address(this), amount);
        contractBalance = erc20Contract.balanceOf(address(this));  
        emit tokensStaked(msg.sender, amount);
    }

    /// @dev Allows user to unstake tokens after the correct time period has elapsed
    /// @param token - address of the official ERC20 token which is being unlocked here.
    /// @param amount - the amount to unlock (in wei)
    function unstakeTokens(IERC20 token, uint256 amount, uint256 rewardId, uint256 stakeId) public isActive {
        require(userStakedToken[msg.sender][rewardId][stakeId].amount >= amount, "Insufficient token balance, try lesser amount");
        require(token == erc20Contract, "Token parameter must be the same as the erc20 contract address which was passed into the constructor");
        require(rewards[rewardId].allowed, "This rewards has been suspended, it is only possible to withdraw");
        if (block.timestamp >= userStakedToken[msg.sender][rewardId][stakeId].stakingDate) {
            uint256 initialAmount = amount;
            amount = amount.add(((amount.mul(rewards[rewardId].percentage)).div(100)));
            _unstakeTokens(msg.sender,amount);
            userStakedToken[msg.sender][rewardId][stakeId].amount = userStakedToken[msg.sender][rewardId][stakeId].amount.sub(initialAmount);
        } else {
            _unstakeTokens(msg.sender,amount);
            userStakedToken[msg.sender][rewardId][stakeId].amount = userStakedToken[msg.sender][rewardId][stakeId].amount.sub(amount);
        }
    }

    function _unstakeTokens(address user,uint256 amount) private {
        require(contractBalance >= amount,"funds not available");
        erc20Contract.safeTransfer(user, amount);
        contractBalance = erc20Contract.balanceOf(address(this));
        emit TokensUnstaked(user, amount);
    }

    /// @dev Transfer accidentally locked ERC20 tokens.
    /// @param token - ERC20 token address.
    /// @param amount of ERC20 tokens to remove.
    function transferAccidentallyLockedTokens(IERC20 token, uint256 amount) public onlyOwner {
        require(address(token) != address(0), "Token address can not be zero");
        // This function can not access the official timelocked tokens; just other random ERC20 tokens that may have been accidently sent here
        require(token != erc20Contract, "Token address can not be ERC20 address which was passed into the constructor");
        // Transfer the amount of the specified ERC20 tokens, to the owner of this contract
        token.safeTransfer(owner, amount);
    }

    function addRewardFunds(IERC20 token, uint256 amount) public onlyOwner {
        require(token == erc20Contract, "You are only allowed to stake the official erc20 token address which was passed into this contract's constructor");
        require(amount <= token.balanceOf(msg.sender), "Not enough STATE tokens in your wallet, please try lesser amount");

        token.safeTransferFrom(msg.sender, address(this), amount);
        contractBalance = erc20Contract.balanceOf(address(this));  

    }

    function collectUnusedRewardToken() public onlyOwner {
        erc20Contract.safeTransfer(owner, erc20Contract.balanceOf(address(this)));    
    }

    function addReward(string memory name, uint256 percentage, uint256 time) public onlyOwner {
        rewardsCounter++;
        rewards.push(Reward(rewardsCounter,name,percentage,time,true));
    }

    function modifyRewards(uint256 id,string memory name, uint256 percentage, uint256 time) public onlyOwner {
        rewards[id] = Reward(id,name,percentage,time,true);
    }

    function removeRewards(uint256 id) public onlyOwner {
        rewards[id].allowed = false;    
    }

    function pauseStaking () public onlyOwner isActive {
        active = false;
    }

    function unPauseStaking () public onlyOwner {
        require(!active, "the contract is already active");
        active = true;
    }

    function getRewardsList() public view returns(Reward[] memory) {
        return rewards;
    }

    function getUserStakingInfo (address user, uint256 rewardId) public view returns (StakingInfo[] memory) {
        return userStakedToken[user][rewardId];
    }
}"
