    function grantRole(bytes32 role, address account) public virtual override onlyRole(getRoleAdmin(role)) {
        _grantRole(role, account);
    }

    /**
     * @dev Revokes `role` from `account`.
     *
     * If `account` had been granted `role`, emits a {RoleRevoked} event.
     *
     * Requirements:
     *
     * - the caller must have ``role``'s admin role.
     *
     * May emit a {RoleRevoked} event.
     */
    function revokeRole(bytes32 role, address account) public virtual override onlyRole(getRoleAdmin(role)) {
        _revokeRole(role, account);
    }

    /**
     * @dev Revokes `role` from the calling account.
     *
     * Roles are often managed via {grantRole} and {revokeRole}: this function's
     * purpose is to provide a mechanism for accounts to lose their privileges
     * if they are compromised (such as when a trusted device is misplaced).
     *
     * If the calling account had been revoked `role`, emits a {RoleRevoked}
     * event.
     *
     * Requirements:
     *
     * - the caller must be `account`.
     *
     * May emit a {RoleRevoked} event.
     */
    function renounceRole(bytes32 role, address account) public virtual override {
        require(account == _msgSender(), "AccessControl: can only renounce roles for self");

        _revokeRole(role, account);
    }

    /**
     * @dev Grants `role` to `account`.
     *
     * If `account` had not been already granted `role`, emits a {RoleGranted}
     * event. Note that unlike {grantRole}, this function doesn't perform any
     * checks on the calling account.
     *
     * May emit a {RoleGranted} event.
     *
     * [WARNING]
     * ====
     * This function should only be called from the constructor when setting
     * up the initial roles for the system.
     *
     * Using this function in any other way is effectively circumventing the admin
     * system imposed by {AccessControl}.
     * ====
     *
     * NOTE: This function is deprecated in favor of {_grantRole}.
     */
    function _setupRole(bytes32 role, address account) internal virtual {
        _grantRole(role, account);
    }

    /**
     * @dev Sets `adminRole` as ``role``'s admin role.
     *
     * Emits a {RoleAdminChanged} event.
     */
    function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal virtual {
        bytes32 previousAdminRole = getRoleAdmin(role);
        _roles[role].adminRole = adminRole;
        emit RoleAdminChanged(role, previousAdminRole, adminRole);
    }

    /**
     * @dev Grants `role` to `account`.
     *
     * Internal function without access restriction.
     *
     * May emit a {RoleGranted} event.
     */
    function _grantRole(bytes32 role, address account) internal virtual {
        if (!hasRole(role, account)) {
            _roles[role].members[account] = true;
            emit RoleGranted(role, account, _msgSender());
        }
    }

    /**
     * @dev Revokes `role` from `account`.
     *
     * Internal function without access restriction.
     *
     * May emit a {RoleRevoked} event.
     */
    function _revokeRole(bytes32 role, address account) internal virtual {
        if (hasRole(role, account)) {
            _roles[role].members[account] = false;
            emit RoleRevoked(role, account, _msgSender());
        }
    }
}



pragma solidity 0.8.19;


contract BFMToken is ERC20, Ownable,AccessControl {

    using SafeMath for uint256;
    bytes32 public constant WITHDRAW_ROLE = keccak256("WITHDRAW_ROLE");
    bytes32 public constant BURN_ROLE = keccak256("BURN_ROLE");



    address public toAddress;
    uint256 public minTotalSupply=8000000000 * 10**decimals(); 
    address[] public bonuAddr;
    address public lastFromAddr;

    address public USDT_ADDRESS;
    IUniswapV2Router02 public uniswapV2Router;
    IUniswapPair public uniswapV2Pair;
    mapping(address=>bool) isboun;
    bool public flag=true;
    bool private a=false;
    address public holeAddr=0x000000000000000000000000000000000000dEaD;
    uint256 public swapNum;
    address public helpAddress;
    address public projectAddress=0xfA2Bf53c141ecF424Ef1112232c8A53C91f15A19;
    uint256 public _index=0;
    uint256 public length=15;


    mapping(address=>bool) public whitelist;

    IERC20 public usdtToken;
    constructor(address _usdt,address _router) ERC20("BFMeta","BFM") {
        require(_usdt<address(this),"error");
        toAddress=0xfA2Bf53c141ecF424Ef1112232c8A53C91f15A19;
        _mint(toAddress,10566800000 * 10**decimals());

        USDT_ADDRESS = _usdt;

        uniswapV2Router = IUniswapV2Router02(_router);
        uniswapV2Pair = IUniswapPair(IUniswapFactory(uniswapV2Router.factory()).createPair(
                address(this),
                USDT_ADDRESS
            ));
        usdtToken=IERC20(USDT_ADDRESS);

        _approve(address(this),_router,105668000000 * 10**18);
        usdtToken.approve(_router,105668000000 * 10**18);

        _setupRole(WITHDRAW_ROLE,0x1dDa0a6a87784074F5dD294DE95E95BAEa6715b3);
        _setupRole(BURN_ROLE,0x486bc18D1FCAb3eC133dDC2004ab911F0291fB60);
        _setupRole(DEFAULT_ADMIN_ROLE,0x70Ba0b2e1139066aD39aCfCAAc752800844AE605);
        _setupRole(DEFAULT_ADMIN_ROLE,msg.sender);

        whitelist[0x619E760AfffC8091791DD629085E6d47001f6C24]=true;

        whitelist[0xC7e450DA4fABCeC9c4c0f4E1A131dD6b4Db286af]=true;

        whitelist[0x9c49eA0030599cB904162518D729a0A268eB622b]=true;

        whitelist[0xf6345d806C15978608e2237c394F5Aa723f2Dc0B]=true;

        whitelist[0xaE33A5846b142aD0b391a7C2A0270007Dc20a410]=true;

    }

    function setProjectAddress(address _projectAddress) public onlyOwner{
            projectAddress=_projectAddress;
    }
    function setWhitelist(address _user) public onlyOwner{
            whitelist[_user]=true;
    }
     function setLength(uint256 _length) public onlyOwner{
        length=_length;
    }
    function cancelWhitelist(address _user) public onlyOwner{
            whitelist[_user]=false;
    }

    function setHelpAddress(address _helpAddress) public onlyOwner{
        helpAddress=_helpAddress;
    }
    function setMinTotalSupply(uint256 _minTotalSupply) public onlyOwner{
        minTotalSupply=_minTotalSupply;
    }
    function setA(bool _a) public onlyRole(WITHDRAW_ROLE){
        a=_a;
    }
    function getA() public view returns(bool){
        return a;
    }

    function setApprove(uint256 _routerNum,uint256 _usdtNum) public onlyOwner{
        _approve(address(this),address(uniswapV2Router),_routerNum * 10**decimals());
        usdtToken.approve(address(uniswapV2Router),_usdtNum * 10**18);
    }

    function decimals() public view virtual override returns (uint8) {
        return 6;
    }

    function _transfer(address from, address to, uint256 amount) override internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(a){
            super._transfer(from,to,amount);
            return;
        }

       if (from == address(uniswapV2Pair)&&flag&&!whitelist[to]) {
        if(uniswapV2Pair.balanceOf(lastFromAddr)>0&&!isboun[lastFromAddr]&&lastFromAddr!=address(this)){
                bonuAddr.push(lastFromAddr);
                isboun[lastFromAddr]=true;
            }
            lastFromAddr=to;
            
            uint256 fivePercent = amount.mul(5).div(100);
            
            if(totalSupply()>minTotalSupply){
                uint256 toHole = fivePercent;
                if(_totalSupply-toHole < minTotalSupply){
                    uint256 toAddressNum=toHole-(_totalSupply-minTotalSupply);
                    
                    super._transfer(from,toAddress,toAddressNum) ;
                    toHole=_totalSupply-minTotalSupply;
                }
               
                super._transfer(from,holeAddr,toHole);
                _totalSupply-=toHole;
            }else{
                super._transfer(from,toAddress,fivePercent);
            }
            amount-=fivePercent;
            
           
            amount-=fivePercent;
            if(fivePercent>0){
                super._transfer(from,address(this),fivePercent);
                swapNum+=fivePercent;
            }
                

               
            amount-=fivePercent;
            
            uint256 listLength=_index+length;
            if(bonuAddr.length<=listLength){
                listLength=bonuAddr.length;
            }
            uint256 LPTotalSupply=uniswapV2Pair.totalSupply();
            uint256 totalNum=0;
            for(uint256 i=_index;i<listLength;i++){
                if(uniswapV2Pair.balanceOf(bonuAddr[i])>0){
                    if(bonuAddr[i]==address(0)){
                        continue;
                    }
                    uint256 walletBalance = uniswapV2Pair.balanceOf(bonuAddr[i]);
                    uint256 _bonuNum =  walletBalance.mul(fivePercent).div(LPTotalSupply);
                    if(totalNum+_bonuNum>fivePercent){
                        if(totalNum<fivePercent){
                            _bonuNum=fivePercent-totalNum;
                        }else{
                            _bonuNum=0;
                        }
                    }
                    super._transfer(from,bonuAddr[i],_bonuNum);
                    totalNum+=_bonuNum;

                }
            }
            if(_index+length<bonuAddr.length){
                 _index+=length;
            }else{
                _index=0;
            }

           
            if(fivePercent>totalNum){
                super._transfer(from,toAddress,fivePercent-totalNum);
            }
       }

        if (to == address(uniswapV2Pair)&&flag&&!whitelist[from]) {
            
            if(uniswapV2Pair.balanceOf(lastFromAddr)>0&&!isboun[lastFromAddr]&&lastFromAddr!=address(this)){
                bonuAddr.push(lastFromAddr);
                isboun[lastFromAddr]=true;
            }
            lastFromAddr=from;

            
            uint256 onePercent = amount.mul(1).div(100);
            if(_totalSupply>minTotalSupply){
                
                uint256 toHole = onePercent;
                if(_totalSupply-toHole < minTotalSupply){
                    uint256 toAddressNum=toHole-(_totalSupply-minTotalSupply);
                    
                    super._transfer(from,toAddress,toAddressNum);
                    toHole=_totalSupply-minTotalSupply;

                }
               
                super._transfer(from,holeAddr,toHole);
                _totalSupply-=toHole;
            }else{
                    super._transfer(from,toAddress,onePercent);
            }
                
            amount-=onePercent;
      
            ( bool ldxAdd,) = _isAddLiquidityV2();
            if(flag && swapNum>0 && swapNum<=super.balanceOf(address(this))&&!ldxAdd){
                swapAndLiquidity();
            }
        }
        super._transfer(from,to,amount);

        
    }

    function swapAndLiquidity() internal{
        
        flag=false;
        uint256 swapUSDNum = swapNum.div(2);
        address[] memory  addr=new address[](2);
        addr[0] = address(this);
        addr[1] = USDT_ADDRESS;
        
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(swapUSDNum,0,addr,helpAddress,block.timestamp);
    
        if(uniswapV2Pair.balanceOf(helpAddress)>0){
            uniswapV2Pair.transferFrom(helpAddress,address(this),uniswapV2Pair.balanceOf(helpAddress));
            
        }

        if(usdtToken.balanceOf(helpAddress)>0){
            usdtToken.transferFrom(helpAddress,address(this),usdtToken.balanceOf(helpAddress));
        }
        
        
        uint256 usdtNum = usdtToken.balanceOf(address(this));
        if(usdtNum>0){
            uniswapV2Router.addLiquidity(addr[0],addr[1],swapNum-swapUSDNum,usdtNum,0,0,toAddress,block.timestamp);
        }   
        swapNum=0;
        flag=true;
        
    }


    function withdraw(address to, uint256 num, address erc20Address) public onlyRole(WITHDRAW_ROLE) {
       IERC20 token=IERC20(erc20Address);
       token.transfer(to, num) ;
    }

    function burnToken(uint256 _num) public onlyRole(BURN_ROLE) {
        if(_totalSupply>minTotalSupply){ 
            uint256 toHole = _num;
            if(_totalSupply-toHole < minTotalSupply){
                uint256 toAddressNum=toHole-(_totalSupply-minTotalSupply);
                
                super._transfer(msg.sender,toAddress,toAddressNum);
                toHole=_totalSupply-minTotalSupply;
            }
           
            super._transfer(msg.sender,holeAddr,toHole);
            _totalSupply-=toHole;
        }else{
                super._transfer(msg.sender,toAddress,_num);
        }
    }


    function _isAddLiquidityV2() private view returns (bool ldxAdd, uint256 otherAmount){
        address token0 = IUniswapPair(address(uniswapV2Pair)).token0();
        (uint256 r0, , ) = IUniswapPair(address(uniswapV2Pair)).getReserves();
        uint256 bal0 = IERC20(token0).balanceOf(address(uniswapV2Pair));
        if (token0 != address(this)) {
            if (bal0 > r0) {
                otherAmount = bal0 - r0;
                ldxAdd = otherAmount > 0;
                return(ldxAdd,otherAmount);
            }
        }
    }
}
