    "contracts/v2/bids/RareboardNFTKeyBidder.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;


import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "../../interfaces/INFTKEYMarketplaceV2.sol";
import "../../interfaces/IWETH.sol";
import "./NFTKeyBidProxyFactory.sol";
import "./IRareboardNFTKeyBidder.sol";
import "./EmergencyRescue.sol";

/** 
 * @title Rareboard bidder contract to create bids for NFTKey marketplace with optional verification.
 * @notice Bids are handled by a proxy contract which is created per user.
 * These prroxy contracts hold the WBNB used for the bids.
 * To make sure dynamic NFTs do not change state after a bid is placed, bidders can submit a optional
 * static verifier call which is evaluated before the bid accept transaction passes.
 */
contract RareboardNFTKeyBidder is NFTKeyBidProxyFactory, EmergencyRescue, ReentrancyGuard, Pausable, IRareboardNFTKeyBidder {

    /** @dev Static call to be verrified on bid acceptance */
    struct Verifier {
        address target;
        bytes data;
    }

    /** @dev Current state of a bid */
    struct BitState {
        address bidder;
        uint256 tokenId;
        uint256 value;
        uint256 expireTimestamp;
        bool hasVerifier;
        bool verificationPassed;
    }

    /** @dev Bid configuration */
    struct Bid {
        address erc721Address;
        uint256 tokenId;
        uint256 value;
        uint256 expireTimestamp;
        Verifier verifier;
    }

    struct Token {
        address erc721Address;
        uint256 tokenId;
    }

    event BidEntered(address indexed user, address proxy, address indexed erc721Address, uint256 indexed tokenId, uint256 value, uint256 expireTimestamp, bool isSafe);
    event BidWithdrawn(address indexed user, address proxy, address indexed erc721Address, uint256 indexed tokenId);
    event BidAccepted(address indexed bidder, address proxy, address indexed erc721Address, uint256 indexed tokenId, address seller);
    event ProxyCreated(address indexed user, address proxy);
    event ProxyClosed(address indexed user, address proxy);

    IWETH public constant WBNB = IWETH(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
    INFTKEYMarketplaceV2 public constant NFTKEY = INFTKEYMarketplaceV2(0x55E53B5e38Decb925A26cA5F38BddE68F373Bba8);

    /** user => proxy */
    mapping(address => NFTKeyBidProxy) public proxies;

    /** proxy => userr */
    mapping(address => address) public proxyOwners;

    /** user => erc721Address => tokenId => Verifier */
    mapping(address => mapping(address => mapping(uint256 => Verifier))) public verifiers;


    function createProxy() external nonReentrant whenNotPaused {
        NFTKeyBidProxy proxy = proxies[msg.sender];
        require(address(proxy) == address(0), "Proxy already exists");
        _getProxy(msg.sender);
    }

    function enterBidsForTokens(Bid[] calldata bids) external payable nonReentrant whenNotPaused {
        NFTKeyBidProxy proxy = _getProxy(msg.sender);
        proxy.deposit{value: msg.value}();

        for (uint256 i = 0; i < bids.length; ++i) {
            bool isSafe = bids[i].verifier.target != address(0);
            if (isSafe) {
                require(_evaluateVerifier(bids[i].verifier), "Verifier failed");
                verifiers[msg.sender][bids[i].erc721Address][bids[i].tokenId] = bids[i].verifier;
            }

            emit BidEntered(msg.sender, address(proxy), bids[i].erc721Address, bids[i].tokenId, bids[i].value, bids[i].expireTimestamp, isSafe);
            proxy.enterBidForToken(bids[i].erc721Address, bids[i].tokenId, bids[i].value, bids[i].expireTimestamp);
        }
    }

    function withdrawBidsForTokens(Token[] calldata tokens, uint256 withdrawAmount) external nonReentrant {
        NFTKeyBidProxy proxy = proxies[msg.sender];
        require(address(proxy) != address(0), "No proxy registered");

        for (uint256 i = 0; i < tokens.length; ++i) {
            proxy.withdrawBidForToken(tokens[i].erc721Address, tokens[i].tokenId);

            if (verifiers[msg.sender][tokens[i].erc721Address][tokens[i].tokenId].target != address(0)) {
                delete verifiers[msg.sender][tokens[i].erc721Address][tokens[i].tokenId];
            }
        }

        if (withdrawAmount > 0) proxy.withdraw(withdrawAmount);
    }

    function closeProxy() external nonReentrant {
        NFTKeyBidProxy proxy = proxies[msg.sender];
        require(address(proxy) != address(0), "No proxy registered");
        proxy.closeProxy();

        emit ProxyClosed(msg.sender, address(proxy));

        delete proxies[msg.sender];
        delete proxyOwners[address(proxy)];
    }

    function deposit() external payable nonReentrant whenNotPaused {

        NFTKeyBidProxy proxy = _getProxy(msg.sender);
        proxy.deposit{value: msg.value}();
    }

    function withdraw(uint256 amount) external nonReentrant {
        NFTKeyBidProxy proxy = proxies[msg.sender];
        require(address(proxy) != address(0), "No proxy registered");
        proxy.withdraw(amount);
    }

    function withdrawAll() external nonReentrant {
        NFTKeyBidProxy proxy = proxies[msg.sender];
        require(address(proxy) != address(0), "No proxy registered");
        proxy.withdrawAll();
    }

    function onBidAccepted(address user, address erc721Address, uint256 tokenId, address seller) external override {
        require(!paused(), "Paused");
        NFTKeyBidProxy proxy = proxies[user];
        require(address(proxy) == msg.sender, "Only proxy");

        Verifier memory verifier = verifiers[user][erc721Address][tokenId];
        if (verifier.target != address(0)) {
            Address.functionStaticCall(verifier.target, verifier.data, "Bid verification failed");
            delete verifiers[msg.sender][erc721Address][tokenId];
        }

        emit BidAccepted(user, address(proxy), erc721Address, tokenId, seller);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }


    function availableFunds(address user) external view returns (uint256) {
        NFTKeyBidProxy proxy = proxies[user];
        if (address(proxy) == address(0)) return 0;
        return WBNB.balanceOf(address(proxy));
    }

    function getBidderBids(
        address erc721Address,
        address bidder,
        uint256 from,
        uint256 size
    ) external view returns (BitState[] memory bids) {
        NFTKeyBidProxy proxy = proxies[bidder];
        INFTKEYMarketplaceV2.Bid[] memory _bids = NFTKEY.getBidderBids(erc721Address, address(proxy), from, size);

        bids = new BitState[](_bids.length);
        for (uint256 i = 0; i < _bids.length; ++i) {
            (, bool hasVerifier, bool verificationPassed) = _evaluateBid(erc721Address, _bids[i]);
            bids[i].bidder = address(proxy);
            bids[i].tokenId = _bids[i].tokenId;
            bids[i].value = _bids[i].value;
            bids[i].expireTimestamp = _bids[i].expireTimestamp;
            bids[i].hasVerifier = hasVerifier;
            bids[i].verificationPassed = verificationPassed;
        }
    }

    function getBidderTokenBid(
        address erc721Address,
        uint256 tokenId,
        address bidder
    ) external view returns (BitState memory bid) {
        NFTKeyBidProxy proxy = proxies[bidder];
        INFTKEYMarketplaceV2.Bid memory _bid = NFTKEY.getBidderTokenBid(erc721Address, tokenId, address(proxy));

        (, bool hasVerifier, bool verificationPassed) = _evaluateBid(erc721Address, _bid);
        bid.bidder = address(proxy);
        bid.tokenId = tokenId;
        bid.value = _bid.value;
        bid.expireTimestamp = _bid.expireTimestamp;
        bid.hasVerifier = hasVerifier;
        bid.verificationPassed = verificationPassed;
    }

    function evaluateBid(address erc721Address, INFTKEYMarketplaceV2.Bid memory bid) external view returns (bool isProxy, bool hasVerifier, bool verificationPassed) {
        return _evaluateBid(erc721Address, bid);
    }

    function evaluateBids(address erc721Address, INFTKEYMarketplaceV2.Bid[] memory _bids) external view returns (bool[] memory isProxies, bool[] memory hasVerifiers, bool[] memory verificationPasseds) {
        isProxies = new bool[](_bids.length);
        hasVerifiers = new bool[](_bids.length);
        verificationPasseds = new bool[](_bids.length);

        for (uint256 i = 0; i < _bids.length; ++i) {
            (bool isProxy, bool hasVerifier, bool verificationPassed) = _evaluateBid(erc721Address, _bids[i]);
            isProxies[i] = isProxy;
            hasVerifiers[i] = hasVerifier;
            verificationPasseds[i] = verificationPassed;
        }
    }


    function _getProxy(address user) internal returns (NFTKeyBidProxy) {
        NFTKeyBidProxy proxy = proxies[user];
        if (address(proxy) == address(0)) {
            proxy = createProxy(user);
            proxies[user] = proxy;
            proxyOwners[address(proxy)] = user;
            emit ProxyCreated(user, address(proxy));
        }
        return proxy;
    }

    function _evaluateVerifier(Verifier memory verifier) internal view returns (bool) {
        (bool success, ) = verifier.target.staticcall(verifier.data);
        return success;
    }

    function _evaluateBid(address erc721Address, INFTKEYMarketplaceV2.Bid memory _bid) internal view returns (bool isProxy, bool hasVerifier, bool verificationPassed) {
        address user = proxyOwners[_bid.bidder];
        isProxy = user != address(0);

        Verifier memory verifier = verifiers[user][erc721Address][_bid.tokenId];
        hasVerifier = isProxy && verifier.target != address(0);

        verificationPassed = !hasVerifier || _evaluateVerifier(verifier);
    }
}"
    },
