    "contracts/v2/bids/NFTKeyBidProxyFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import "./NFTKeyBidProxy.sol";

contract NFTKeyBidProxyFactory {
    NFTKeyBidProxy public instance;


    constructor() {
        instance = new NFTKeyBidProxy();
    }

    function createProxy(address _user) internal returns (NFTKeyBidProxy) {
        bytes memory data = abi.encodeWithSignature("initialize(address)", _user);
        ERC1967Proxy proxy = new ERC1967Proxy(address(instance), data);
        return NFTKeyBidProxy(payable(proxy));
    }
}
"
