    "contracts/v2/bids/NFTKeyBidProxy.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./IRareboardNFTKeyBidder.sol";
import "../../interfaces/INFTKEYMarketplaceV2.sol";
import "../../interfaces/IWETH.sol";


contract NFTKeyBidProxy is IERC721Receiver, Initializable {

    INFTKEYMarketplaceV2 public constant NFTKEY = INFTKEYMarketplaceV2(0x55E53B5e38Decb925A26cA5F38BddE68F373Bba8);
    IWETH public constant WBNB = IWETH(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);

    address public owner;
    address public controller;

    modifier onlyController {
        require(msg.sender == controller, "Only controller");
        _;
    }

    function initialize(address _owner) external initializer {
        controller = msg.sender;
        owner = _owner;
        WBNB.approve(address(NFTKEY), type(uint256).max);
    }

    receive() external payable {
        require(msg.sender == address(WBNB), "Only unwrapping");
    }

    function enterBidForToken(
        address erc721Address,
        uint256 tokenId,
        uint256 value,
        uint256 expireTimestamp
    ) external onlyController {
        NFTKEY.enterBidForToken(erc721Address, tokenId, value, expireTimestamp);
    }

    function withdrawBidForToken(address erc721Address, uint256 tokenId) external {
        NFTKEY.withdrawBidForToken(erc721Address, tokenId);
    }

    function deposit() external payable onlyController {
        WBNB.deposit{value: msg.value}();
    }

    function withdraw(uint256 amount) external onlyController {
        WBNB.withdraw(amount);
        Address.sendValue(payable(owner), amount);
    }

    function withdrawAll() external onlyController {
        uint256 balanceWBNB = WBNB.balanceOf(address(this));
        require(balanceWBNB > 0, "Nothing to withdraw");
        WBNB.withdraw(balanceWBNB);
        Address.sendValue(payable(owner), balanceWBNB);
    }

    function closeProxy() external onlyController {
        uint256 balance = WBNB.balanceOf(address(this));
        if (balance > 0) {
            WBNB.withdraw(balance);
        }
        selfdestruct(payable(owner));
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes memory data
    ) public virtual override returns (bytes4) {
        address erc721Address = msg.sender;
        require(operator == address(NFTKEY), "Only bids");

        IRareboardNFTKeyBidder(controller).onBidAccepted(owner, erc721Address, tokenId, from);

        IERC721(erc721Address).safeTransferFrom(address(this), owner, tokenId, data);
        return this.onERC721Received.selector;
    }


    // Emergency function: In case any ETH get stuck in the contract unintentionally
    // Only owner can retrieve the asset balance to a recipient address
    function rescueETH(address payable recipient) onlyController external {
        revert("Cannot rescue ETH");
    }

    // Emergency function: In case any ERC20 tokens get stuck in the contract unintentionally
    // Only owner can retrieve the asset balance to a recipient address
    function rescueERC20(address asset, address recipient) onlyController external {
        require(asset != address(WBNB), "Cannot rescue WBNB");
        asset.call(abi.encodeWithSelector(0xa9059cbb, recipient, IERC20(asset).balanceOf(address(this))));
    }

    // Emergency function: In case any ERC721 tokens get stuck in the contract unintentionally
    // Only owner can retrieve the asset balance to a recipient address
    function rescueERC721(address asset, uint256[] calldata ids, address recipient) onlyController external {
        for (uint256 i = 0; i < ids.length; i++) {
            IERC721(asset).transferFrom(address(this), recipient, ids[i]);
        }
    }

    // Emergency function: In case any ERC1155 tokens get stuck in the contract unintentionally
    // Only owner can retrieve the asset balance to a recipient address
    function rescueERC1155(address asset, uint256[] calldata ids, uint256[] calldata amounts, address recipient) onlyController external {
        for (uint256 i = 0; i < ids.length; i++) {
            IERC1155(asset).safeTransferFrom(address(this), recipient, ids[i], amounts[i], "");
        }
    }
}"
    },
