pragma solidity ^0.7.4;






/**
 * @title HGraphToken contract
 * @dev This is the implementation of the ERC20 HyperGraph Token.
 */
contract HGraphToken is Ownable, ERC20, ERC20Burnable {
    using SafeMath for uint256;

    // -- EIP712 --
    // https://github.com/ethereum/EIPs/blob/master/EIPS/eip-712.md#definition-of-domainseparator

    bytes32 private constant DOMAIN_TYPE_HASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract,bytes32 salt)"
    );
    bytes32 private constant DOMAIN_NAME_HASH = keccak256("HyperGraph Token");
    bytes32 private constant DOMAIN_VERSION_HASH = keccak256("0");
    bytes32
        private constant DOMAIN_SALT = 0x51f3d585afe6dfeb2af01bba0889a36c1db03beec88c6a4d0c53817069026afa; // Randomly generated salt
    bytes32 private constant PERMIT_TYPEHASH = keccak256(
        "Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)"
    );

    // -- State --

    bytes32 private DOMAIN_SEPARATOR;
    mapping(address => bool) private _minters;
    mapping(address => uint256) public nonces;

    // -- Events --
    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);
    event MintedMine(address indexed account, uint256 amount);
    event MintedInvestor(address indexed account, uint256 amount);
    event MintedMarketing(address indexed account, uint256 amount);
    event MintedTeam(address indexed account, uint256 amount);
    event MintedOrgNode(address indexed account, uint256 amount);
    event SetMintRound(uint256 startBlock, uint256 endBlock, uint256 blockSupply);

    uint256 private TOTAL_SUPPLY = 5 * (10 ** 8) * (10 ** 18); // total supply is 0.5 billion
    
    uint256 private TEAM_TOTAL_SUPPLY = 5 * (10 ** 7) * (10 ** 18); 
    uint256 public _nowTeamSupply = 0;
    
    uint256 private INVESTOR_TOTAL_SUPPLY = 5 * (10 ** 7) * (10 ** 18); 
    uint256 public _nowInvestorSupply = 0;
    
    uint256 private MARKETING_TOTAL_SUPPLY = 25 * (10 ** 6) * (10 ** 18); 
    uint256 private INITIAL_SUPPLY = 25 * (10 ** 4) * (10 ** 18);

    uint256 private MINE_TOTAL_SUPPLY = 125 * (10 ** 6) * (10 ** 18); 
    uint256 public _nowMineSupply = 0;

    uint256 private ORGNODE_TOTAL_SUPPLY = 25 * (10 ** 7) * (10 ** 18);

    struct MintRound {
        uint16 round;     
        uint256 startBlock; 
        uint256 endBlock; 
        uint256 mineBlockSupply;
        uint256 roundMineTotalSupply;
        uint256 roundMineSupply;
    }

    MintRound[] public mintRounds;
    bool private destroyOrg = false;

    function mintOrgNode() external onlyMinter{
        require(!destroyOrg, "has been minted");
        mint(address(0x1), ORGNODE_TOTAL_SUPPLY);
        destroyOrg = true;
        emit MintedOrgNode(address(0x1), ORGNODE_TOTAL_SUPPLY);
    }

    function mintMarketing(address _marketingLockAddress) external onlyMinter{
        uint256 _amount = MARKETING_TOTAL_SUPPLY.sub(INITIAL_SUPPLY);
        mint(_marketingLockAddress, _amount);
        emit MintedMarketing(_marketingLockAddress, _amount);
    }

     function setMintRound(uint256 _startBlock, uint256 _endBlock, uint256 _blockSupply) external onlyMinter{
        require(block.number < _startBlock && block.number < _endBlock, "Time must be future");
        require(_endBlock > _startBlock, "EndBlock must greater that startBlock");

        uint16 len = uint16(mintRounds.length);
        if(len > 0){
            MintRound memory lastRound = mintRounds[len - 1];
            require(lastRound.endBlock < _startBlock, "One MineRound at a time");    
        }
        mintRounds.push(MintRound({
            round: len + 1,
            startBlock: _startBlock,
            endBlock: _endBlock,
            mineBlockSupply: _blockSupply,
            roundMineTotalSupply: _endBlock.sub(_startBlock).mul(_blockSupply),
            roundMineSupply: 0
        }));
        emit SetMintRound(_startBlock, _endBlock, _blockSupply);
    }
    function _getReleaseableAmount() private view returns(uint256){
        uint16 len = uint16(mintRounds.length);
        require(len > 0, "no mint rounds");
        uint256 canReleaseAmount = 0;
        
        for(uint16 i=0; i<len; i++){
            uint256 roundSupply = 0;
            if(mintRounds[i].endBlock < block.number){
                roundSupply = mintRounds[i].roundMineTotalSupply.mul(2).div(5);
                canReleaseAmount = canReleaseAmount.add(roundSupply);
            }
            else if(mintRounds[i].startBlock > block.number){
                continue;
            }
            else{
                roundSupply = (block.number.sub(mintRounds[i].startBlock)).mul(mintRounds[i].mineBlockSupply).mul(2).div(5);
                canReleaseAmount = canReleaseAmount.add(roundSupply);
            }
        }
        return canReleaseAmount;
    }

    function mintTeam() external onlyMinter{
        uint256 canReleaseAmount = _getReleaseableAmount();
        require(canReleaseAmount > _nowTeamSupply, "no need to mint team token");
        if(canReleaseAmount > TEAM_TOTAL_SUPPLY){
            canReleaseAmount = TEAM_TOTAL_SUPPLY;
        }

        uint256 _amount = canReleaseAmount.sub(_nowTeamSupply);
        require(_nowTeamSupply.add(_amount) <= TEAM_TOTAL_SUPPLY, "exceed team amount");
      
        _nowTeamSupply = _nowTeamSupply.add(_amount);
        mint(msg.sender, _amount);
        emit MintedTeam(msg.sender, _amount);
    }

    function mintInvestor() external onlyMinter{
        uint256 canReleaseAmount = _getReleaseableAmount();
        require(canReleaseAmount > _nowInvestorSupply, "No need to mint investor token");
        if(canReleaseAmount > INVESTOR_TOTAL_SUPPLY){
            canReleaseAmount = INVESTOR_TOTAL_SUPPLY;
        }

        uint256 _amount = canReleaseAmount.sub(_nowInvestorSupply);
        require(_nowInvestorSupply.add(_amount) <= INVESTOR_TOTAL_SUPPLY, "Exceed investor amount");
      
        _nowInvestorSupply = _nowInvestorSupply.add(_amount);
        mint(msg.sender, _amount);
        emit MintedInvestor(msg.sender, _amount);
    }

    function mintMine(address _to, uint256 _amount) external onlyMinter returns (bool){
        uint256 len = mintRounds.length;
        require(len > 0, "no mint rounds");

        // MintRound storage currentRound = mintRounds[len - 1];
        uint256 _tamount = _amount;
        if(_nowMineSupply.add(_tamount) > MINE_TOTAL_SUPPLY){
            _tamount = MINE_TOTAL_SUPPLY.sub(_nowMineSupply);
        }
       
        // if(currentRound.roundMineSupply.add(_tamount) > currentRound.roundMineTotalSupply){
        //     _tamount = currentRound.roundMineTotalSupply.sub(currentRound.roundMineSupply);
        //     if(_tamount == 0){
        //         return false;
        //     }
        // }
        // currentRound.roundMineSupply = currentRound.roundMineSupply.add(_tamount);

        _nowMineSupply = _nowMineSupply.add(_tamount);
        mint(_to, _tamount);
        emit MintedMine(_to, _tamount);
        return true;
    }
   
    modifier onlyMinter() {
        require(isMinter(msg.sender), "Only minter can call");
        _;
    }

    /**
     * @dev HyperGraph Token Contract Constructor.
     */
    constructor() ERC20("HyperGraph Token", "HGT") {
        
        // The Governor has the initial supply of tokens
        mint(msg.sender, INITIAL_SUPPLY);
        emit MintedMarketing(msg.sender, INITIAL_SUPPLY);

        // The Governor is the default minter
        _addMinter(msg.sender);

        // EIP-712 domain separator
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                DOMAIN_TYPE_HASH,
                DOMAIN_NAME_HASH,
                DOMAIN_VERSION_HASH,
                _getChainID(),
                address(this),
                DOMAIN_SALT
            )
        );
    }

    /**
     * @dev Approve token allowance by validating a message signed by the holder.
     * @param _owner Address of the token holder
     * @param _spender Address of the approved spender
     * @param _value Amount of tokens to approve the spender
     * @param _deadline Expiration time of the signed permit
     * @param _v Signature version
     * @param _r Signature r value
     * @param _s Signature s value
     */
    function permit(
        address _owner,
        address _spender,
        uint256 _value,
        uint256 _deadline,
        uint8 _v,
        bytes32 _r,
        bytes32 _s
    ) external {
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(
                    abi.encode(
                        PERMIT_TYPEHASH,
                        _owner,
                        _spender,
                        _value,
                        nonces[_owner],
                        _deadline
                    )
                )
            )
        );
        nonces[_owner] = nonces[_owner].add(1);

        address recoveredAddress = ECDSA.recover(digest, abi.encodePacked(_r, _s, _v));
        require(_owner == recoveredAddress, "HGT: invalid permit");
        require(_deadline == 0 || block.timestamp <= _deadline, "HGT: expired permit");

        _approve(_owner, _spender, _value);
    }

    /**
     * @dev Add a new minter.
     * @param _account Address of the minter
     */
    function addMinter(address _account) external onlyOwner {
        _addMinter(_account);
    }

    /**
     * @dev Remove a minter.
     * @param _account Address of the minter
     */
    function removeMinter(address _account) external onlyOwner {
        _removeMinter(_account);
    }

    /**
     * @dev Mint new tokens.
     * @param _to Address to send the newly minted tokens
     * @param _amount Amount of tokens to mint
     */
    function mint(address _to, uint256 _amount) private {
        require(_amount.add(totalSupply()) <= TOTAL_SUPPLY, "Exceed totalSupply");
        _mint(_to, _amount);
    }

    /**
     * @dev Return if the `_account` is a minter or not.
     * @param _account Address to check
     * @return True if the `_account` is minter
     */
    function isMinter(address _account) public view returns (bool) {
        return _minters[_account];
    }

    /**
     * @dev Add a new minter.
     * @param _account Address of the minter
     */
    function _addMinter(address _account) private {
        _minters[_account] = true;
        emit MinterAdded(_account);
    }

    /**
     * @dev Remove a minter.
     * @param _account Address of the minter
     */
    function _removeMinter(address _account) private {
        _minters[_account] = false;
        emit MinterRemoved(_account);
    }

    /**
     * @dev Get the running network chain ID.
     * @return The chain ID
     */
    function _getChainID() private pure returns (uint256) {
        uint256 id;
        assembly {
            id := chainid()
        }
        return id;
    }
}
