contract SwapPoolMain is Ownable {

    address public apgc;
    address public apgb;
    address public collect;
    address private dead;

    constructor() {
        apgc = 0xc8042B7124C6a014A2eb134FB7Ca897F91Bf2ba4;
        apgb = 0xc57ee2Cb06Cc4c896FB927CB0ee4A99A80A963dB;
        collect = _msgSender();
        dead = 0x000000000000000000000000000000000000dEaD;
    }


    function convert(address fromToken, address toToken, uint256 amount) public {
        require(amount > 0, "Wrong amount");
        require(fromToken != toToken, "Wrong token");
        if (fromToken == apgc) {
            IERC20(apgc).transferFrom(_msgSender(), address(this), amount);
            IERC20(apgb).transfer(_msgSender(), amount);
        } else if (fromToken == apgb) {
            IERC20(apgb).transferFrom(_msgSender(), dead, amount);
            IERC20(apgc).transfer(_msgSender(), amount);
        } else {
            revert("Wrong token");
        }
    }

    function setApgc(address account, uint8 t) public onlyOwner {
        require(account != address(0), "Wrong address");
        if (t == 1) {
            apgc = account;
        } else {
            apgb = account;
        }
    }

    function setCollect(address collect_) public onlyOwner {
        collect = collect_;
    }

    function withdraw(address token, address account, uint256 amount) public onlyOwner {
        IERC20(token).transfer(account, amount);
    }
}
