contract WRAITH is Context, IERC20, IERC20Metadata, Ownable {
    
    using SafeMath for uint256;
    using Address for address;
    
    address payable public MarketingWalletAddress = payable(0xCb781eF6f03Bc22606e9e2E75A936C71cE177E48); // Marketing Address
    address payable public wraithProtocolWalletAddress = payable(0x09A10d2A856c7cc7Fb1E9A9f49a748db1099a4b1); // wraith Address
    
    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;
    
    mapping (address => bool) private _isExcludedFromFee;
    mapping (address => bool) private _isExcluded;
    address[] private _excluded;
    
    mapping (address => bool) private _isExchange;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal;
    uint256 private _rTotal;
    uint256 private _tFeeTotal;

    string private _name;
    string private _symbol;
    uint8 private _decimals = 9;

    uint256 public _burnFee = 2;
    uint256 public _liquidityFee = 5;
    uint256 public _MarketingFee = 3;
    uint256 public _wraithProtocolFee = 3;
    uint256 public _totalTaxPercent = 0;
    uint256 private _prevTotalTaxPercent = 0;
    
    uint256 public _sellBurnFee = 3;
    uint256 public _sellLiquidityFee = 6;
    uint256 public _sellMarketingFee = 4;
    uint256 public _sellWraithProtocolFee = 4;
    uint256 public _sellTotalTaxPercent = 0;
    uint256 private _sellPrevTotalTaxPercent = 0;
    
    uint256 public _buyBurnFee = 3;
    uint256 public _buyLiquidityFee = 6;
    uint256 public _buyMarketingFee = 4;
    uint256 public _buyWraithProtocolFee = 4;
    uint256 public _buyTotalTaxPercent = 0;
    uint256 private _buyPrevTotalTaxPercent = 0;

    uint256 public _maxTxAmount = 3000000 * 10**6 * 10**9;
    uint256 private minimumTokensBeforeSwap = 50000 * 10**6 * 10**9; 

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    
    bool internal inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;
    bool public swapAndLiquifyByLimitOnly = false;

    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    
    event SwapETHForTokens(
        uint256 amountIn,
        address[] path
    );
    
    event SwapTokensForETH(
        uint256 amountIn,
        address[] path
    );
    
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
    
    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }
    
    constructor (string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        
        //address uniswapRouterAddr = 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3; //testnet pancakeswap (https://pancake.kiemtienonline360.com/)
        address uniswapRouterAddr = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(uniswapRouterAddr);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;

        _isExchange[uniswapRouterAddr] = true;

        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        
        _totalTaxPercent = _burnFee.add(_liquidityFee).add(_MarketingFee).add(_wraithProtocolFee);
        _prevTotalTaxPercent = _totalTaxPercent;
    
        _sellTotalTaxPercent = _sellBurnFee.add(_sellLiquidityFee).add(_sellMarketingFee).add(_sellWraithProtocolFee);
        _sellPrevTotalTaxPercent = _sellTotalTaxPercent;
    
        _buyTotalTaxPercent = _buyBurnFee.add(_buyLiquidityFee).add(_buyMarketingFee).add(_buyWraithProtocolFee);
        _buyPrevTotalTaxPercent = _buyTotalTaxPercent;

        _mint( _msgSender(), (9400827596 * 10**3 * 10**9));
    }

    function decimals() public override view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tOwned[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function minimumTokensBeforeSwapAmount() public view returns (uint256) {
        return minimumTokensBeforeSwap;
    }

    function _transfer(address from, address to, uint256 amount) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!_isExcluded[from], "Address is excluded from sending");
        require(!_isExcluded[to], "Address is excluded from receiving");
        
        uint256 senderBalance = balanceOf(from);
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        
        if(from != owner() && to != owner()) {
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");
        }



        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinimumTokenBalance = contractTokenBalance >= minimumTokensBeforeSwap;

        if (overMinimumTokenBalance && !inSwapAndLiquify && from != uniswapV2Pair && swapAndLiquifyEnabled) 
        {
            if(swapAndLiquifyByLimitOnly)
                contractTokenBalance = minimumTokensBeforeSwap;
            swapAndLiquify(from, to, contractTokenBalance);    
        }

        bool takeFee = true;
        //if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
        }
        _tokenTransfer(from, to, amount);
    }

    function swapAndLiquify(address from, address to, uint256 tAmount) private lockTheSwap {
       
        uint256 forBurn;
        uint256 forLiquidity;
        uint256 forWallets;
        if(_isExchange[from]){
            forBurn = tAmount.div(_buyTotalTaxPercent).mul(_buyBurnFee);
            forLiquidity = tAmount.div(_buyTotalTaxPercent).mul(_buyLiquidityFee);
            forWallets = tAmount.sub(forLiquidity).sub(forBurn);
        }else if(_isExchange[to]){
            forBurn = tAmount.div(_sellTotalTaxPercent).mul(_sellBurnFee);
            forLiquidity = tAmount.div(_sellTotalTaxPercent).mul(_sellLiquidityFee);
            forWallets = tAmount.sub(forLiquidity).sub(forBurn);
        }else{
            forBurn = tAmount.div(_totalTaxPercent).mul(_burnFee);
            forLiquidity = tAmount.div(_totalTaxPercent).mul(_liquidityFee);
            forWallets = tAmount.sub(forLiquidity).sub(forBurn);
        }
            

        if(forLiquidity > 0)
        {
            uint256 half = forLiquidity.div(2);
            uint256 otherHalf = forLiquidity.sub(half);
    
            uint256 initialBalance = address(this).balance;
            swapTokensForEth(half); 
            uint256 newBalance = address(this).balance.sub(initialBalance);
            addLiquidity(otherHalf, newBalance);
            emit SwapAndLiquify(half, newBalance, otherHalf);
        }

        if(forWallets > 0 && _buyMarketingFee.add(_buyWraithProtocolFee) > 0 && _isExchange[from])
        {
            uint256 initialBalance = address(this).balance;
            swapTokensForEth(forWallets);
            uint256 newBalance = address(this).balance.sub(initialBalance);
    
            uint256 MarketingShare = newBalance.div(_buyMarketingFee.add(_buyWraithProtocolFee)).mul(_buyMarketingFee);
            uint256 wraithWalletShare = newBalance.sub(MarketingShare);
            
            if(MarketingShare > 0)
                transferToAddressETH(from, MarketingWalletAddress, MarketingShare);
            
            if(wraithWalletShare > 0)
                transferToAddressETH(from, wraithProtocolWalletAddress, wraithWalletShare);
        }else if(forWallets > 0 && _sellMarketingFee.add(_sellWraithProtocolFee) > 0 && _isExchange[from]) {
            uint256 initialBalance = address(this).balance;
            swapTokensForEth(forWallets);
            uint256 newBalance = address(this).balance.sub(initialBalance);
    
            uint256 MarketingShare = newBalance.div(_sellMarketingFee.add(_sellWraithProtocolFee)).mul(_sellMarketingFee);
            uint256 wraithWalletShare = newBalance.sub(MarketingShare);
            
            if(MarketingShare > 0)
                transferToAddressETH(from, MarketingWalletAddress, MarketingShare);
            
            if(wraithWalletShare > 0)
                transferToAddressETH(from, wraithProtocolWalletAddress, wraithWalletShare);
        }else{
            uint256 initialBalance = address(this).balance;
            swapTokensForEth(forWallets);
            uint256 newBalance = address(this).balance.sub(initialBalance);
    
            uint256 MarketingShare = newBalance.div(_MarketingFee.add(_wraithProtocolFee)).mul(_MarketingFee);
            uint256 wraithWalletShare = newBalance.sub(MarketingShare);
            
            if(MarketingShare > 0)
                transferToAddressETH(from, MarketingWalletAddress, MarketingShare);
            
            if(wraithWalletShare > 0)
                transferToAddressETH(from, wraithProtocolWalletAddress, wraithWalletShare);
        }
        
        if(forBurn > 0)
        {
            _burn(address(this),forBurn);
        }
    }
    
    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this), // The contract
            block.timestamp
        );
        
        emit SwapTokensForETH(tokenAmount, path);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );
    }
    
    function _tokenTransfer(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 tTransferAmount, uint256 tLiquidity) = _getValues(sender, recipient, tAmount);
    	_tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _takeLiquidity(uint256 tLiquidity) private {
        _tOwned[address(this)] = _tOwned[address(this)].add(tLiquidity);
    }
    
    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }
    
    function _getValues(address sender, address recipient, uint256 tAmount) private view returns (uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tLiquidity) = _getTValues(sender, recipient, tAmount);
        (uint256 rAmount, uint256 rTransferAmount) = _getRValues(tAmount, tLiquidity, _getRate());
        return (rAmount, rTransferAmount, tTransferAmount, tLiquidity);
    }

    function _getTValues(address sender, address recipient, uint256 tAmount) private view returns (uint256, uint256) {
        uint256 tLiquidity = calculateFee(sender, recipient, tAmount);
        uint256 tTransferAmount = tAmount.sub(tLiquidity);
        return (tTransferAmount, tLiquidity);
    }

    function _getRValues(uint256 tAmount, uint256 tLiquidity, uint256 currentRate) private pure returns (uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rLiquidity = tLiquidity.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rLiquidity);
        return (rAmount, rTransferAmount);
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return ( _rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return ( _rTotal, _tTotal);
        return (rSupply, _tTotal);
    }
    
    function calculateFee(address sender, address recipient, uint256 tAmount) private view returns (uint256) {
        if(_isExchange[recipient] == true){
            return tAmount.mul(_sellTotalTaxPercent).div(
                10**2
            );
        }else if(_isExchange[sender] == true){
            return tAmount.mul(_buyTotalTaxPercent).div(
                10**2
            );
        }else{
            return tAmount.mul(_totalTaxPercent).div(
                10**2
            );
        }
    }

    function removeAllFee() private {
        _prevTotalTaxPercent = _totalTaxPercent;
        _totalTaxPercent = 0;
        
        _sellPrevTotalTaxPercent = _sellTotalTaxPercent;
        _sellTotalTaxPercent = 0;
        
        _buyPrevTotalTaxPercent = _buyTotalTaxPercent;
        _buyTotalTaxPercent = 0;
    }
    
    function restoreAllFee() private {
        _totalTaxPercent = _prevTotalTaxPercent;
        
        _sellTotalTaxPercent = _sellPrevTotalTaxPercent;
        
        _buyTotalTaxPercent = _buyPrevTotalTaxPercent;
    }

    function isExcludedFromFee(address account) public view returns(bool) {
        return _isExcludedFromFee[account];
    }
    
    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }
    
    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }
    
    function isExchange(address account) public view returns(bool) {
        return _isExchange[account];
    }
    
    function setExchange(address account) public onlyOwner {
        _isExchange[account] = true;
    }
    
    function RemoveExchange(address account) public onlyOwner {
        _isExchange[account] = false;
    }    
    
    function setTaxes(uint256 newBurnFee, uint256 newLiquidityTax, uint256 newMarketingTax, uint256 newWraithProtocolTax) external onlyOwner() {
        require(newBurnFee+newLiquidityTax+newMarketingTax+newWraithProtocolTax < 30, "Taxes cannot exceed 30% by default");
        
        _burnFee = newBurnFee;
        _liquidityFee = newLiquidityTax;
        _MarketingFee = newMarketingTax;
        _wraithProtocolFee = newWraithProtocolTax;
        _totalTaxPercent = _burnFee.add(_liquidityFee).add(_MarketingFee).add(_wraithProtocolFee);
        _prevTotalTaxPercent = _totalTaxPercent;
    }

    function setSellTaxes(uint256 newBurnFee, uint256 newLiquidityTax, uint256 newMarketingTax, uint256 newWraithProtocolTax) external onlyOwner() {
        require(newBurnFee+newLiquidityTax+newMarketingTax+newWraithProtocolTax < 30, "Sell taxes cannot exceed 30% by default");
        
        _sellBurnFee = newBurnFee;
        _sellLiquidityFee = newLiquidityTax;
        _sellMarketingFee = newMarketingTax;
        _sellWraithProtocolFee = newWraithProtocolTax;
        _sellTotalTaxPercent = _sellBurnFee.add(_sellLiquidityFee).add(_sellMarketingFee).add(_sellWraithProtocolFee);
        _sellPrevTotalTaxPercent = _sellTotalTaxPercent;
    }

    function setBuyTaxes(uint256 newBurnFee, uint256 newLiquidityTax, uint256 newMarketingTax, uint256 newWraithProtocolTax) external onlyOwner() {
        require(newBurnFee+newLiquidityTax+newMarketingTax+newWraithProtocolTax < 30, "Buy taxes cannot exceed 30% by default");
        
        _buyBurnFee = newBurnFee;
        _buyLiquidityFee = newLiquidityTax;
        _buyMarketingFee = newMarketingTax;
        _buyWraithProtocolFee = newWraithProtocolTax;
        _buyTotalTaxPercent = _buyBurnFee.add(_buyLiquidityFee).add(_buyMarketingFee).add(_buyWraithProtocolFee);
        _buyPrevTotalTaxPercent = _buyTotalTaxPercent;
    }

    function setMaxTxAmount(uint256 maxTxAmount) external onlyOwner() {
        require(maxTxAmount.mul(100).div(_tTotal) > 5, "MaxTxAmount has to be at least 5% of total supply");
        _maxTxAmount = maxTxAmount;
    }

    function setNumTokensBeforeSwap(uint256 newLimit) external onlyOwner() {
        minimumTokensBeforeSwap = newLimit;
    }

    function setMarketingWalletAddress(address newAddress) external onlyOwner() {
