pragma solidity ^0.8.12;



abstract contract Context 
{
    function _msgSender() internal view virtual returns (address) 
    {
        return msg.sender;
    }
    function _msgData() internal view virtual returns (bytes calldata) 
    {
        this; 
        return msg.data;
    }
}

abstract contract OwnableV2 is Context
{
    address _owner;
    address public _newOwner;
    constructor()  
    {
        _owner = payable(msg.sender);
    }
    modifier onlyOwner() 
    {
        require(_msgSender() == _owner, "Only owner");
        _;
    }
    function changeOwner(address newOwner) onlyOwner public
    {
        _newOwner = newOwner;
    }
    function confirm() public
    {
        require(_newOwner == msg.sender);
        _owner = _newOwner;
    }
}



abstract contract ContractOwnerV2 is OwnableV2
{
    address _contractOwner;

    modifier isContractOwner()
    {
        require(msg.sender == _contractOwner, "no access");
        _;
    }
    function setContractOwner(address contractOwner) onlyOwner public 
    {
        _contractOwner = contractOwner;
    }
}


interface IDepositeStorage
{
    function getDeposite(address acc) external view returns (uint);
    function getDepDate (address acc) external view returns(uint);
    function addDep(address acc, uint amount) external;
    function subDep(address acc, uint amount) external;
    function setDepDate(address acc, uint date) external;
}

contract DepositeStorageV2 is IDepositeStorage, ContractOwnerV2
{
    mapping (address => uint) private _deposites;
    mapping (address => uint) private _depositeDate;

    function getDeposite(address acc) public  override view returns (uint) 
    {
        return _deposites[acc] ;
    } 
    function getDepDate(address acc) public  override view returns(uint) 
    {
        return _depositeDate[acc] ;
    } 


    function addDep(address acc, uint amount) isContractOwner public  override
    {
       _deposites[acc] += amount;
    } 
    function subDep(address acc, uint amount) isContractOwner public  override
    {
        if (_deposites[acc] > amount)
        {
           _deposites[acc] -= amount;
        }
        else
        {
            _deposites[acc] = 0;
        }
    } 
    function setDepDate(address acc, uint date) isContractOwner public  override 
    {
        _depositeDate[acc] = date;
    } 
}
