interface IPetStore {
    enum PublicSaleRarity {
        Common,
        Rare
    }

    struct AllowlistConfig {
        bool open;
        uint64 totalSupply;
    }

    struct PublicSaleConfig {
        bool open;
        uint64 totalSupply;
        uint64 individualSupply;
        uint64 individualBulkPurchase;
        uint256 commonPrice;
        uint256 rarePrice;
    }

    struct PackedMintedAmount {
        uint64 freeMint;
        uint64 publicSale;
        uint64 sheepAllowlist;
        uint64 apeAllowlist;
    }

    error MintNotOpen();
    error SignatureInvalid();
    error SignatureExpired();
    error SignatureAlreadyUsed();
    error ExceedMintTotalSupply();
    error ExceedMintIndividualSupply();
    error ExceedBulkPurchaseAmount();
    error InsufficientPayment();
    error OrderAlreadyFullFilled();
    error ZeroQuantityNotAllowed();

    event ApprovalSignerUpdated(address signer);
    event FreeMintConfigUpdated(AllowlistConfig config);
    event SheepAllowlistConfigUpdated(AllowlistConfig config);
    event ApeAllowlistConfigUpdated(AllowlistConfig config);
    event PublicSaleConfigUpdated(PublicSaleConfig config);
    event FreeMintExecuted(address wallet, uint256[] tokenIds);
    event SheepAllowlistMintExecuted(address wallet, uint256[] tokenIds);
    event ApeAllowlistMintExecuted(address wallet, uint256[] tokenIds);
    event PublicSaleExecuted(address wallet, uint256[] tokenIds, uint256 rarity);
    event UserCheckIn(uint256 ticketId, address wallet);

    function freeMint(
        uint256 expireAt,
        uint256 quantity,
        uint256 maxQuantity,
        bytes calldata signature
    ) external;

    function sheepAllowlistMint(
        uint256 expireAt,
        uint256 quantity,
        uint256 maxQuantity,
        bytes calldata signature
    ) external;

    function apeAllowlistMint(
        uint256 expireAt,
        uint256 quantity,
        uint256 maxQuantity,
        bytes calldata signature
    ) external;

    function publicSale(uint256 quantity, PublicSaleRarity rarity) external payable;
}
