    "src/PetStore/PetStore.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {Withdrawable} from "../shared/Withdrawable.sol";
import {IMergePet} from "../shared/IMergePet.sol";
import {IPetStore} from "./IPetStore.sol";

contract PetStore is Ownable, Pausable, EIP712, Withdrawable, IPetStore {
    IMergePet public petToken;
    address public approvalSigner;
    AllowlistConfig public freeMintConfig;
    AllowlistConfig public sheepAllowlistConfig;
    AllowlistConfig public apeAllowlistConfig;
    PublicSaleConfig public publicSaleConfig;
    PackedMintedAmount public totalMintedAmount;
    mapping(address => PackedMintedAmount) public individualMintedAmount;
    mapping(uint256 => bool) public usedCheckInTickets;
    bytes32 private constant _FREE_MINT_SIG_TYPE_HASH =
        keccak256("FreeMint(uint256 expireAt,address wallet,uint256 maxQuantity)");
    bytes32 private constant _APE_MINT_SIG_TYPE_HASH =
        keccak256("ApeAllowlistMint(uint256 expireAt,address wallet,uint256 maxQuantity)");
    bytes32 private constant _SHEEP_MINT_SIG_TYPE_HASH =
        keccak256("SheepAllowlistMint(uint256 expireAt,address wallet,uint256 maxQuantity)");
    bytes32 private constant _CHECK_IN_TYPE_HASH =
        keccak256("CheckIn(uint256 ticketId,uint256 expireAt,address wallet)");

    constructor(address pet_) EIP712("PetStore", "1") {
        petToken = IMergePet(pet_);
    }

    modifier checkQuantity(uint256 quantity) {
        if (quantity == 0) {
            revert ZeroQuantityNotAllowed();
        }
        _;
    }

    function setApprovalSigner(address signer) external onlyOwner {
        approvalSigner = signer;
        emit ApprovalSignerUpdated(signer);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function setFreeMintConfig(AllowlistConfig calldata config) external onlyOwner {
        freeMintConfig = config;
        emit FreeMintConfigUpdated(config);
    }

    function setSheepAllowlistConfig(AllowlistConfig calldata config) external onlyOwner {
        sheepAllowlistConfig = config;
        emit SheepAllowlistConfigUpdated(config);
    }

    function setApeAllowlistConfig(AllowlistConfig calldata config) external onlyOwner {
        apeAllowlistConfig = config;
        emit ApeAllowlistConfigUpdated(config);
    }

    function setPublicSaleConfig(PublicSaleConfig calldata config) external onlyOwner {
        publicSaleConfig = config;
        emit PublicSaleConfigUpdated(config);
    }

    function freeMint(
        uint256 expireAt,
        uint256 quantity,
        uint256 maxQuantity,
        bytes calldata signature
    ) external whenNotPaused checkQuantity(quantity) {
        if (!freeMintConfig.open) {
            revert MintNotOpen();
        }
        if (expireAt < block.timestamp) {
            revert SignatureExpired();
        }
        if (
            !_verifyAllowlistMintSignature(
                _FREE_MINT_SIG_TYPE_HASH,
                expireAt,
                maxQuantity,
                signature
            )
        ) {
            revert SignatureInvalid();
        }
        uint64 i64Quantity = uint64(quantity);
        if (individualMintedAmount[_msgSender()].freeMint + i64Quantity > maxQuantity) {
            revert ExceedMintIndividualSupply();
        }
        if (totalMintedAmount.freeMint + i64Quantity > freeMintConfig.totalSupply) {
            revert ExceedMintTotalSupply();
        }

        totalMintedAmount.freeMint += i64Quantity;
        individualMintedAmount[_msgSender()].freeMint += i64Quantity;
        uint256[] memory tokenIds = petToken.mintBatch(_msgSender(), quantity);
        emit FreeMintExecuted(_msgSender(), tokenIds);
    }

    function sheepAllowlistMint(
        uint256 expireAt,
        uint256 quantity,
        uint256 maxQuantity,
        bytes calldata signature
    ) external whenNotPaused checkQuantity(quantity) {
        if (!sheepAllowlistConfig.open) {
            revert MintNotOpen();
        }
        if (expireAt < block.timestamp) {
            revert SignatureExpired();
        }
        if (
            !_verifyAllowlistMintSignature(
                _SHEEP_MINT_SIG_TYPE_HASH,
                expireAt,
                maxQuantity,
                signature
            )
        ) {
            revert SignatureInvalid();
        }
        uint64 i64Quantity = uint64(quantity);
        if (individualMintedAmount[_msgSender()].sheepAllowlist + i64Quantity > maxQuantity) {
            revert ExceedMintIndividualSupply();
        }
        if (totalMintedAmount.sheepAllowlist + i64Quantity > sheepAllowlistConfig.totalSupply) {
            revert ExceedMintTotalSupply();
        }

        totalMintedAmount.sheepAllowlist += i64Quantity;
        individualMintedAmount[_msgSender()].sheepAllowlist += i64Quantity;
        uint256[] memory tokenIds = petToken.mintBatch(_msgSender(), quantity);
        emit SheepAllowlistMintExecuted(_msgSender(), tokenIds);
    }

    function apeAllowlistMint(
        uint256 expireAt,
        uint256 quantity,
        uint256 maxQuantity,
        bytes calldata signature
    ) external whenNotPaused checkQuantity(quantity) {
        if (!apeAllowlistConfig.open) {
            revert MintNotOpen();
        }
        if (expireAt < block.timestamp) {
            revert SignatureExpired();
        }
        if (
            !_verifyAllowlistMintSignature(
                _APE_MINT_SIG_TYPE_HASH,
                expireAt,
                maxQuantity,
                signature
            )
        ) {
            revert SignatureInvalid();
        }
        uint64 i64Quantity = uint64(quantity);
        if (totalMintedAmount.apeAllowlist + i64Quantity > apeAllowlistConfig.totalSupply) {
            revert ExceedMintTotalSupply();
        }
        if (individualMintedAmount[_msgSender()].apeAllowlist + i64Quantity > maxQuantity) {
            revert ExceedMintIndividualSupply();
        }

        totalMintedAmount.apeAllowlist += i64Quantity;
        individualMintedAmount[_msgSender()].apeAllowlist += i64Quantity;
        uint256[] memory tokenIds = petToken.mintBatch(_msgSender(), quantity);
        emit ApeAllowlistMintExecuted(_msgSender(), tokenIds);
    }

    function publicSale(
        uint256 quantity,
        PublicSaleRarity rarity
    ) external payable whenNotPaused checkQuantity(quantity) {
        if (!publicSaleConfig.open) {
            revert MintNotOpen();
        }
        uint256 price = rarity == PublicSaleRarity.Rare
            ? publicSaleConfig.rarePrice
            : publicSaleConfig.commonPrice;
        if (msg.value < price * quantity) {
            revert InsufficientPayment();
        }
        uint64 i64Quantity = uint64(quantity);
        if (i64Quantity > publicSaleConfig.individualBulkPurchase) {
            revert ExceedBulkPurchaseAmount();
        }
        if (totalMintedAmount.publicSale + i64Quantity > publicSaleConfig.totalSupply) {
            revert ExceedMintTotalSupply();
        }
        if (
            individualMintedAmount[_msgSender()].publicSale + i64Quantity >
            publicSaleConfig.individualSupply
        ) {
            revert ExceedMintIndividualSupply();
        }

        totalMintedAmount.publicSale += i64Quantity;
        individualMintedAmount[_msgSender()].publicSale += i64Quantity;
        uint256[] memory tokenIds = petToken.mintBatch(_msgSender(), quantity);
        emit PublicSaleExecuted(_msgSender(), tokenIds, uint256(rarity));
    }

    function checkIn(
        uint256 ticketId,
        uint256 expireAt,
        bytes calldata signature
    ) external whenNotPaused {
        if (expireAt < block.timestamp) {
            revert SignatureExpired();
        }
        if (usedCheckInTickets[ticketId]) {
            revert SignatureAlreadyUsed();
        }
        if (!_verifyCheckInSignature(ticketId, expireAt, signature)) {
            revert SignatureInvalid();
        }

        usedCheckInTickets[ticketId] = true;
        emit UserCheckIn(ticketId, _msgSender());
    }

    function _verifyAllowlistMintSignature(
        bytes32 typeHash,
        uint256 expireAt,
        uint256 maxQuantity,
        bytes calldata signature
    ) private view returns (bool) {
        bytes32 digest = _hashTypedDataV4(
            keccak256(abi.encode(typeHash, expireAt, _msgSender(), maxQuantity))
        );
        return ECDSA.recover(digest, signature) == approvalSigner;
    }

    function _verifyCheckInSignature(
        uint256 ticketId,
        uint256 expireAt,
        bytes calldata signature
    ) private view returns (bool) {
        bytes32 digest = _hashTypedDataV4(
            keccak256(abi.encode(_CHECK_IN_TYPE_HASH, ticketId, expireAt, _msgSender()))
        );
        return ECDSA.recover(digest, signature) == approvalSigner;
    }
}
"
    },
