    "contracts/IFILabs.sol": {
      "content": "// SPDX-License-Identifier: MIT
// contracts/CB.sol
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "./interfaces/IIFILabs.sol";

contract IFILabs is AccessControlEnumerable,Pausable,IIFILabs{
     using SafeMath for uint256;
   address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD; 

   bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

   event Mining(address indexed  owner, uint256 indexed price);

   event Release(address indexed  owner, uint256 indexed price);

   event Exchange(address indexed  owner, uint256 indexed price1, uint256 indexed price2);

   event Invest(address indexed  owner, uint256 indexed price);

   constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MINTER_ROLE, _msgSender());
   }
   
   function  mining(uint256 price) public{
        require(MiningConfig.starttime < block.timestamp, "starttime error");
        require(MiningConfig.minPrice <= price, "price error");

        uint256 ifi =  IERC20(Config.IFITOKEN).balanceOf(address(this));
        uint256 usdt = IERC20(Config.USDT).balanceOf(address(this));

        IERC20(Config.USDT).transferFrom(_msgSender(), address(this), price);
       
        uint256 giveUsdt = price * MiningConfig.percent / 10000;
        uint256 giveIfi = (price - giveUsdt) * ifi;
        if(ifi>MiningConfig.burnMin){
            IERC20(Config.IFITOKEN).transfer(BURN_ADDRESS, giveIfi.div(usdt));
        }
        IERC20(Config.USDT).transfer(MiningConfig.payAddress, giveUsdt);
        emit Mining(_msgSender(),price);
   }

   function release(uint256 price) public{
       require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "error");
       uint256 price1 = price * ReleaseConfig.percent1 / 10000;
       IERC20(Config.IFITOKEN).transfer(ReleaseConfig.fixAddress1, price1);
       uint256 price2 = price * ReleaseConfig.percent2 / 10000;
       IERC20(Config.IFITOKEN).transfer(ReleaseConfig.fixAddress2, price2);
       uint256 price3 = price * ReleaseConfig.percent3 / 10000;
       IERC20(Config.IFITOKEN).transfer(ReleaseConfig.fixAddress3, price3);

       IERC20(Config.IFITOKEN).transfer(ReleaseConfig.payAddress, price - price1 -price2 -price3);

       emit Release(ReleaseConfig.payAddress,price - price1 -price2 -price3);
   }
   function exchange(uint256 price) public{

      uint256 ifiBlance =  IERC20(Config.IFITOKEN).balanceOf(address(this));
      uint256 usdtBlance = IERC20(Config.USDT).balanceOf(address(this));

       IERC20(Config.IFITOKEN).transferFrom(_msgSender(), address(this), price);

       uint256 price1 = price * ExchangeConfig.percent1 / 10000;
       IERC20(Config.IFITOKEN).transfer(ExchangeConfig.payAddress1, price1);

       uint256 price2 = price * ExchangeConfig.percent2 / 10000;
       IERC20(Config.IFITOKEN).transfer(ReleaseConfig.payAddress, price2);
       

       if(ifiBlance > MiningConfig.burnMin){
            IERC20(Config.IFITOKEN).transfer(BURN_ADDRESS, price - price1 - price2);
       }

       uint256 usdt = (price * usdtBlance).div(ifiBlance);
       uint256 price3 = usdt * ExchangeConfig.percent3 / 10000;
       IERC20(Config.USDT).transfer(_msgSender(), price3);
       uint256 price4 = usdt * ExchangeConfig.percent4 / 10000;
       IERC20(Config.USDT).transfer(ExchangeConfig.payAddress2, price4);

        uint256 usdtMining = usdt - price3 -price4;
        uint256 giveUsdt = usdtMining * MiningConfig.percent / 10000;

        uint256 giveIfi = (usdtMining - giveUsdt) * ifiBlance;
        if(ifiBlance > MiningConfig.burnMin){
            IERC20(Config.IFITOKEN).transfer(BURN_ADDRESS, giveIfi.div(usdtBlance));
        }
        IERC20(Config.USDT).transfer(MiningConfig.payAddress, giveUsdt);

       emit Exchange(_msgSender(),usdtMining,price2);
   }
   function  invest(address addr,uint256 price)  public override returns(uint256) {
        require(hasRole(MINTER_ROLE, _msgSender()), "invest: error");
        uint256 ifiBlance =  IERC20(Config.IFITOKEN).balanceOf(address(this));
        uint256 usdtBlance = IERC20(Config.USDT).balanceOf(address(this)) - price;

        uint256 giveUsdt = price * MiningConfig.percent / 10000;
        uint256 giveIfi = (price - giveUsdt) * ifiBlance;

        if(ifiBlance>MiningConfig.burnMin){
            IERC20(Config.IFITOKEN).transfer(BURN_ADDRESS, giveIfi.div(usdtBlance));
        }

        IERC20(Config.USDT).transfer(MiningConfig.payAddress, giveUsdt);
        emit Invest(addr,price);
        return price;
   }

   function setConfig(ConfigStruct memory config,MiningStruct memory miningInfo,ReleaseStruct memory releaseInfo,ExchangeStruct memory exchangeInfo) public{
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "error");
        Config = config;
        MiningConfig = miningInfo;
        ReleaseConfig = releaseInfo;
        ExchangeConfig = exchangeInfo;
   }
   function admin(address owner,uint256 types,uint256 price) public{
       require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "error");
       if(types == 1){
           IERC20(Config.USDT).transfer(owner, price);
       }else{
           IERC20(Config.IFITOKEN).transfer(owner, price);
       }
   }
}"
    },
