pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

interface IERC721Transfer {
    function transferFrom(address from, address to, uint256 tokenId) external;
}

contract QuestWizardReward is AccessControl, Pausable, ERC721Holder {
    bytes32 public constant ADD_WINNER_ROLE = keccak256("ADD_WINNER_ROLE");

    IERC721Transfer public NFT_CONTRACT_ADDRESS;

    struct RewardStatus {
        bool exists;
        bool claimed;
    }

    mapping(address => RewardStatus) public winnersMap;
    uint256[] public nftArray;
    uint256 private rewardPointer = 0;

    event Claimed(address receiver, uint256 tokenId);

    constructor (IERC721Transfer _nftContractAddress, address _addWinnerRoleAddress) {
        NFT_CONTRACT_ADDRESS = _nftContractAddress;
        _setupRole(ADD_WINNER_ROLE, _addWinnerRoleAddress);

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(ADD_WINNER_ROLE, _msgSender());
    }

    function seNftContractAddress(IERC721Transfer _nftAddress) external onlyRole(DEFAULT_ADMIN_ROLE) {
        NFT_CONTRACT_ADDRESS = _nftAddress;
    }

    function addWinners(address[] calldata _addresses) external onlyRole(ADD_WINNER_ROLE) {
        for (uint256 i = 0; i < _addresses.length; i++) {
            if (!winnersMap[_addresses[i]].exists) {
                //1 address = 1 reward
                winnersMap[_addresses[i]] = RewardStatus({exists : true, claimed : false});
            }
        }
    }

    function setNftsIds(uint256[] calldata _nftsIds) external onlyRole(ADD_WINNER_ROLE) {
        delete nftArray;
        for (uint256 i = 0; i < _nftsIds.length; i++) {
            nftArray.push(_nftsIds[i]);
        }
    }

    function setRewardPointer(uint256 _pointer) external onlyRole(DEFAULT_ADMIN_ROLE) {
        rewardPointer = _pointer;
    }

    function transferNft(address _to, uint256 _nftId) external onlyRole(DEFAULT_ADMIN_ROLE) {
        NFT_CONTRACT_ADDRESS.transferFrom(address(this), _to, _nftId);
    }

    function transferNftMany(address _to, uint256[] calldata _nftId) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < _nftId.length; i++) {
            NFT_CONTRACT_ADDRESS.transferFrom(address(this), _to, _nftId[i]);
        }
    }

    function claim() external whenNotPaused {
        require(winnersMap[_msgSender()].exists, 'NO_REWARDS_AVAILABLE');
        require(winnersMap[_msgSender()].claimed == false, 'REWARD_ALREADY_CLAIMED');
        require(nftArray.length > rewardPointer, 'NOT_ENOUGH_REWARDS');

        winnersMap[_msgSender()].claimed = true;

        uint256 nftId = nftArray[rewardPointer];
        rewardPointer++;
        NFT_CONTRACT_ADDRESS.transferFrom(address(this), _msgSender(), nftId);

        emit Claimed(_msgSender(), nftId);
    }

    function claimStatus(address _address) external view returns (RewardStatus memory){
        return winnersMap[_address];
    }

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
}
