// File: BuyPower/contracts/Meta-project/rewardUvt.sol


pragma solidity ^0.8.4;





interface IMetaSale {
    function getUserEquity(address user)external view returns (uint256 userequity , uint256 allQuity);
}

interface IFinance {
    function sendToken(address _to, uint256 _amount ,address _token) external;
}

contract RewardUvt is Ownable{
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    IMetaSale public metaSaleAddre;
    IFinance public financeAddre;
    IERC20 public uvtToken;
    /*
    *Last dividend time
    */
    uint256 public latestTime;
    /*
    *Intervals
    */
    uint256 immutable public  intervalTime = 1 days;
    /*
    *Cumulative amount of transferred Uvt
    */
    uint256 public totalQuota;
     /*
    *Cumulative number of Uvt received
    */
    uint256 public haveTotalclaim;
     /*
    *Dividend ratio
    */
    uint256 public REWARD_RATE;
    /*
    *Cumulative amount claimed by users
    */
    mapping (address =>uint256) public userTotalclaim;
    /*
    *The contract balance after the user applies
    */
    mapping (address =>uint256) public latestBalance;
    
    event ClaimReward(address user ,uint256 amount , uint256 oldTotalQuota,uint256 totalQuota ,uint256 userequity ,uint256 allQuity);
    event SendUvtToself(address to , uint256 amount);
    constructor(IMetaSale _metaSale ,IFinance _financeAddre){
         metaSaleAddre = _metaSale;
         latestTime = block.timestamp;
         financeAddre = _financeAddre;
         REWARD_RATE =1;
         uvtToken = IERC20(0x196Eb1D21C05CC265Ea0a1479E924E7983467838);
    }
    modifier canClaimReward(){
        require(msg.sender == address(metaSaleAddre), "caleUserquota:Unavailable user");
        _;
    }
    function setReward_rate(uint256 _rate)external onlyOwner returns(bool){
        require(_rate >0 ,"setReward_rate:rate is zone");
        REWARD_RATE = _rate;
        return true;
    }

    function setMetaSale(IMetaSale _metaSale) external onlyOwner returns (bool){
        require(address(_metaSale) != address(0), "setMetaSale:This address is not supported");
        metaSaleAddre = _metaSale;
        return true;
    }

    function setFinanceAddre(IFinance _financeAddre) external onlyOwner returns (bool){
        require(address(_financeAddre) != address(0), "setFinanceAddre:This address is not supported");
        financeAddre = _financeAddre;
        return true;
    }
    function claimReward()external returns(bool){
        if (block.timestamp.sub(latestTime) >= intervalTime){
            sendUvtToself();
        }
        uint256 oldtotalQuota = latestBalance[msg.sender];
        uint256 userClaimQuota = getClaimQuota();
        require(userClaimQuota > 0 ,"Insufficient claim amount");
        haveTotalclaim += userClaimQuota;
        userTotalclaim[msg.sender] += userClaimQuota;
        uvtToken.safeTransfer(msg.sender, userClaimQuota);
        latestBalance[msg.sender] = totalQuota;
        (uint256 userequity , uint256 allQuity) =metaSaleAddre.getUserEquity(msg.sender);
        emit ClaimReward(msg.sender, userClaimQuota ,oldtotalQuota,totalQuota ,userequity ,allQuity);
        return true;
    }
    
    function calcUserquota(address user)external canClaimReward returns(bool)
    {
        (uint256 userequity , uint256 allQuity) =metaSaleAddre.getUserEquity(user);
         if (block.timestamp.sub(latestTime) >= intervalTime){
            sendUvtToself();
         }
         if (userequity > 0 ){
           if (totalQuota > latestBalance[user]){
            uint256 oldtotalQuota = latestBalance[user];
            uint256  userClaimQuota = (totalQuota.sub(latestBalance[user])).mul(userequity).div(allQuity); 
            haveTotalclaim += userClaimQuota;
            userTotalclaim[user] += userClaimQuota;
            uvtToken.safeTransfer(user,userClaimQuota);
            latestBalance[user] = totalQuota;
            emit ClaimReward(user, userClaimQuota ,oldtotalQuota,totalQuota ,userequity ,allQuity);
           }
        }
        else {
            latestBalance[user] = totalQuota;
        }
        return true;
    }

    function getClaimQuota()public view returns (uint256 userClaimQuota)
    {
       uint256 currentTotalBalance =  totalQuota;
       uint256 balance = uvtToken.balanceOf(address(this));

       if (block.timestamp.sub(latestTime) >= intervalTime){
            uint256 banance = uvtToken.balanceOf(address(financeAddre));
            uint256 amount = banance.mul(REWARD_RATE).div(100);
            currentTotalBalance += amount;
            balance += amount;
       }
        (uint256 usereQuity , uint256 totallQuity) = getEquity();
        if (usereQuity == 0 || totallQuity == 0 ||  balance ==0){
            userClaimQuota = 0;
        }
        else {
            userClaimQuota = (currentTotalBalance.sub(latestBalance[msg.sender])).mul(usereQuity).div(totallQuity);
        }
    }
    
    function sendUvtToself()internal returns (bool){
        uint256 banance = uvtToken.balanceOf(address(financeAddre));
        if (banance > 0 ){
            uint256 amount = banance.mul(REWARD_RATE).div(100);
            totalQuota += amount;
            financeAddre.sendToken(address(this) , amount ,address(uvtToken));
            latestTime = block.timestamp;
            emit SendUvtToself(address(this), amount);
        }
        return true;
    }

