    "contracts/Brokie.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Interfaces/IPair.sol";
import "./Interfaces/IRouter.sol";
import "./Interfaces/IFactory.sol";
import "./Burner.sol";

contract Brokie is ERC20{
    using SafeERC20 for IERC20;

    address payable public owner;
    address public liqPool;
    address public router;
    address public colleteral;

    address public teamWallet;

    uint public lastAction;
    uint8 public currentFeeNominator = 50;
    uint constant public FEE_DENOMINATOR = 1000;

    bool public firstMint;

    Burner public burner;

    address[3] public path;

    bool public locked;

    // bsc mainnet
    address public constant ROUTER = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address public constant FACTORY = 0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73;
    address public constant WETH = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;

    // bsc testnet
    // address constant public ROUTER = 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3;
    // address constant public FACTORY = 0xB7926C0430Afb07AA7DEfDE6DA862aE0Bde767bc;
    // address constant public WETH = 0xae13d989daC2f0dEbFf460aC112a837C89BAa7cd;


    event Withdrawal(uint busdAmount, uint burnedBrokies);
    event Deposit(uint busdAmount, uint mintedBrokies);
    event AddCollateralToLp(uint mintedToLp);
    event BuybackAndBurn(uint colleteralInput);

    modifier onlyOnce() {
        require(!locked, "One function call per function call.");
        locked = true;
        _;
        locked = false;
    }

    constructor(address _collateral, address _teamWallet) ERC20("Brokie", "BRO") {
        lastAction = block.number;
        colleteral = _collateral;
        path = [_collateral, WETH, address(this)];
        burner = new Burner();
        teamWallet = _teamWallet;

    }

    function initialSupply(uint amount) external { 
        require(firstMint == false, "Already happend");
        require(msg.sender == teamWallet, "U R NOT TEAM");
        IERC20(colleteral).safeTransferFrom(msg.sender, address(this), amount);
        _mint(msg.sender, 69_420 * 10**18);
        firstMint = true;
    }

    function getPair(address one, address two) external view returns(address){
        return IFactory(FACTORY).getPair(one, two);
    }

    function setLiq(address newPool) external {
        require(liqPool == address(0), "Already set");
        liqPool = newPool;
    }

    function setTeamWallet(address newWallet) external {
        require(msg.sender == teamWallet, "You are not allowed to set.");
        teamWallet = newWallet;
    }

    function burn(uint amount) external {
        _burn(msg.sender, amount);
    }

    function deposit(uint inputAmount, uint minAmount) onlyOnce() external {

        uint beforeBalance = IERC20(address(this)).balanceOf(msg.sender);
        uint teamFee = inputAmount / FEE_DENOMINATOR;
        uint amount = inputAmount - teamFee;
        uint toMint = depositRatioWithoutFees(amount);

        currentFeeNominator = nextFee();
        uint256 fee = toMint * currentFeeNominator / FEE_DENOMINATOR;

        lastAction = block.number;

        uint userMint = toMint - fee;
        uint addToLp = fee / 2;
        _mint(msg.sender, userMint);
        _mint(liqPool, addToLp);
        IPair(liqPool).sync();

        IERC20(colleteral).safeTransferFrom(msg.sender, address(this), inputAmount);
        IERC20(colleteral).safeTransfer(teamWallet, teamFee);

        uint afterBalance = IERC20(address(this)).balanceOf(msg.sender);
        require(afterBalance - beforeBalance >= minAmount, "Output Amount does not fit given minAmount.");
        emit Deposit(inputAmount, userMint);
        emit AddCollateralToLp(addToLp);
        _burnLiq();
    }

    function setFee() public returns (uint8 feeNominator) {
        currentFeeNominator = nextFee();
        feeNominator = currentFeeNominator;
    }

    function nextFee() public view returns (uint8 feeNominator) {
        feeNominator = currentFeeNominator;
        uint deltaBlocks = block.number - lastAction;
        uint modBlock = deltaBlocks / 192;

        if (modBlock > 0){
            if (modBlock > feeNominator - 10){
                feeNominator = 10;
            } else {
                uint8 tempModBlock = uint8(modBlock);
                feeNominator = feeNominator - tempModBlock;
                if (feeNominator < 10) {
                    feeNominator = 10;
                }
            }
        } else {
            if (feeNominator < 100){
                feeNominator += 1;
            }
        }

    }

    function depositRatio(uint inputAmount) public view returns(uint) {
        uint amount = inputAmount - (inputAmount / FEE_DENOMINATOR);
        uint withoutFee = depositRatioWithoutFees(amount);
        uint fee = nextFee();
        return withoutFee - (withoutFee * fee / FEE_DENOMINATOR);
    }

    function depositRatioWithoutFees(uint amount) public view returns(uint) {
        uint ratio = amount * 10**18 / IERC20(colleteral).balanceOf(address(this));
        return ratio * totalSupply() / 10**18;

    }

    function withdrawRatioWithoutFees(uint amount) public view returns(uint) {
        uint ratio = amount * 1e18 / totalSupply();
        return ratio * IERC20(colleteral).balanceOf(address(this)) / 1e18;
    }

    function withdrawRatio(uint amount) public view returns(uint) {
        uint withoutFee = withdrawRatioWithoutFees(amount);
        return withoutFee - (withoutFee / FEE_DENOMINATOR) - (withoutFee * nextFee() / FEE_DENOMINATOR);

    }

    function withdraw(uint amount, uint minAmount) onlyOnce() external {
        uint beforeBalance = IERC20(colleteral).balanceOf(msg.sender);
        uint toWithdrawl = withdrawRatioWithoutFees(amount);
        uint teamFee = toWithdrawl / FEE_DENOMINATOR;
        currentFeeNominator = nextFee();
        uint fee = (toWithdrawl - teamFee) * currentFeeNominator / FEE_DENOMINATOR;
        lastAction = block.number;
        _burn(msg.sender, amount);

        IERC20(colleteral).safeTransfer(msg.sender, toWithdrawl - teamFee - fee);
        IERC20(colleteral).safeTransfer(teamWallet, teamFee);

        IERC20(colleteral).approve(ROUTER, fee);

        address[] memory tempPath0 = new address[](3);
        tempPath0[0] = path[0];
        tempPath0[1] = path[1];
        tempPath0[2] = path[2];

        IRouter(ROUTER).swapExactTokensForTokensSupportingFeeOnTransferTokens(
            fee,
            0,
            tempPath0,
            address(burner),
            block.timestamp + 20
        );
        uint afterBalance = IERC20(colleteral).balanceOf(msg.sender);
        uint withdrawAmount = afterBalance - beforeBalance;
        require(withdrawAmount >= minAmount, "Slippage to high");
        _burnLiq();
        emit BuybackAndBurn(fee);
        emit Withdrawal(withdrawAmount, amount);
    }

    function _burnLiq() internal {
        uint bal = balanceOf(liqPool);
        uint percentage =  totalSupply() / 5;
        if (bal > percentage){
            _burn(liqPool, bal - percentage);
            IPair(liqPool).sync();
        }
        burner.burn();
    }

}
"
