contract CryptoBattle_Token is Context, IERC20, Ownable {
    using SafeMath for uint256;
    address internal zeroAddress = 0x0000000000000000000000000000000000000000;
    address private dead = 0x000000000000000000000000000000000000dEaD;

    uint256 public maxTaxFee = 5;
    uint256 public maxDevFee = 5;
    uint256 public maxSellTaxFee = 5;

    address[] private _excluded;

    address public _projectWalletAddress; // development wallet here
    address public _marketingWalletAddress; // marketing wallet here
    address public _fundWalletAddress; // fund wallet here

    address public router;
    address public basePair;

    string private _name = "Crypto Battle Token";
    string private _symbol = "CBT";
    uint8 private _decimals = 18;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 10_000_000 * 10 ** _decimals;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));

    uint256 private _tFeeTotal;

    uint256 public _taxFee = 3;
    uint256 private _previousTaxFee = _taxFee;

    uint256 public _devFee = 3;
    uint256 private _previousDevFee = _devFee;

    uint256 public _sellTaxFee = 3;
    uint256 private _previousSellTaxFee = _sellTaxFee;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled;

    uint256 public numTokensSellToFee = _tTotal.mul(1).div(10000); //0.01%;
    uint256 public valueNativeSellToFee = 0.05 ether; //0.05 BNB

    bool public sellWithOracle = false;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isExcluded;
    mapping(address => bool) private _isdevWallet;

    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquifyAmountUpdated(uint256 amount);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    event UpdateSwapV2(address indexed newAddress, address indexed oldAddress);

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor(address marketingWalletAddress_, address fundWalletAddress_) {
        router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        basePair = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
        _rOwned[msg.sender] = _rTotal;

        _projectWalletAddress = msg.sender;
        _marketingWalletAddress = marketingWalletAddress_;
        _fundWalletAddress = fundWalletAddress_;

        swapAndLiquifyEnabled = true;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(router);
        // Create a uniswap pair for this new token
        uniswapV2Pair = UniSwapFactory(_uniswapV2Router.factory()).createPair(
            address(this),
            basePair
        );

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;

        //exclude from rewards pair address, burn address and zero address
        _isExcluded[router] = true;
        _isExcluded[uniswapV2Pair] = true;
        _isExcluded[dead] = true;
        _isExcluded[zeroAddress] = true;

        //exclude owner and this contract from fee
        _isExcludedFromFee[msg.sender] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[_marketingWalletAddress] = true;
        _isExcludedFromFee[_fundWalletAddress] = true;

        //set wallet provided to true
        _isdevWallet[msg.sender] = true;

        emit Transfer(address(0), msg.sender, _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function reflectionFromToken(
        uint256 tAmount,
        bool deductTransferFee
    ) public view returns (uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount, , , , , ) = _getValues(tAmount);
            return rAmount;
        } else {
            (, uint256 rTransferAmount, , , , ) = _getValues(tAmount);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(
        uint256 rAmount
    ) public view returns (uint256) {
        require(
            rAmount <= _rTotal,
            "Amount must be less than total reflections"
        );
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner {
        require(!_isExcluded[account], "Account is already excluded");
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeInReward(address account) external onlyOwner {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function excludeFromFee(address account) public onlyOwner {
        require(!_isExcludedFromFee[account], "Account is already excluded");
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        require(_isExcludedFromFee[account], "Account is already included");
        _isExcludedFromFee[account] = false;
    }

    function setTaxFeePercent(uint256 taxFee) external onlyOwner {
        require(taxFee >= 0 && taxFee <= maxTaxFee, "taxFee out of range");
        _taxFee = taxFee;
        _previousTaxFee = _taxFee;
    }

    function setDevFeePercent(uint256 devFee) external onlyOwner {
        require(devFee >= 0 && devFee <= maxDevFee, "teamFee out of range");
        _devFee = devFee;
        _previousDevFee = _devFee;
    }

    function setSellTaxFeePercent(uint256 sellTaxFee) external onlyOwner {
        require(
            sellTaxFee >= 0 && sellTaxFee <= maxSellTaxFee,
            "taxFee out of range"
        );
        _sellTaxFee = sellTaxFee;
        _previousSellTaxFee = _sellTaxFee;
    }

    function setprojectWalletAddress(address _addr) internal virtual {
        if (!_isExcludedFromFee[_addr]) {
            excludeFromFee(_addr);
        }
        _isdevWallet[_addr] = true;
        _projectWalletAddress = _addr;
    }

    function replaceprojectWalletAddress(
        address _addr,
        address _newAddr
    ) public onlyOwner {
        require(_isdevWallet[_addr], "Wallet address not set previously");
        require(!_isdevWallet[_newAddr], "Wallet address already set");
        if (_isExcludedFromFee[_addr]) {
            includeInFee(_addr);
        }
        _isdevWallet[_addr] = false;
        setprojectWalletAddress(_newAddr);
    }

    function setmarketingWalletAddress(address _addr) public {
        require(
            _msgSender() == _marketingWalletAddress || _msgSender() == owner(),
            "Only marketing wallet can set this"
        );
        require(_addr != address(0), "Zero address not allowed");
        if (!_isExcludedFromFee[_addr]) {
            excludeFromFee(_addr);
        }
        _marketingWalletAddress = _addr;
    }

    function setfundWalletAddress(address _addr) public {
        require(
            _msgSender() == _fundWalletAddress || _msgSender() == owner(),
            "Only fund wallet can set this"
        );
        require(_addr != address(0), "Zero address not allowed");
        if (!_isExcludedFromFee[_addr]) {
            excludeFromFee(_addr);
        }
        _fundWalletAddress = _addr;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    function setSwapBackSettings(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Swapback amount should be at least 0");
        numTokensSellToFee = _amount;
        emit SwapAndLiquifyAmountUpdated(_amount);
    }

    function setSwapBackSettingsETH(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Swapback amount should be at least 0");
        valueNativeSellToFee = _amount;
        emit SwapAndLiquifyAmountUpdated(_amount);
    }

    function setSellWithOracle(bool _status) external onlyOwner {
        require(_status != sellWithOracle, "Already set");
        sellWithOracle = _status;
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        if (rFee > 0) {
            _rTotal = _rTotal.sub(rFee);
        }
        if (tFee > 0) {
            _tFeeTotal = _tFeeTotal.add(tFee);
        }
    }

    function _getValues(
        uint256 tAmount
    )
        private
        view
        returns (uint256, uint256, uint256, uint256, uint256, uint256)
    {
        (uint256 tTransferAmount, uint256 tFee, uint256 tDev) = _getTValues(
            tAmount
        );
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(
            tAmount,
            tFee,
            tDev,
            _getRate()
        );
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee, tDev);
    }

    function _getTValues(
        uint256 tAmount
    ) private view returns (uint256, uint256, uint256) {
        uint256 tFee = calculateTaxFee(tAmount);
        uint256 tDev = calculateDevFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tFee).sub(tDev);
        return (tTransferAmount, tFee, tDev);
    }

    function _getRValues(
        uint256 tAmount,
        uint256 tFee,
        uint256 tDev,
        uint256 currentRate
    ) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rDev = tDev.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee).sub(rDev);
        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (
                _rOwned[_excluded[i]] > rSupply ||
                _tOwned[_excluded[i]] > tSupply
            ) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function _takeDev(uint256 tDev) private {
        if (tDev == 0) return;
        uint256 currentRate = _getRate();
        uint256 rDev = tDev.mul(currentRate);
        _rOwned[address(this)] = _rOwned[address(this)].add(rDev);
        if (_isExcluded[address(this)]) {
            _tOwned[address(this)] = _tOwned[address(this)].add(tDev);
        }
        emit Transfer(_msgSender(), address(this), tDev);
    }

    function calculateTaxFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_taxFee).div(10 ** 2);
    }

    function calculateDevFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_devFee).div(10 ** 2);
    }

    function updateSwapV2Router(address newRouter) public onlyOwner {
        require(
            newRouter != address(uniswapV2Router),
            "The router already has that address"
        );
        require(newRouter != address(0), "Router cannot be the zero address");
        emit UpdateSwapV2(newRouter, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newRouter);
        router = newRouter;
        if (!_isExcluded[newRouter]) {
            _isExcluded[newRouter] = true;
        }
    }

    function updateSwapV2Pair(address newPair) public onlyOwner {
        require(
            newPair != address(uniswapV2Pair),
            "The pair already has that address"
        );
        require(newPair != address(0), "Pair cannot be the zero address");

        address _pairStatus = UniSwapFactory(uniswapV2Router.factory()).getPair(
            address(this),
            basePair
        );
        address _uniswapV2Pair = _pairStatus == zeroAddress
            ? UniSwapFactory(uniswapV2Router.factory()).createPair(
                address(this),
                basePair
            )
            : _pairStatus == newPair
            ? newPair
            : _pairStatus;

        uniswapV2Pair = _uniswapV2Pair;
        if (!_isExcluded[_uniswapV2Pair]) {
            _isExcluded[_uniswapV2Pair] = true;
        }
    }

    function updateBasePair(address newBasePair) public onlyOwner {
        require(
            newBasePair != address(basePair),
            "The base pair already has that address"
        );
        require(
            newBasePair != address(0),
            "Base pair cannot be the zero address"
        );

        basePair = newBasePair;
    }

    function removeAllFee() internal {
        if (_taxFee == 0 && _devFee == 0) return;

        _taxFee = 0;
        _devFee = 0;
        _sellTaxFee = 0;
    }

    function restoreAllFee() internal {
        _taxFee = _previousTaxFee;
        _devFee = _previousDevFee;
        _sellTaxFee = _previousSellTaxFee;
    }

    function zeroFees() public onlyOwner {
        removeAllFee();
    }

    function restoreFees() public onlyOwner {
        restoreAllFee();
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (!_isExcludedFromFee[from]) {
            require(amount > 0, "Transfer amount must be greater than zero");
        }

        //Special case when sell is uniswapV2Pair
        if (to == uniswapV2Pair) {
            _taxFee = _sellTaxFee;
        }

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));

        bool overMinTokenBalance = sellWithOracle
            ? getLatestPrice(contractTokenBalance, true) >= valueNativeSellToFee
            : contractTokenBalance >= numTokensSellToFee;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            //Swap to Fees
            swapAndSend(contractTokenBalance);
        }

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from, to, amount, takeFee);

        //reset tax fees
        restoreAllFee();
    }

    function swapAndSend(uint256 contractTokenBalance) private lockTheSwap {
        // swap tokens for ETH
        swapTokensForEth(contractTokenBalance); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // capture the contract's current ETH balance.
        uint256 nativeBalance = address(this).balance;
        uint256 sendFee = nativeBalance.div(3);

        //Send to project wallets
        payable(_projectWalletAddress).transfer(sendFee);
        payable(_marketingWalletAddress).transfer(sendFee);
        payable(_fundWalletAddress).transfer(sendFee);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> WHT
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = basePair;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        try
            uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
                tokenAmount,
                0, // accept any amount of ETH
                path,
                address(this),
                block.timestamp
            )
        {} catch (bytes memory) {
            try
                uniswapV2Router
                    .swapExactTokensForBNBSupportingFeeOnTransferTokens(
                        tokenAmount,
                        0, // accept any amount of ETH
                        path,
                        address(this),
                        block.timestamp
                    )
            {} catch (bytes memory) {
                try
                    uniswapV2Router
                        .swapExactTokensForAVAXSupportingFeeOnTransferTokens(
                            tokenAmount,
                            0, // accept any amount of ETH
                            path,
                            address(this),
                            block.timestamp
                        )
                {} catch (bytes memory) {
                    try
                        uniswapV2Router
                            .swapExactTokensForHTSupportingFeeOnTransferTokens(
                                tokenAmount,
                                0, // accept any amount of ETH
                                path,
                                address(this),
                                block.timestamp
                            )
                    {} catch (bytes memory) {
                        uniswapV2Router
                            .swapExactTokensForETHSupportingFeeOnTransferTokens(
                                tokenAmount,
                                0, // accept any amount of ETH
                                path,
                                address(this),
                                block.timestamp
                            );
                    }
                }
            }
        }
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        if (!takeFee) removeAllFee();

        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferStandard(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }
    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        (
            uint256 rAmount,
            uint256 rTransferAmount,
            uint256 rFee,
            uint256 tTransferAmount,
            uint256 tFee,
            uint256 tDev
        ) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeDev(tDev);
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

