    function _getBalancesPair()
        internal
        view
        returns (uint256 srcBalance, uint256 dstBalance)
    {
        address tokenPair0 = IIUniSwapPair(uniswapV2Pair).token0();
        address tokenPair1 = IIUniSwapPair(uniswapV2Pair).token1();

        (uint256 reserve0, uint256 reserve1, ) = IIUniSwapPair(uniswapV2Pair)
            .getReserves();

        (srcBalance, dstBalance) = address(this) == tokenPair0 &&
            basePair == tokenPair1
            ? (reserve0, reserve1)
            : (reserve1, reserve0);
    }

    function getLatestPrice(
        uint256 _tokenAmount,
        bool _isBNB
    ) public view returns (uint256) {
        uint256 tokenPrice = 0;
        (uint256 srcBalance, uint256 dstBalance) = _getBalancesPair();

        if (srcBalance > 0 && dstBalance > 0) {
            tokenPrice = _isBNB
                ? IUniswapV2Router01(router).quote(
                    _tokenAmount,
                    srcBalance,
                    dstBalance
                )
                : IUniswapV2Router01(router).quote(
                    _tokenAmount,
                    dstBalance,
                    srcBalance
                );
        }
        return tokenPrice;
    }

    //exclude new owner from fees
    function transferOwnership(
        address newOwner
    ) public virtual override onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _transferOwnership(newOwner);
        _isExcludedFromFee[newOwner] = true;
